        <div id="visualization-area"></div>
        <button id="btn-reset">+</button>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let share1XPercent = 75;
        let share1YPercent = 25;
        let share2XPercent = 25;
        let share2YPercent = 75;

        let draggingHandle = null;
        const handle1 = document.getElementById('joystick-handle1');
        const handle2 = document.getElementById('joystick-handle2');
        const joystick1 = document.getElementById('joystick-share1');
        const joystick2 = document.getElementById('joystick-share2');

        function handleMouseDown(event, handle, isShare1) {
            event.preventDefault();
            draggingHandle = { handle, isShare1 };
            document.addEventListener('mousemove', handleMouseMove);
            document.addEventListener('mouseup', handleMouseUp);
        }

        function handleMouseMove(event) {
            if (!draggingHandle) return;
            const { handle, isShare1 } = draggingHandle;
            const container = handle.parentElement;
            const rect = container.getBoundingClientRect();
            const containerWidth = rect.width;
            const containerHeight = rect.height;
            const mouseX = event.clientX;
            const mouseY = event.clientY;
            let offsetX = mouseX - rect.left;
            let offsetY = mouseY - rect.top;

            offsetX = Math.max(0, Math.min(offsetX, containerWidth));
            offsetY = Math.max(0, Math.min(offsetY, containerHeight));

            const newLeft = (offsetX / containerWidth) * 100;
            const newTop = (offsetY / containerHeight) * 100;

            handle.style.left = newLeft + '%';
            handle.style.top = newTop + '%';

            if (isShare1) {
                share1XPercent = newLeft;
                share1YPercent = newTop;
            } else {
                share2XPercent = newLeft;
                share2YPercent = newTop;
            }
        }

        function handleMouseUp() {
            draggingHandle = null;
            document.removeEventListener('mousemove', handleMouseMove);
            document.removeEventListener('mouseup', handleMouseUp);
        }

        handle1.addEventListener('mousedown', (e) => handleMouseDown(e, handle1, true));
        handle2.addEventListener('mousedown', (e) => handleMouseDown(e, handle2, false));

        document.getElementById('btn-reset').addEventListener('click', () => {
            handle1.style.left = '75%';
            handle1.style.top = '25%';
            handle2.style.left = '25%';
            handle2.style.top = '75%';

            share1XPercent = 75;
            share1YPercent = 25;
            share2XPercent = 25;
            share2YPercent = 75;
        });

        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('visualization-area');
            noLoop();

            const secretImage = generateSecretImage(64, 64);
            const share1 = createGraphics(128, 128);
            const share2 = createGraphics(128, 128);

            for (let i = 0; i < 64; i++) {
                for (let j = 0; j < 64; j++) {
                    const pixel = secretImage[i][j];
                    const x = i * 2;
                    const y = j * 2;
                    const pattern = floor(random(2));

                    if (pixel === 0) {
                        drawPattern(share1, x, y, pattern);
                        drawPattern(share2, x, y, pattern);
                    } else {
                        drawPattern(share1, x, y, pattern);
                        drawPattern(share2, x, y, 1 - pattern);
                    }
                }
            }

            drawShares(share1, share2);
        }

        function draw() {
            redraw();
        }

        function drawShares(share1, share2) {
            background(255);
            const canvasCenterX = width / 2;
            const canvasCenterY = height / 2;
            const maxOffset = 150;
            const scale = maxOffset / 50;

            const x1 = canvasCenterX + (share1XPercent - 50) * scale;
            const y1 = canvasCenterY + (share1YPercent - 50) * scale;
            const x2 = canvasCenterX + (share2XPercent - 50) * scale;
            const y2 = canvasCenterY + (share2YPercent - 50) * scale;

            image(share1, x1, y1);
            image(share2, x2, y2);
        }

        function generateSecretImage(width, height) {
            const secret = [];
            for (let i = 0; i < width; i++) {
                secret[i] = [];
                for (let j = 0; j < height; j++) {
                    secret[i][j] = 0;
                    const dx = i - 32;
                    const dy = j - 32;
                    const distSq = dx*dx + dy*dy;
                    if (distSq <= 20*20) {
                        const eye1 = (i-24)**2 + (j-24)**2 <= 4**2;
                        const eye2 = (i-40)**2 + (j-24)**2 <= 4**2;
                        if (eye1 || eye2) {
                            secret[i][j] = 1;
                        } else if (j >= 40 && (i-32)**2 + (j-40)**2 <= 8**2) {
                            secret[i][j] = 1;
                        }
                    }
                }
            }
            return secret;
        }

        function drawPattern(graphic, x, y, pattern) {
            graphic.noStroke();
            if (pattern === 0) {
                graphic.fill(0);
                graphic.rect(x, y, 1, 1);
                graphic.rect(x+1, y+1, 1, 1);
                graphic.fill(0, 0, 0, 0);
                graphic.rect(x+1, y, 1, 1);
                graphic.rect(x, y+1, 1, 1);
            } else {
                graphic.fill(0, 0, 0, 0);
                graphic.rect(x, y, 1, 1);
                graphic.rect(x+1, y+1, 1, 1);
                graphic.fill(0);
                graphic.rect(x+1, y, 1, 1);
                graphic.rect(x, y+1, 1, 1);
            }
        }
    </script>
</body>
</html>
