<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="rings-control-group">
                <label for="rings-radios">rings</label>
                <div id="rings-radios">
                    <input type="radio" name="rings" value="1" id="rings-1" checked>
                    <label for="rings-1">1</label>
                    <input type="radio" name="rings" value="2" id="rings-2">
                    <label for="rings-2">2</label>
                    <input type="radio" name="rings" value="3" id="rings-3">
                    <label for="rings-3">3</label>
                    <input type="radio" name="rings" value="4" id="rings-4">
                    <label for="rings-4">4</label>
                    <input type="radio" name="rings" value="5" id="rings-5">
                    <label for="rings-5">5</label>
                    <input type="radio" name="rings" value="6" id="rings-6">
                    <label for="rings-6">6</label>
                    <input type="radio" name="rings" value="7" id="rings-7">
                    <label for="rings-7">7</label>
                </div>
            </div>
            <div id="circles-per-ring-control-group">
                <label for="cpr-radios">circles per ring</label>
                <div id="cpr-radios">
                    <input type="radio" name="cpr" value="4" id="cpr-4">
                    <label for="cpr-4">4</label>
                    <input type="radio" name="cpr" value="6" id="cpr-6">
                    <label for="cpr-6">6</label>
                    <input type="radio" name="cpr" value="8" id="cpr-8" checked>
                    <label for="cpr-8">8</label>
                    <input type="radio" name="cpr" value="10" id="cpr-10">
                    <label for="cpr-10">10</label>
                    <input type="radio" name="cpr" value="12" id="cpr-12">
                    <label for="cpr-12">12</label>
                    <input type="radio" name="cpr" value="14" id="cpr-14">
                    <label for="cpr-14">14</label>
                </div>
            </div>
            <div id="inverse-control-group">
                <label for="checkbox-inverse">inverse</label>
                <input type="checkbox" id="checkbox-inverse">
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let numRings = 1;
        let numCirclesPerRing = 8;
        let inverseMode = false;
        let locators = [];
        let draggedLocator = null;
        let originalCircles = [];

        function setup() {
            let canvas = createCanvas(550, 550);
            canvas.parent('canvas-container');
            angleMode(RADIANS);
            locators = [new p5.Vector(-50, 0), new p5.Vector(50, 0)];
            generateOriginalCircles();
        }

        function draw() {
            background('#C1D4E6');
            translate(width/2, height/2);
            if (inverseMode) {
                drawInverse();
            } else {
                drawNormal();
            }
        }

        function drawNormal() {
            stroke('#A0A0A0');
            strokeWeight(1);
            noFill();
            let N = numCirclesPerRing;
            for (let i = 0; i < N; i++) {
                if (i < originalCircles.length) {
                    let c = originalCircles[i];
                    line(0, 0, c.center.x, c.center.y);
                }
            }

            fill('#8C929D');
            stroke(0);
            strokeWeight(1);
            for (let i = 0; i < originalCircles.length; i++) {
                let c = originalCircles[i];
                ellipse(c.center.x, c.center.y, c.radius * 2);
            }
        }

        function drawInverse() {
            fill('#FF0000');
            noStroke();
            for (let i = 0; i < locators.length; i++) {
                ellipse(locators[i].x, locators[i].y, 8, 8);
            }

            stroke('#FF0000');
            strokeWeight(1);
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);

            let C_inv = p5.Vector.add(locators[0], locators[1]).mult(0.5);
            let R_inv = p5.Vector.dist(locators[0], locators[1]) / 2;
            fill(255, 0, 0, 76);
            stroke(0);
            ellipse(C_inv.x, C_inv.y, R_inv * 2);

            fill(220, 100, 220, 128);
            stroke(0);
            strokeWeight(1);
            for (let i = 0; i < originalCircles.length; i++) {
                let C_orig = originalCircles[i].center;
                let r_orig = originalCircles[i].radius;
                let d = C_inv.dist(C_orig);

                if (abs(d - r_orig) < 0.001) {
                    let v = p5.Vector.sub(C_orig, C_inv);
                    v.normalize();
                    let perp = createVector(-v.y, v.x);
                    let distanceLine = (R_inv * R_inv) / (2 * d);
                    let Q = p5.Vector.add(C_inv, p5.Vector.mult(v, distanceLine));

                    let lineLength = 1000;
                    let start = p5.Vector.add(Q, p5.Vector.mult(perp, -lineLength/2));
                    let end = p5.Vector.add(Q, p5.Vector.mult(perp, lineLength/2));
                    line(start.x, start.y, end.x, end.y);
                } else {
                    let denominator = d * d - r_orig * r_orig;
                    let factor = (R_inv * R_inv) / denominator;
                    let C_new = p5.Vector.add(C_inv, p5.Vector.mult(p5.Vector.sub(C_orig, C_inv), factor));
                    let r_new = abs(factor) * r_orig;

                    ellipse(C_new.x, C_new.y, r_new * 2);
                }
            }
        }

        function generateOriginalCircles() {
            originalCircles = [];
            let N = numCirclesPerRing;
            let M = numRings;
            let R_max = width/2 * 0.9;
            let sinPI_N = sin(PI / N);
            let k = (1 - sinPI_N) / (1 + sinPI_N);
            let R_c = R_max / (1 + sinPI_N);
            let r = R_c * sinPI_N;

            for (let i = 0; i < M; i++) {
                let current_R_c = R_c * pow(k, i);
                let current_r = r * pow(k, i);
                for (let j = 0; j < N; j++) {
                    let angle = j * TWO_PI / N;
                    let x = current_R_c * cos(angle);
                    let y = current_R_c * sin(angle);
                    originalCircles.push({
                        center: createVector(x, y),
                        radius: current_r
                    });
                }
            }
        }

        document.querySelectorAll('input[name="rings"]').forEach(radio => {
            radio.addEventListener('change', () => {
                numRings = parseInt(radio.value);
                generateOriginalCircles();
                redraw();
            });
        });

        document.querySelectorAll('input[name="cpr"]').forEach(radio => {
            radio.addEventListener('change', () => {
                numCirclesPerRing = parseInt(radio.value);
                generateOriginalCircles();
                redraw();
            });
        });

        document.getElementById('checkbox-inverse').addEventListener('change', (e) => {
            inverseMode = e.target.checked;
            redraw();
        });

        function mousePressed() {
            if (inverseMode) {
                let mx = mouseX - width/2;
                let my = mouseY - height/2;
                draggedLocator = null;
                for (let i = 0; i < locators.length; i++) {
                    let d = dist(mx, my, locators[i].x, locators[i].y);
                    if (d < 10) {
                        draggedLocator = i;
                        break;
                    }
                }
            }
        }

        function mouseDragged() {
            if (draggedLocator !== null && inverseMode) {
                let mx = mouseX - width/2;
                let my = mouseY - height/2;
                locators[draggedLocator].x = mx;
                locators[draggedLocator].y = my;
                redraw();
            }
        }

        function mouseReleased() {
            draggedLocator = null;
        }
    </script>
</body>
</html>
