    <title>pH Calculation Demo</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            display: flex;
            flex-direction: column;
            min-height: 100vh;
        }
        #control-panel {
            padding: 15px;
            background: #f5f5f5;
            border-bottom: 1px solid #ccc;
            display: flex;
            gap: 30px;
        }
        .slider-group {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #visualization-container {
            position: relative;
            flex: 1;
            display: flex;
            justify-content: center;
            align-items: center;
        }
        #formula-strong, #formula-weak {
            position: absolute;
            font-size: 1.2em;
            pointer-events: none;
        }
        #formula-strong {
            color: maroon;
            top: 20%;
            left: 10%;
        }
        #formula-weak {
            color: green;
            top: 20%;
            right: 10%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label id="label-logCa">\(C_a\)</label>
                <input type="range" id="slider-logCa" min="-4" max="0" step="0.01" value="-1">
            </div>
            <div class="slider-group">
                <label id="label-pka">\(pK_a\)</label>
                <input type="range" id="slider-pka" min="-4" max="7" step="0.1" value="1.2">
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="p5-canvas"></canvas>
            <div id="formula-strong">\( \color{maroon}{pH = - \log C_a} \)</div>
            <div id="formula-weak">\( \color{green}{pH = \frac{1}{2} (pK_a - \log C_a)} \)</div>
        </div>
    </div>

    <script>
        let logCa = -1;
        let pKa = 1.2;
        let canvas;
        const pHMax = 7;
        const pKaMin = -4;
        const pKaMax = 7;

        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('visualization-container');
            drawPlot();
            MathJax.typesetPromise();
        }

        function drawPlot() {
            background(255);
            stroke(150);
            drawAxes();

            // Draw strong acid line (red)
            stroke('maroon');
            const pHStrong = -logCa;
            const yStrong = map(pHStrong, 0, pHMax, height, 0);
            line(0, yStrong, width, yStrong);

            // Draw weak acid line (green)
            stroke('green');
            const pHWeakStart = 0.5 * (pKaMin - logCa);
            const pHWeakEnd = 0.5 * (pKaMax - logCa);
            const yWeakStart = map(pHWeakStart, 0, pHMax, height, 0);
            const yWeakEnd = map(pHWeakEnd, 0, pHMax, height, 0);
            line(0, yWeakStart, width, yWeakEnd);

            // Draw exact pH curve (blue)
            stroke('blue');
            strokeWeight(2);
            beginShape();
            for (let pKaVal = pKaMin; pKaVal <= pKaMax; pKaVal += 0.1) {
                const pHVal = calculateExactpH(pKaVal, logCa);
                const x = map(pKaVal, pKaMin, pKaMax, 0, width);
                const y = map(pHVal, 0, pHMax, height, 0);
                vertex(x, y);
            }
            endShape();

            // Draw indicator point (orange)
            strokeWeight(1);
            const exactpH = calculateExactpH(pKa, logCa);
            const xPoint = map(pKa, pKaMin, pKaMax, 0, width);
            const yPoint = map(exactpH, 0, pHMax, height, 0);
            fill('orange');
            stroke('black');
            ellipse(xPoint, yPoint, 10, 10);
        }

        function calculateExactpH(pKaVal, logCaVal) {
            const Ca = Math.pow(10, logCaVal);
            const Ka = Math.pow(10, -pKaVal);
            const discriminant = Math.sqrt(Ka*Ka + 4*Ka*Ca);
            const Hplus = (-Ka + discriminant) / 2;
            return -Math.log10(Hplus);
        }

        function drawAxes() {
            // X-axis (pKa)
            line(0, height, width, height);
            for (let pKaVal = pKaMin; pKaVal <= pKaMax; pKaVal += 1) {
                const x = map(pKaVal, pKaMin, pKaMax, 0, width);
                line(x, height, x, height - 10);
                text(pKaVal, x, height + 20);
            }

            // Y-axis (pH)
            line(0, 0, 0, height);
            for (let pHVal = 0; pHVal <= pHMax; pHVal += 1) {
                const y = map(pHVal, 0, pHMax, height, 0);
                line(0, y, 10, y);
                text(pHVal, -20, y + 5);
            }
        }

        document.getElementById('slider-logCa').addEventListener('input', function() {
            logCa = parseFloat(this.value);
            drawPlot();
        });

        document.getElementById('slider-pka').addEventListener('input', function() {
            pKa = parseFloat(this.value);
            drawPlot();
        });
    </script>
</body>
</html>
