            width: 200px;
            height: 200px;
            background: #eee;
            position: relative;
            border: 1px solid #999;
            cursor: pointer;
        }
        .joystick-area::before,
        .joystick-area::after {
            content: '';
            position: absolute;
            background: #999;
        }
        .joystick-area::before {
            width: 1px;
            height: 100%;
            left: 50%;
            top: 0;
            transform: translateX(-50%);
        }
        .joystick-area::after {
            height: 1px;
            width: 100%;
            top: 50%;
            left: 0;
            transform: translateY(-50%);
        }
        .joystick-handle {
            width: 20px;
            height: 20px;
            background: white;
            border: 2px solid #666;
            border-radius: 50%;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            cursor: grab;
        }
        .joystick-handle:active {
            cursor: grabbing;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label>search algorithm</label>
                <div>
                    <input type="radio" id="radio-dijkstra" name="algorithm" value="dijkstra" checked>
                    <label for="radio-dijkstra">Dijkstra</label>
                    <input type="radio" id="radio-a-star" name="algorithm" value="a-star">
                    <label for="radio-a-star">A*</label>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-step">algorithm step</label>
                <div class="slider-container">
                    <input type="range" id="slider-step" min="1" max="1" value="1">
                    <span id="label-step">+ 1</span>
                </div>
            </div>
            <div class="control-group">
                <label>starting point</label>
                <div id="joystick-start" class="joystick-area">
                    <div id="handle-start" class="joystick-handle"></div>
                </div>
            </div>
            <div class="control-group">
                <label>end point</label>
                <div id="joystick-end" class="joystick-area">
                    <div id="handle-end" class="joystick-handle"></div>
                </div>
            </div>
            <div class="control-group">
                <label for="select-obstacles">obstacles</label>
                <select id="select-obstacles">
                    <option value="1-square">1 square</option>
                    <option value="2-squares">2 squares</option>
                    <option value="l-shaped-wall">L-shaped wall</option>
                </select>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const GRID_SIZE = 50;
        let currentHistory = [];
        let currentStep = 1;
        let startCoords = { x: 0.15, y: 0.85 };
        let endCoords = { x: 0.85, y: 0.15 };
        let selectedObstacle = '1-square';
        let selectedAlgorithm = 'dijkstra';

        // Initialize joysticks
        function setupJoystick(id, callback, defaultX, defaultY) {
            const area = document.getElementById(id);
            const handle = area.querySelector('.joystick-handle');
            let isDragging = false;

            function updateHandle(x, y) {
                const rect = area.getBoundingClientRect();
                const normX = Math.max(0, Math.min(1, x / rect.width));
                const normY = Math.max(0, Math.min(1, y / rect.height));
                handle.style.left = `${normX * 100}%`;
                handle.style.top = `${normY * 100}%`;
                callback(normX, normY);
            }

            area.addEventListener('mousedown', (e) => {
                isDragging = true;
                updateHandle(e.offsetX, e.offsetY);
            });

            document.addEventListener('mousemove', (e) => {
                if (!isDragging) return;
                const rect = area.getBoundingClientRect();
                updateHandle(e.clientX - rect.left, e.clientY - rect.top);
            });

            document.addEventListener('mouseup', () => {
                isDragging = false;
                triggerRecompute();
            });

            // Initialize position
            updateHandle(defaultX * area.clientWidth, defaultY * area.clientHeight);
        }

        setupJoystick('joystick-start', (x, y) => {
            startCoords = { x, y };
        }, 0.15, 0.85);

        setupJoystick('joystick-end', (x, y) => {
            endCoords = { x, y };
        }, 0.85, 0.15);

        // Event listeners for controls
        document.getElementById('slider-step').addEventListener('input', (e) => {
            currentStep = parseInt(e.target.value);
            document.getElementById('label-step').textContent = `+ ${currentStep}`;
            drawCurrentStep();
        });

        document.querySelectorAll('input[name="algorithm"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                selectedAlgorithm = e.target.value;
                triggerRecompute();
            });
        });

        document.getElementById('select-obstacles').addEventListener('change', (e) => {
            selectedObstacle = e.target.value;
            triggerRecompute();
        });

        function triggerRecompute() {
            const startCell = {
                x: Math.floor(startCoords.x * (GRID_SIZE - 1)),
                y: Math.floor(startCoords.y * (GRID_SIZE - 1))
            };
            const endCell = {
                x: Math.floor(endCoords.x * (GRID_SIZE - 1)),
                y: Math.floor(endCoords.y * (GRID_SIZE - 1))
            };

            const obstacles = getObstacles(selectedObstacle);
            currentHistory = selectedAlgorithm === 'dijkstra' 
                ? dijkstra(GRID_SIZE, startCell, endCell, obstacles)
                : aStar(GRID_SIZE, startCell, endCell, obstacles);

            currentStep = 1;
            const slider = document.getElementById('slider-step');
            slider.max = currentHistory.length;
            slider.value = 1;
            document.getElementById('label-step').textContent = `+ 1`;
            drawCurrentStep();
        }

        function getObstacles(type) {
            const obstacles = [];
            switch(type) {
                case '1-square':
                    for (let x = 20; x < 30; x++) {
                        for (let y = 20; y < 30; y++) {
                            obstacles.push({x, y});
                        }
                    }
                    break;
                case '2-squares':
                    for (let x = 10; x < 20; x++) {
                        for (let y = 10; y < 20; y++) {
                            obstacles.push({x, y});
                        }
                    }
                    for (let x = 30; x < 40; x++) {
                        for (let y = 30; y < 40; y++) {
                            obstacles.push({x, y});
                        }
                    }
                    break;
                case 'l-shaped-wall':
                    for (let x = 20; x < 30; x++) obstacles.push({x, y: 20});
                    for (let y = 20; y < 30; y++) obstacles.push({x: 29, y});
                    break;
            }
            return obstacles;
        }

        // Priority Queue implementation
        class PriorityQueue {
            constructor() {
                this.items = [];
            }

            enqueue(element, priority) {
                this.items.push({element, priority});
                this.items.sort((a, b) => a.priority - b.priority);
            }

            dequeue() {
                return this.items.shift().element;
            }

            isEmpty() {
                return this.items.length === 0;
            }
        }

        // Pathfinding algorithms
        function dijkstra(gridSize, start, end, obstacles) {
            return runAlgorithm(gridSize, start, end, obstacles, false);
        }

        function aStar(gridSize, start, end, obstacles) {
            return runAlgorithm(gridSize, start, end, obstacles, true);
        }

        function runAlgorithm(gridSize, start, end, obstacles, useHeuristic) {
            const history = [];
            const grid = Array.from({length: gridSize}, () => 
                Array.from({length: gridSize}, () => ({
                    visited: false,
                    distance: Infinity,
                    neighbor: null,
                    fScore: Infinity
                }))
            );

            const isObstacle = (x, y) => obstacles.some(o => o.x === x && o.y === y);
            const getNeighbors = (x, y) => {
                const neighbors = [];
                for (let dx = -1; dx <= 1; dx++) {
                    for (let dy = -1; dy <= 1; dy++) {
                        if (dx === 0 && dy === 0) continue;
                        const nx = x + dx, ny = y + dy;
                        if (nx >= 0 && nx < gridSize && ny >= 0 && ny < gridSize && !isObstacle(nx, ny)) {
                            neighbors.push({x: nx, y: ny, cost: Math.sqrt(dx*dx + dy*dy)});
                        }
                    }
                }
                return neighbors;
            };

            const heuristic = (a, b) => Math.abs(a.x - b.x) + Math.abs(a.y - b.y);

            const pq = new PriorityQueue();
            grid[start.x][start.y].distance = 0;
            grid[start.x][start.y].fScore = useHeuristic ? heuristic(start, end) : 0;
            pq.enqueue(start, grid[start.x][start.y].fScore);

            let step = 0;

            while (!pq.isEmpty()) {
                const current = pq.dequeue();
                const currentCell = grid[current.x][current.y];

                if (currentCell.visited) continue;
                currentCell.visited = true;

                // Record current state
                history.push({
                    openSet: [...pq.items].map(item => item.element),
                    closedSet: [current],
                    path: []
                });

                if (current.x === end.x && current.y === end.y) break;

                for (const neighbor of getNeighbors(current.x, current.y)) {
                    if (grid[neighbor.x][neighbor.y].visited) continue;

                    const tentativeDist = currentCell.distance + neighbor.cost;
                    if (tentativeDist < grid[neighbor.x][neighbor.y].distance) {
                        grid[neighbor.x][neighbor.y].distance = tentativeDist;
                        grid[neighbor.x][neighbor.y].neighbor = current;
                        const priority = useHeuristic 
                            ? tentativeDist + heuristic(neighbor, end)
                            : tentativeDist;
                        pq.enqueue(neighbor, priority);
                    }
                }
            }

            // Backtrack path
            let current = end;
            const path = [];
            while (current && (current.x !== start.x || current.y !== start.y)) {
                path.unshift(current);
                current = grid[current.x][current.y].neighbor;
            }
            path.unshift(start);

            // Add final path to history
            if (path.length > 1) {
                history.forEach(frame => {
                    frame.path = path;
                });
            }

            return history;
        }

        // p5.js sketch
        new p5((p) => {
            p.setup = () => {
                const canvas = p.createCanvas(600, 600);
                canvas.parent('canvas-container');
                triggerRecompute();
            };

            p.draw = () => {
                // This is intentionally empty - drawing is controlled by drawCurrentStep
            };

            function drawCurrentStep() {
                if (!currentHistory.length) return;
                const state = currentHistory[currentStep - 1];
                const cellSize = p.width / GRID_SIZE;

                p.background(255);

                // Draw grid
                for (let x = 0; x < GRID_SIZE; x++) {
                    for (let y = 0; y < GRID_SIZE; y++) {
                        const cell = p.createVector(x, y);
                        let color = [255];

                        // Check if obstacle
                        if (getObstacles(selectedObstacle).some(o => o.x === x && o.y === y)) {
                            color = [0];
                        }
                        // Check if in open set
                        else if (state.openSet.some(pos => pos.x === x && pos.y === y)) {
                            color = [189, 224, 254];
                        }
                        // Check if in closed set
                        else if (state.closedSet.some(pos => pos.x === x && pos.y === y)) {
                            color = [0, 0, 205];
                        }

                        p.fill(...color);
                        p.noStroke();
                        p.rect(x * cellSize, y * cellSize, cellSize, cellSize);
                    }
                }

                // Draw start and end points
                const start = p.createVector(
                    Math.floor(startCoords.x * (GRID_SIZE - 1)),
                    Math.floor(startCoords.y * (GRID_SIZE - 1))
                );
                const end = p.createVector(
                    Math.floor(endCoords.x * (GRID_SIZE - 1)),
                    Math.floor(endCoords.y * (GRID_SIZE - 1))
                );

                p.fill(52, 168, 83);
                p.noStroke();
                p.rect(start.x * cellSize, start.y * cellSize, cellSize, cellSize);
                p.rect(end.x * cellSize, end.y * cellSize, cellSize, cellSize);

                // Draw path if final step
                if (currentStep === currentHistory.length && state.path.length > 1) {
                    p.stroke(255, 0, 0);
                    p.strokeWeight(3);
                    p.noFill();
                    p.beginShape();
                    state.path.forEach(point => {
                        const px = (point.x + 0.5) * cellSize;
                        const py = (point.y + 0.5) * cellSize;
                        p.vertex(px, py);
                    });
                    p.endShape();
                }
            }

            window.drawCurrentStep = drawCurrentStep;
        });
    </script>
</body>
</html>
