<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Electric Field Simulation</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            justify-content: center;
            padding: 20px;
            background-color: #f0f0f0;
        }
        #app-container {
            display: flex;
            flex-direction: column;
            width: 100%;
            max-width: 1000px;
            background-color: white;
            padding: 20px;
            box-shadow: 0 0 10px rgba(0,0,0,0.1);
        }
        #controls-container {
            display: flex;
            flex-direction: column;
            gap: 15px;
            margin-bottom: 20px;
        }
        .slider-group {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        .slider-group label {
            width: 120px;
            font-weight: bold;
        }
        .slider-group input {
            flex: 1;
        }
        .slider-group span {
            width: 80px;
            text-align: right;
            font-family: monospace;
        }
        #main-content {
            display: flex;
            gap: 20px;
            flex-wrap: wrap;
        }
        #canvas-container {
            flex: 1;
            min-width: 500px;
            height: 500px;
            position: relative;
        }
        #info-panel {
            flex: 1;
            min-width: 300px;
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        #info-panel p {
            margin: 0;
            padding: 8px;
            background-color: #f8f8f8;
            border-radius: 4px;
            font-size: 14px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="slider-group">
                <label for="slider-source-charge">source charge</label>
                <input type="range" id="slider-source-charge" min="-5" max="5" step="0.1" value="2.0">
                <span id="label-source-charge">2.0</span>
            </div>
            <div class="slider-group">
                <label for="slider-test-charge">charge</label>
                <input type="range" id="slider-test-charge" min="-0.2" max="0.2" step="0.0005" value="-0.0845">
                <span id="label-test-charge">-0.0845</span>
            </div>
        </div>
        <div id="main-content">
            <div id="canvas-container"></div>
            <div id="info-panel">
                <p id="display-E-mag">|E| = 0.153 N/C</p>
                <p id="display-Ex">E<sub>x</sub> component = 0.085 N/C</p>
                <p id="display-Ey">E<sub>y</sub> component = 0.127 N/C</p>
                <p id="display-x-pos">x position = 2.00 m</p>
                <p id="display-y-pos">y position = 3.00 m</p>
                <p id="display-force">Electric force = 0.013 N</p>
            </div>
        </div>
    </div>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.js"></script>
    <script>
        let Q = 2.0;
        let q = -0.0845;
        let x = 2.0;
        let y = 3.0;
        let dragging = false;
        let E_mag, Ex, Ey, F_mag;
        const scale = 40; // pixels per meter
        const vectorScale = 400; // scaling factor for vectors

        function setup() {
            const canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
            calculateVectors();
            updateInfoPanel();

            // Initialize slider labels
            document.getElementById('label-source-charge').innerText = Q.toFixed(1);
            document.getElementById('label-test-charge').innerText = q.toFixed(4);

            // Slider event listeners
            document.getElementById('slider-source-charge').addEventListener('input', function() {
                Q = parseFloat(this.value);
                document.getElementById('label-source-charge').innerText = Q.toFixed(1);
                calculateVectors();
                updateInfoPanel();
            });

            document.getElementById('slider-test-charge').addEventListener('input', function() {
                q = parseFloat(this.value);
                document.getElementById('label-test-charge').innerText = q.toFixed(4);
                calculateVectors();
                document.getElementById('display-force').innerText = `Electric force = ${F_mag.toFixed(3)} N`;
            });
        }

        function draw() {
            background(255);
            translate(width/2, height/2);
            scale(1, -1);
            drawGrid();

            // Draw source charge
            fill(0, 0, 255);
            ellipse(0, 0, 20, 20);

            // Draw test charge
            fill(0);
            ellipse(x, y, 20, 20);

            // Draw vectors
            if (E_mag !== undefined) {
                drawArrow({x: x, y: y}, {x: Ex * vectorScale, y: Ey * vectorScale}, color(255, 0, 0));
            }
            if (q !== 0 && F_mag !== undefined) {
                drawArrow({x: x, y: y}, {x: (q * Ex) * vectorScale, y: (q * Ey) * vectorScale}, color(0, 0, 255));
            }
        }

        function drawGrid() {
            stroke(220);
            // Draw grid lines
            for (let i = -7; i <= 7; i++) {
                line(-width, i * scale, width, i * scale);
                line(i * scale, -height, i * scale, height);
            }
            // Draw axes
            stroke(0);
            line(-width, 0, width, 0);
            line(0, -height, 0, height);
            // Draw axis labels
            fill(0);
            textSize(12);
            textAlign(CENTER, CENTER);
            for (let i = -6; i <= 6; i += 2) {
                if (i !== 0) {
                    text(i, i * scale, -10);
                    text(i, 10, i * scale);
                }
            }
        }

        function drawArrow(base, vec, col) {
            let tip = {x: base.x + vec.x, y: base.y + vec.y};
            stroke(col);
            strokeWeight(2);
            line(base.x, base.y, tip.x, tip.y);
            // Arrowhead
            let angle = atan2(vec.y, vec.x);
            push();
            translate(tip.x, tip.y);
            rotate(angle);
            fill(col);
            triangle(0, 0, -5, 2, -5, -2);
            pop();
            strokeWeight(1);
        }

        function calculateVectors() {
            const r = sqrt(x * x + y * y);
            if (r === 0) {
                E_mag = 0;
                Ex = 0;
                Ey = 0;
                F_mag = 0;
                return;
            }
            E_mag = Q / (r * r);
            Ex = (Q * x) / (r * r * r);
            Ey = (Q * y) / (r * r * r);
            F_mag = abs(q) * E_mag;
        }

        function updateInfoPanel() {
            document.getElementById('display-E-mag').innerText = `|E| = ${E_mag.toFixed(3)} N/C`;
            document.getElementById('display-Ex').innerText = `E<sub>x</sub> component = ${Ex.toFixed(3)} N/C`;
            document.getElementById('display-Ey').innerText = `E<sub>y</sub> component = ${Ey.toFixed(3)} N/C`;
            document.getElementById('display-x-pos').innerText = `x position = ${x.toFixed(2)} m`;
            document.getElementById('display-y-pos').innerText = `y position = ${y.toFixed(2)} m`;
            document.getElementById('display-force').innerText = `Electric force = ${F_mag.toFixed(3)} N`;
        }

        function mousePressed() {
            const screenX = x * scale + width/2;
            const screenY = height/2 - y * scale;
            const d = dist(mouseX, mouseY, screenX, screenY);
            if (d < 10) dragging = true;
        }

        function mouseDragged() {
            if (dragging) {
                x = (mouseX - width/2) / scale;
                y = (height/2 - mouseY) / scale;
                calculateVectors();
                updateInfoPanel();
            }
        }

        function mouseReleased() {
            dragging = false;
        }
    </script>
</body>
</html>
