</head>
<body>
    <h1>Fourier Transform of Radially Symmetric Potential Functions</h1>
    <div id="controls-container">
        <div id="function-selector">
            <input type="checkbox" id="checkbox-hertz" checked>
            <label for="checkbox-hertz">Hertz</label>
            <input type="checkbox" id="checkbox-overlap" checked>
            <label for="checkbox-overlap">overlap</label>
            <input type="checkbox" id="checkbox-gaussian" checked>
            <label for="checkbox-gaussian">Gaussian</label>
        </div>
        <div id="scale-selector">
            <label>axes</label>
            <button id="btn-linear-linear" class="active">linear-linear</button>
            <button id="btn-log-linear">log-linear</button>
            <button id="btn-log-log">log-log</button>
        </div>
        <div id="sliders-container">
            <div id="slider-r-control">
                <label for="slider-r">radius r</label>
                <input type="range" id="slider-r" min="0.1" max="5" step="0.1" value="2.5">
                <span id="span-r-value">2.5</span>
            </div>
            <div id="slider-k-control">
                <label for="slider-k">wave number k</label>
                <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50">
                <span id="span-k-value">50</span>
            </div>
        </div>
    </div>
    <p class="hover-info">(Hover over the curves to see math labels.)</p>
    <div id="plots-container">
        <div id="plot-left"></div>
        <div id="plot-right"></div>
    </div>

    <script>
        const colors = {
            hertz: '#ff0000',
            overlap: '#000000',
            gaussian: '#0000ff'
        };

        function init() {
            updatePlots();
            document.querySelectorAll('#scale-selector button').forEach(btn => {
                btn.addEventListener('click', () => {
                    document.querySelectorAll('#scale-selector button').forEach(b => b.classList.remove('active'));
                    btn.classList.add('active');
                    updatePlots();
                });
            });
        }

        function updatePlots() {
            // Update slider values
            document.getElementById('span-r-value').textContent = document.getElementById('slider-r').value;
            document.getElementById('span-k-value').textContent = document.getElementById('slider-k').value;

            // Get control states
            const rMax = parseFloat(document.getElementById('slider-r').value);
            const kMax = parseFloat(document.getElementById('slider-k').value);
            const hertzChecked = document.getElementById('checkbox-hertz').checked;
            const overlapChecked = document.getElementById('checkbox-overlap').checked;
            const gaussianChecked = document.getElementById('checkbox-gaussian').checked;
            const logY = document.getElementById('btn-log-linear').classList.contains('active') || 
                         document.getElementById('btn-log-log').classList.contains('active');

            // Generate left plot data
            const rValues = Array.from({length: 200}, (_, i) => rMax * i / 199);
            const leftData = [];

            if (hertzChecked) {
                const hertzData = rValues.map(r => r <= 1 ? Math.pow(1 - r*r, 2.5) : 0);
                leftData.push({
                    x: rValues,
                    y: hertzData,
                    type: 'scatter',
                    name: 'Hertz',
                    line: {color: colors.hertz},
                    hovertemplate: 'Hertz<br>f(r) = (1 - r^2)^{5/2}, r ≤ 1<extra></extra>'
                });
            }

            if (overlapChecked) {
                const overlapData = rValues.map(r => r <= 1 ? Math.pow(1 - r, 2) : 0);
                leftData.push({
                    x: rValues,
                    y: overlapData,
                    type: 'scatter',
                    name: 'Overlap',
                    line: {color: colors.overlap},
                    hovertemplate: 'Overlap<br>f(r) = (1 - r)^2, r ≤ 1<extra></extra>'
                });
            }

            if (gaussianChecked) {
                const gaussianData = rValues.map(r => Math.exp(-Math.PI * r * r));
                leftData.push({
                    x: rValues,
                    y: gaussianData,
                    type: 'scatter',
                    name: 'Gaussian',
                    line: {color: colors.gaussian},
                    hovertemplate: 'Gaussian<br>f(r) = e^{-\\pi r^2}<extra></extra>'
                });
            }

            // Generate right plot data
            const kValues = Array.from({length: 200}, (_, i) => kMax * i / 199);
            const rightData = [];

            if (hertzChecked) {
                const hertzF = kValues.map(k => {
                    if (k === 0) return 15 * Math.PI * (3 - 0) / (0.001**5); // Avoid division by zero
                    return 15 * Math.PI * ((3 - k*k) * Math.sin(k) - 3 * k * Math.cos(k)) / Math.pow(k, 5);
                });
                rightData.push({
                    x: kValues,
                    y: hertzF,
                    type: 'scatter',
                    name: 'Hertz F(k)',
                    line: {color: colors.hertz},
                    hovertemplate: 'Hertz F(k)<br>F(k) = 15\\pi \\frac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}<extra></extra>'
                });
            }

            if (overlapChecked) {
                const overlapF = kValues.map(k => {
                    if (k === 0) return 8 * Math.PI * (2 - 0) / (0.001**4);
                    return 8 * Math.PI * (2 - k*k - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / Math.pow(k, 4);
                });
                rightData.push({
                    x: kValues,
                    y: overlapF,
                    type: 'scatter',
                    name: 'Overlap F(k)',
                    line: {color: colors.overlap},
                    hovertemplate: 'Overlap F(k)<br>F(k) = 8\\pi \\frac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}<extra></extra>'
                });
            }

            if (gaussianChecked) {
                const gaussianF = kValues.map(k => (1/Math.sqrt(Math.PI)) * Math.exp(-k*k / (4*Math.PI)));
                rightData.push({
                    x: kValues,
                    y: gaussianF,
                    type: 'scatter',
                    name: 'Gaussian F(k)',
                    line: {color: colors.gaussian},
                    hovertemplate: 'Gaussian F(k)<br>F(k) = \\frac{1}{\\sqrt{\\pi}} e^{-k^2 / (4\\pi)}<extra></extra>'
                });
            }

            // Update left plot
            Plotly.newPlot('plot-left', leftData, {
                title: {text: 'Potential Functions f(r)', font: {color: '#666'}},
                xaxis: {title: 'r', range: [0, rMax]},
                yaxis: {title: 'f(r)'},
                margin: {l: 40, r: 20, b: 40, t: 40}
            });

            // Update right plot
            Plotly.newPlot('plot-right', rightData, {
                title: {text: '3D Fourier Transform (real part)', font: {color: '#666'}},
                xaxis: {title: 'k', range: [0, kMax]},
                yaxis: {title: 'F(k)', type: logY ? 'log' : 'linear'},
                margin: {l: 40, r: 20, b: 40, t: 40}
            });

            // Render MathJax in tooltips
            setTimeout(() => MathJax.typeset(), 100);
        }

        // Event listeners
        document.getElementById('slider-r').addEventListener('input', updatePlots);
        document.getElementById('slider-k').addEventListener('input', updatePlots);
        document.getElementById('checkbox-hertz').addEventListener('change', updatePlots);
        document.getElementById('checkbox-overlap').addEventListener('change', updatePlots);
        document.getElementById('checkbox-gaussian').addEventListener('change', updatePlots);

        // Initialize on load
        window.onload = init;
    </script>
</body>
</html>
