</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <h4>scale</h4>
            <div id="scale-switch">
                <input type="radio" id="scale-population" name="scale" value="population">
                <label for="scale-population">population</label>
                <input type="radio" id="scale-individual" name="scale" value="individual" checked>
                <label for="scale-individual">individual</label>
            </div>
            <div class="slider-group">
                <label for="slider-population">average city population (k)</label>
                <input type="range" id="slider-population" min="1" max="1000" step="1" value="250" disabled>
                <span id="value-population">250</span>
            </div>

            <h4>type</h4>
            <div id="type-switch">
                <input type="radio" id="type-emissions" name="type" value="emissions" checked>
                <label for="type-emissions">emissions</label>
                <input type="radio" id="type-costs" name="type" value="costs">
                <label for="type-costs">costs</label>
            </div>

            <div class="slider-group">
                <label for="slider-annual-emissions">annual CO₂ emissions per person (tons/year)</label>
                <input type="range" id="slider-annual-emissions" min="1" max="25" step="0.1" value="14">
                <span id="value-annual-emissions">14</span>
            </div>
            <div class="slider-group">
                <label for="slider-reduction-time">reduction time until 50% emissions (years)</label>
                <input type="range" id="slider-reduction-time" min="5" max="50" step="1" value="20">
                <span id="value-reduction-time">20</span>
            </div>
            <div class="slider-group">
                <label for="slider-annual-budget">annual CO₂ budget per person (tons/year)</label>
                <input type="range" id="slider-annual-budget" min="0" max="5" step="0.1" value="1.5">
                <span id="value-annual-budget">1.5</span>
            </div>
            <div class="slider-group">
                <label for="slider-start-year">start of action</label>
                <input type="range" id="slider-start-year" min="2020" max="2049" step="1" value="2020">
                <span id="value-start-year">2020</span>
            </div>
            <div class="slider-group">
                <label for="slider-compensation-cost">compensation costs per ton of CO₂ ($/ton)</label>
                <input type="range" id="slider-compensation-cost" min="5" max="100" step="1" value="15">
                <span id="value-compensation-cost">15</span>
            </div>
            <div class="slider-group">
                <label for="slider-fractional-compensation">fractional compensation (%)</label>
                <input type="range" id="slider-fractional-compensation" min="0" max="100" step="1" value="50">
                <span id="value-fractional-compensation">50</span>
            </div>
            <div class="slider-group">
                <label for="slider-constant-compensation">constant compensation (%)</label>
                <input type="range" id="slider-constant-compensation" min="0" max="100" step="1" value="25">
                <span id="value-constant-compensation">25</span>
            </div>
        </div>

        <div id="vis-panel">
            <div id="climate-neutral-buttons">
                <button id="btn-neutral-now">now</button>
                <button id="btn-neutral-2030">2030</button>
                <button id="btn-neutral-2040" class="active">2040</button>
                <button id="btn-neutral-2050">2050</button>
                <button id="btn-neutral-never">never</button>
            </div>
            <div id="main-plot-container"></div>
            <div id="budget-plot-container"></div>
        </div>
    </div>

    <script>
        let currentParams = {
            scale: 'individual',
            type: 'emissions',
            population: 250,
            annualEmissions: 14,
            reductionTime: 20,
            annualBudget: 1.5,
            startYear: 2020,
            compensationCost: 15,
            fractionalComp: 50,
            constantComp: 25,
            neutralYear: 2040
        };

        function setup() {
            noCanvas();
            createControls();
            drawVisualization();
        }

        function drawVisualization() {
            const mainContainer = document.getElementById('main-plot-container');
            const budgetContainer = document.getElementById('budget-plot-container');

            // Clear existing canvases
            while (mainContainer.firstChild) mainContainer.removeChild(mainContainer.firstChild);
            while (budgetContainer.firstChild) budgetContainer.removeChild(budgetContainer.firstChild);

            // Create new canvases
            const mainCanvas = createCanvas(600, 400);
            mainCanvas.parent('main-plot-container');
            drawMainPlot();

            const budgetCanvas = createCanvas(800, 200);
            budgetCanvas.parent('budget-plot-container');
            drawBudgetPlot();
        }

        function drawMainPlot() {
            background(255);
            const years = Array.from({length: 31}, (_, i) => 2020 + i);
            const data = calculateData(years);

            // Calculate axes
            const xScale = width / 30;
            const yMax = currentParams.type === 'emissions' 
                ? Math.max(...data.realEmissions, ...data.effectiveEmissions) * 1.2 
                : Math.max(...data.annualCosts) * 1.2;
            const yScale = height / yMax;

            // Draw axes
            stroke(0);
            line(0, height, width, height); // X-axis
            line(0, 0, 0, height); // Y-axis

            // Draw emissions plot
            if (currentParams.type === 'emissions') {
                // Real emissions (red line)
                stroke(255, 0, 0);
                beginShape();
                years.forEach((y, i) => {
                    vertex((y - 2020) * xScale, height - data.realEmissions[i] * yScale);
                });
                endShape();

                // Effective emissions (black line)
                stroke(0);
                beginShape();
                years.forEach((y, i) => {
                    vertex((y - 2020) * xScale, height - data.effectiveEmissions[i] * yScale);
                });
                endShape();

                // Compensated area (blue)
                fill(0, 0, 255, 50);
                beginShape();
                years.forEach((y, i) => {
                    vertex((y - 2020) * xScale, height - data.realEmissions[i] * yScale);
                });
                years.slice().reverse().forEach((y, i) => {
                    vertex((y - 2020) * xScale, height - data.effectiveEmissions[30 - i] * yScale);
                });
                endShape();

                // Positive/negative effective emissions
                fill(255, 0, 0, 50);
                beginShape();
                years.forEach((y, i) => {
                    if (data.effectiveEmissions[i] > 0) {
                        vertex((y - 2020) * xScale, height - data.effectiveEmissions[i] * yScale);
                    } else {
                        vertex((y - 2020) * xScale, height);
                    }
                });
                endShape();

                fill(0, 255, 0, 50);
                beginShape();
                years.forEach((y, i) => {
                    if (data.effectiveEmissions[i] < 0) {
                        vertex((y - 2020) * xScale, height - data.effectiveEmissions[i] * yScale);
                    } else {
                        vertex((y - 2020) * xScale, height);
                    }
                });
                endShape();

                // Climate neutral line
                if (currentParams.neutralYear !== 'never') {
                    const nx = (currentParams.neutralYear - 2020) * xScale;
                    stroke(0, 255, 0);
                    line(nx, 0, nx, height);
                    text(`climate neutral ${currentParams.neutralYear}`, nx, 20);

                    // Cumulative lines
                    const cumulative = data.cumulativeEffective;
                    const targetIndex = currentParams.neutralYear - 2020;
                    const targetValue = cumulative[targetIndex];
                    const finalValue = cumulative[30];

                    stroke(0, 0, 255, 100);
                    dashedLine(0, height - targetValue * yScale, width, height - targetValue * yScale);
                    text(`CO₂ emissions until ${currentParams.neutralYear} = ${targetValue.toFixed(1)} tons`, 10, height - targetValue * yScale - 10);

                    dashedLine(0, height - finalValue * yScale, width, height - finalValue * yScale);
                    text(`CO₂ emissions until 2050 = ${finalValue.toFixed(1)} tons`, 10, height - finalValue * yScale - 30);
                }
            } else {
                // Costs plot
                stroke(0, 0, 255);
                beginShape();
                years.forEach((y, i) => {
                    vertex((y - 2020) * xScale, height - data.annualCosts[i] * yScale);
                });
                endShape();

                fill(0, 0, 255, 50);
                beginShape();
                vertex(0, height);
                years.forEach((y, i) => {
                    vertex((y - 2020) * xScale, height - data.annualCosts[i] * yScale);
                });
                vertex(width, height);
                endShape();

                // Average cost line
                const avgCost = data.totalCosts / 31;
                stroke(255, 0, 0, 100);
                dashedLine(0, height - avgCost * yScale, width, height - avgCost * yScale);
                text(`annual personal compensation costs (average) = $${avgCost.toFixed(1)} per year`, 10, height - avgCost * yScale - 10);
                text(`total personal compensation costs (until 2050) = $${data.totalCosts.toFixed(1)}`, 10, 20);
            }
        }

        function drawBudgetPlot() {
            background(255);
            const years = [2020, 2030, 2040, 2050];
            const data = calculateData(years);

            const circleData = years.map((year, i) => {
                const effective = data.effectiveEmissions[i];
                const cumulative = data.cumulativeEffective[i];
                const totalBudget = 30 * currentParams.annualBudget;
                const value = totalBudget - cumulative;
                return {
                    year,
                    value,
                    effective,
                    radius: 20 * Math.sqrt(Math.abs(value))
                };
            });

            const spacing = width / 5;
            circleData.forEach((d, i) => {
                const x = spacing * (i + 1);
                const y = height / 2;

                if (d.effective <= 0) {
                    fill(0, 255, 0, 100);
                    stroke(0);
                } else {
                    fill(255, 0, 0, 50);
                    stroke(0, 0, 0, 100);
                    strokeWeight(2);
                    dashedLine(x - d.radius, y - d.radius, x + d.radius, y + d.radius);
                }

                ellipse(x, y, d.radius * 2);
                fill(0);
                noStroke();
                textAlign(CENTER, CENTER);
                text(`${d.value.toFixed(1)} tons`, x, y);
            });
        }

        function calculateData(years) {
            const data = {
                realEmissions: [],
                effectiveEmissions: [],
                compensatedEmissions: [],
                cumulativeEffective: [],
                annualCosts: [],
                totalCosts: 0
            };

            let cumulative = 0;
            years.forEach(year => {
                // Calculate real emissions
                let real;
                if (year < currentParams.startYear) {
                    real = currentParams.annualEmissions;
                } else {
                    const t = year - currentParams.startYear;
                    real = currentParams.annualEmissions * Math.pow(0.5, t / currentParams.reductionTime);
                }

                // Calculate compensated emissions
                const fractional = real * (currentParams.fractionalComp / 100);
                const constant = currentParams.annualEmissions * (currentParams.constantComp / 100);
                const compensated = fractional + constant;

                // Calculate effective emissions
                const effective = real - compensated;

                // Calculate costs
                const annualCost = compensated * currentParams.compensationCost;

                // Scale if population mode
                if (currentParams.scale === 'population') {
                    const populationFactor = currentParams.population * 1000 / 1000000;
                    real *= populationFactor;
                    effective *= populationFactor;
                    compensated *= populationFactor;
                    annualCost *= currentParams.population * 1000;
                }

                data.realEmissions.push(real);
                data.effectiveEmissions.push(effective);
                data.compensatedEmissions.push(compensated);
                data.annualCosts.push(annualCost);

                cumulative += effective;
                data.cumulativeEffective.push(cumulative);
                data.totalCosts += annualCost;
            });

            return data;
        }

        function createControls() {
            // Scale switch
            document.getElementById('scale-switch').addEventListener('change', (e) => {
                currentParams.scale = e.target.value;
                document.getElementById('slider-population').disabled = currentParams.scale === 'individual';
                update();
            });

            // Type switch
            document.getElementById('type-switch').addEventListener('change', (e) => {
                currentParams.type = e.target.value;
                update();
            });

            // Sliders
            const sliders = [
                'slider-population', 'slider-annual-emissions', 'slider-reduction-time',
                'slider-annual-budget', 'slider-start-year', 'slider-compensation-cost',
                'slider-fractional-compensation', 'slider-constant-compensation'
            ];

            sliders.forEach(id => {
                const input = document.getElementById(id);
                const span = document.getElementById(`value-${id.replace('slider-', '')}`);
                input.addEventListener('input', () => {
                    currentParams[id.replace('slider-', '')] = parseFloat(input.value);
                    span.textContent = input.value;
                    update();
                });
                // Initialize span values
                span.textContent = input.value;
            });

            // Climate neutral buttons
            const buttons = ['now', '2030', '2040', '2050', 'never'];
            buttons.forEach(btn => {
                const el = document.getElementById(`btn-neutral-${btn}`);
                el.addEventListener('click', () => {
                    currentParams.neutralYear = btn === 'now' ? 2020 : btn === 'never' ? 'never' : parseInt(btn);
                    // Update active button styling
                    document.querySelectorAll('#climate-neutral-buttons button').forEach(b => b.classList.remove('active'));
                    if (btn !== 'never') el.classList.add('active');
                    update();
                });
            });
        }

        function update() {
            drawVisualization();
        }

        function dashedLine(x1, y1, x2, y2) {
            const dashLength = 10;
            const dx = x2 - x1;
            const dy = y2 - y1;
            const distance = Math.sqrt(dx * dx + dy * dy);
            let dashCount = Math.floor(distance / dashLength);
            strokeCap(SQUARE);

            for (let i = 0; i < dashCount; i++) {
                const xStart = x1 + (dx / dashCount) * i;
                const yStart = y1 + (dy / dashCount) * i;
                const xEnd = xStart + (dx / dashCount) * 0.5;
                const yEnd = yStart + (dy / dashCount) * 0.5;
                line(xStart, yStart, xEnd, yEnd);
            }
        }
    </script>
</body>
</html>
