            margin-left: auto;
            padding: 8px 12px;
            font-weight: bold;
        }
        #canvas-container {
            flex: 1;
            position: relative;
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="solid-selector">
                <button id="btn-solid-1" class="solid-btn active">1</button>
                <button id="btn-solid-2" class="solid-btn">2</button>
                <button id="btn-solid-3" class="solid-btn">3</button>
            </div>
            <div id="fold-slider-container">
                <label for="slider-fold">fold-unfold</label>
                <input type="range" id="slider-fold" min="0" max="1" step="0.01" value="0">
            </div>
            <button id="btn-reset-view">+</button>
        </div>
        <div id="canvas-container"></div>
    </div>

    <!-- Three.js and OrbitControls -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/controls/OrbitControls.js"></script>

    <script>
        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        const camera = new THREE.PerspectiveCamera(50, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(0, 0, 10);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(5, 5, 5).normalize();
        scene.add(directionalLight);

        // Geometry definitions for solids
        const solids = {
            1: {
                vertices: [
                    // Central vertical edge
                    new THREE.Vector2(-1, 0), new THREE.Vector2(1, 0),
                    // Left triangles
                    new THREE.Vector2(-2, 1), new THREE.Vector2(-2, -1),
                    // Right triangles
                    new THREE.Vector2(2, 1), new THREE.Vector2(2, -1)
                ],
                faces: [
                    [0, 2, 1], [0, 1, 3], [0, 2, 3],
                    [1, 4, 0], [1, 0, 5], [1, 4, 5]
                ],
                hinges: [
                    { parentFace: 0, childFace: 2, axis: new THREE.Vector3(0, 0, 1), angle: Math.PI * 0.8 },
                    { parentFace: 1, childFace: 5, axis: new THREE.Vector3(0, 0, 1), angle: Math.PI * 0.8 },
                    { parentFace: 3, childFace: 4, axis: new THREE.Vector3(0, 0, 1), angle: Math.PI * 0.8 }
                ],
                colors: ['#E47A55', '#A4C6E3', '#F4E0E1', '#F3C76E', '#8BC34A', '#673AB7']
            }
            // Add solids 2 and 3 similarly
        };

        let currentSolid = null;
        let foldProgress = 0;

        // Create solid group
        function createSolid(solidId) {
            const solid = new THREE.Group();
            const geo = solids[solidId];
            const materials = geo.colors.map(color => new THREE.MeshStandardMaterial({
                color: new THREE.Color(color),
                side: THREE.DoubleSide,
                flatShading: true
            }));

            // Create base faces
            geo.faces.forEach((face, i) => {
                const vertices = face.map(v => new THREE.Vector3(geo.vertices[v].x, geo.vertices[v].y, 0));
                const geometry = new THREE.BufferGeometry().setFromPoints(vertices);
                const mesh = new THREE.Mesh(geometry, materials[i]);
                solid.add(mesh);
            });

            // Add folding pivots (simplified example)
            geo.hinges.forEach(hinge => {
                const pivot = new THREE.Object3D();
                pivot.rotation.y = 0;
                solid.add(pivot);
                // Attach child face to pivot
                if (hinge.childFace < solid.children.length) {
                    const child = solid.children[hinge.childFace];
                    pivot.add(child);
                    pivot.position.set(0, 0, 0); // Adjust based on hinge edge
                }
            });

            return solid;
        }

        // Update folding state
        function updateFolding(t) {
            foldProgress = t;
            if (currentSolid) {
                currentSolid.traverse(obj => {
                    if (obj.isObject3D && obj.rotation) {
                        obj.rotation.y = t * Math.PI * 0.8; // Example angle
                    }
                });
                // Update colors
                currentSolid.children.forEach((child, i) => {
                    if (child.isMesh) {
                        const color = new THREE.Color().lerpColors(
                            new THREE.Color(0xF5E0E0),
                            new THREE.Color(solids[1].colors[i % solids[1].colors.length]),
                            t
                        );
                        child.material.color = color;
                    }
                });
            }
        }

        // Event listeners
        document.getElementById('slider-fold').addEventListener('input', (e) => {
            updateFolding(parseFloat(e.target.value));
        });

        document.querySelectorAll('.solid-btn').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('.solid-btn').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                if (currentSolid) scene.remove(currentSolid);
                currentSolid = createSolid(btn.id.split('-')[2]);
                scene.add(currentSolid);
                updateFolding(foldProgress);
                resetView();
            });
        });

        document.getElementById('btn-reset-view').addEventListener('click', resetView);

        function resetView() {
            controls.reset();
            controls.update();
        }

        // Initial setup
        currentSolid = createSolid(1);
        scene.add(currentSolid);
        updateFolding(0);

        // Resize handler
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();
    </script>
</body>
</html>
