<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>relative volatility</h3>
            <button id="btn-reset">+</button>
            <div class="slider-row">
                <label for="slider-ac">between A and C</label>
                <input type="range" id="slider-ac" min="0.1" max="5.0" step="0.01" value="0.6">
                <span id="value-ac">0.60</span>
            </div>
            <div class="slider-row">
                <label for="slider-bc">between B and C</label>
                <input type="range" id="slider-bc" min="0.1" max="5.0" step="0.01" value="3.93">
                <span id="value-bc">3.93</span>
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="ternary-plot-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        // Global variables for p5.js
        let alpha13 = 0.6;
        let alpha23 = 3.93;
        const N = 10; // Grid divisions
        const colorStops = [
            { value: 0.0, color: [44, 123, 182] },    // Dark Blue
            { value: 0.25, color: [171, 217, 233] },  // Light Blue
            { value: 0.5, color: [255, 255, 191] },   // Green-Yellow
            { value: 0.75, color: [253, 174, 97] },   // Orange
            { value: 1.0, color: [215, 25, 28] }      // Red
        ];

        function setup() {
            const canvas = createCanvas(600, 550);
            canvas.parent('visualization-container');
            noLoop(); // Manual redraw control
            drawTernaryPlot();
        }

        function drawTernaryPlot() {
            background(255);
            const padding = 50;
            const side = min(width, height) - 2 * padding;
            const height = side * sqrt(3)/2;

            // Triangle vertices (A, B, C)
            const A = createVector(width/2, padding);
            const B = createVector(width/2 - side/2, padding + height);
            const C = createVector(width/2 + side/2, padding + height);

            // Draw main triangle
            noFill();
            stroke(0);
            strokeWeight(2);
            triangle(A.x, A.y, B.x, B.y, C.x, C.y);

            // Draw grid
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N - i; j++) {
                    const k = N - i - j;
                    if (k < 0) continue;

                    // Barycentric coordinates
                    const xA = i / N;
                    const xB = j / N;
                    const xC = k / N;

                    // Calculate function F
                    const F = calculateF(xA, xB, xC, alpha13, alpha23);
                    const norm = constrain(F / 2.0, 0, 1);
                    const color = getColorFromGradient(norm);

                    // Calculate triangle vertices in Cartesian coordinates
                    const p1 = barycentricToCartesian(xA, xB, xC, A, B, C);
                    const p2 = barycentricToCartesian((i+1)/N, xB, (k-1)/N, A, B, C);
                    const p3 = barycentricToCartesian(xA, (j+1)/N, (k-1)/N, A, B, C);

                    // Draw triangle
                    fill(color);
                    stroke(0);
                    strokeWeight(0.5);
                    triangle(p1.x, p1.y, p2.x, p2.y, p3.x, p3.y);
                }
            }

            // Draw labels and ticks
            drawLabels(A, B, C, side);
        }

        function calculateF(x1, x2, x3, alpha13, alpha23) {
            const alpha = [alpha13, alpha23, 1];
            const x = [x1, x2, x3];
            const D = alpha[0]*x[0] + alpha[1]*x[1] + alpha[2]*x[2];
            let F = 0;

            for (let i = 0; i < 3; i++) {
                const yi = (alpha[i] * x[i]) / D;
                F += pow(x[i] - yi, 2);
            }

            return F;
        }

        function barycentricToCartesian(xA, xB, xC, A, B, C) {
            const x = A.x * xA + B.x * xB + C.x * xC;
            const y = A.y * xA + B.y * xB + C.y * xC;
            return createVector(x, y);
        }

        function getColorFromGradient(value) {
            for (let i = 0; i < colorStops.length - 1; i++) {
                const lower = colorStops[i];
                const upper = colorStops[i+1];
                if (value >= lower.value && value <= upper.value) {
                    const t = (value - lower.value) / (upper.value - lower.value);
                    return lerpColor(
                        color(lower.color[0], lower.color[1], lower.color[2]),
                        color(upper.color[0], upper.color[1], upper.color[2]),
                        t
                    );
                }
            }
            return color(colorStops[colorStops.length-1].color);
        }

        function drawLabels(A, B, C, side) {
            // Labels at vertices
            textSize(16);
            fill(0);
            textAlign(CENTER, CENTER);
            text('A', A.x, A.y - 20);
            text('B', B.x, B.y + 20);
            text('C', C.x, C.y + 20);

            // Tick marks
            textSize(12);
            textAlign(CENTER);
            for (let i = 0; i <= 10; i++) {
                const t = i / 10;

                // AB axis
                const pAB = p5.Vector.lerp(A, B, t);
                text(nf(t, 1, 1), pAB.x, pAB.y - 10);

                // BC axis
                const pBC = p5.Vector.lerp(B, C, t);
                text(nf(1-t, 1, 1), pBC.x, pBC.y + 10);

                // CA axis
                const pCA = p5.Vector.lerp(C, A, t);
                text(nf(1-t, 1, 1), pCA.x, pCA.y - 10);
            }
        }

        // Event Listeners
        document.getElementById('slider-ac').addEventListener('input', function() {
            alpha13 = parseFloat(this.value);
            document.getElementById('value-ac').textContent = alpha13.toFixed(2);
            redraw();
        });

        document.getElementById('slider-bc').addEventListener('input', function() {
            alpha23 = parseFloat(this.value);
            document.getElementById('value-bc').textContent = alpha23.toFixed(2);
            redraw();
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            const sliderAC = document.getElementById('slider-ac');
            const sliderBC = document.getElementById('slider-bc');
            sliderAC.value = 0.6;
            sliderBC.value = 3.93;
            alpha13 = 0.6;
            alpha23 = 3.93;
            document.getElementById('value-ac').textContent = '0.60';
            document.getElementById('value-bc').textContent = '3.93';
            redraw();
        });

        function redraw() {
            drawTernaryPlot();
        }
    </script>
</body>
</html>
