</head>
<body>
    <h1>Band Structure of a Quantum Wire with Rashba and Zeeman Interactions</h1>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-wso">Rashba spin-orbit interaction strength (wso)</label><br>
                <input type="range" id="slider-wso" min="0" max="2" step="0.1" value="0">
                <span id="value-wso">0.0</span>
            </div>
            <hr>
            <h2>magnetic field</h2>
            <div>
                <label for="slider-n">subband (n)</label><br>
                <input type="range" id="slider-n" min="0" max="10" step="1" value="0">
                <span id="value-n">0</span>
            </div>
            <h3>strengths</h3>
            <div>
                <label for="slider-bx">x direction (BX)</label><br>
                <input type="range" id="slider-bx" min="0" max="2" step="0.1" value="0">
                <span id="value-bx">0.0</span>
            </div>
            <div>
                <label for="slider-by">y direction (BY)</label><br>
                <input type="range" id="slider-by" min="0" max="2" step="0.1" value="0">
                <span id="value-by">0.0</span>
            </div>
            <div>
                <label for="slider-bz">z direction (BZ)</label><br>
                <input type="range" id="slider-bz" min="0" max="2" step="0.1" value="0">
                <span id="value-bz">0.0</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Initialize slider displays
        document.getElementById('slider-wso').addEventListener('input', () => {
            document.getElementById('value-wso').textContent = parseFloat(this.value).toFixed(1);
            updatePlot();
        });
        document.getElementById('slider-n').addEventListener('input', () => {
            document.getElementById('value-n').textContent = parseInt(this.value);
            updatePlot();
        });
        document.getElementById('slider-bx').addEventListener('input', () => {
            document.getElementById('value-bx').textContent = parseFloat(this.value).toFixed(1);
            updatePlot();
        });
        document.getElementById('slider-by').addEventListener('input', () => {
            document.getElementById('value-by').textContent = parseFloat(this.value).toFixed(1);
            updatePlot();
        });
        document.getElementById('slider-bz').addEventListener('input', () => {
            document.getElementById('value-bz').textContent = parseFloat(this.value).toFixed(1);
            updatePlot();
        });

        // Initial plot setup
        let plotInitialized = false;
        let plotData = [];
        let plotLayout = {
            xaxis: {
                title: 'normalized k length (lwky)',
                range: [-5, 5],
                zeroline: true
            },
            yaxis: {
                title: '2 E / &#x210F;&#x03C9;',
                range: [0, 40],
                zeroline: true
            },
            margin: { t: 40, b: 40, l: 40, r: 40 }
        };

        function updatePlot() {
            // Get current parameter values
            const n = parseInt(document.getElementById('slider-n').value);
            const wso = parseFloat(document.getElementById('slider-wso').value);
            const BX = parseFloat(document.getElementById('slider-bx').value);
            const BY = parseFloat(document.getElementById('slider-by').value);
            const BZ = parseFloat(document.getElementById('slider-bz').value);

            // Generate k values
            const kValues = [];
            for (let k = -5; k <= 5; k += 0.1) {
                kValues.push(k);
            }

            // Calculate energy bands
            const E_plus = [];
            const E_minus = [];
            let hasSplit = false;

            for (const k of kValues) {
                const sqrtTerm = Math.sqrt(Math.pow(BZ + wso * k, 2) + Math.pow(BX, 2) + Math.pow(BY, 2));
                const base = (2 * n + 1) + Math.pow(k, 2);
                E_plus.push(base + sqrtTerm);
                E_minus.push(base - sqrtTerm);
                if (sqrtTerm > 0) hasSplit = true;
            }

            // Prepare plot data
            plotData = [];
            if (hasSplit) {
                plotData.push({
                    x: kValues,
                    y: E_plus,
                    mode: 'lines',
                    line: { color: 'red', dash: 'solid', width: 2 }
                }, {
                    x: kValues,
                    y: E_minus,
                    mode: 'lines',
                    line: { color: 'red', dash: 'dash', width: 2 }
                });
            } else {
                plotData.push({
                    x: kValues,
                    y: E_plus,
                    mode: 'lines',
                    line: { color: 'red', dash: 'solid', width: 2 }
                });
            }

            // Update plot title
            const titleParts = [`n = ${n}`];
            if (BX !== 0) titleParts.push(`BX = ${BX.toFixed(1)}`);
            if (BY !== 0) titleParts.push(`BY = ${BY.toFixed(1)}`);
            if (BZ !== 0) titleParts.push(`BZ = ${BZ.toFixed(1)}`);
            if (wso !== 0) titleParts.push(`wso = ${wso.toFixed(1)}`);
            plotLayout.title = titleParts.join(', ');

            // Update or initialize plot
            if (!plotInitialized) {
                Plotly.newPlot('plot-container', plotData, plotLayout);
                plotInitialized = true;
            } else {
                Plotly.react('plot-container', plotData, plotLayout);
            }
        }

        // Initialize plots on load
        window.onload = updatePlot;
    </script>
</body>
</html>
