            <div id="plot-canvas-1" class="plot-canvas"></div>
            <div id="plot-canvas-2" class="plot-canvas"></div>
            <div id="plot-canvas-3" class="plot-canvas"></div>
            <div id="plot-canvas-4" class="plot-canvas"></div>
            <div id="plot-canvas-5" class="plot-canvas"></div>
            <div id="plot-canvas-6" class="plot-canvas"></div>
        </div>
    </div>

    <script>
        let P = [1, 0, 0];
        let U = [1, 5, 3, 1];
        let C1 = [];
        let C2 = [];
        let C3 = [];
        let Q = [];
        const sketches = [];

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', function() {
                const valueId = 'value-' + this.id.substring('slider-'.length);
                document.getElementById(valueId).textContent = this.value;
                update();
            });
        });

        document.getElementById('toggle-controls').addEventListener('click', function() {
            const slidersArea = document.getElementById('sliders-area');
            if (slidersArea.style.display === 'none') {
                slidersArea.style.display = 'block';
                this.textContent = '+';
            } else {
                slidersArea.style.display = 'none';
                this.textContent = '-';
            }
        });

        function update() {
            P = [
                parseInt(document.getElementById('slider-precip-1').value),
                parseInt(document.getElementById('slider-precip-2').value),
                parseInt(document.getElementById('slider-precip-3').value)
            ];
            U = [
                parseInt(document.getElementById('slider-uh-1').value),
                parseInt(document.getElementById('slider-uh-2').value),
                parseInt(document.getElementById('slider-uh-3').value),
                parseInt(document.getElementById('slider-uh-4').value)
            ];

            C1 = U.map(u => u * P[0]);
            C2 = U.map(u => u * P[1]);
            C3 = U.map(u => u * P[2]);

            Q = [
                P[0]*U[0],
                P[0]*U[1] + P[1]*U[0],
                P[0]*U[2] + P[1]*U[1] + P[2]*U[0],
                P[0]*U[3] + P[1]*U[2] + P[2]*U[1],
                P[1]*U[3] + P[2]*U[2],
                P[2]*U[3]
            ];

            sketches.forEach(s => s.redraw());
        }

        function drawPlot(sketch, data, xLabel, yLabel, xMax, yMax, xOffset) {
            const canvasWidth = sketch.width;
            const canvasHeight = sketch.height;

            sketch.background(255);

            const xTick = xMax <= 5 ? 1 : 2;
            const yTick = yMax <= 10 ? 2 : 20;

            sketch.fill('#d4e6f1');
            sketch.stroke('#2e86c1');
            sketch.strokeWeight(2);

            sketch.beginShape();
            sketch.vertex(xOffset, 0);
            for (let i = 0; i < data.length; i++) {
                const x = i + xOffset;
                const y = data[i];
                sketch.vertex(x, y);
                sketch.vertex(x + 1, y);
            }
            sketch.vertex(xOffset + data.length, 0);
            sketch.endShape();

            function dataToScreen(xData, yData) {
                const screenX = (xData / xMax) * canvasWidth;
                const screenY = canvasHeight - (yData / yMax) * canvasHeight;
                return { screenX, screenY };
            }

            sketch.stroke(0);
            sketch.strokeWeight(1);

            const { screenX: xEnd } = dataToScreen(xMax, 0);
            sketch.line(0, canvasHeight, xEnd, canvasHeight);

            const { screenY: yEnd } = dataToScreen(0, yMax);
            sketch.line(0, canvasHeight, 0, yEnd);

            for (let x = 0; x <= xMax; x += xTick) {
                const { screenX } = dataToScreen(x, 0);
                sketch.line(screenX, canvasHeight, screenX, canvasHeight - 5);
                sketch.textAlign(sketch.CENTER, sketch.TOP);
                sketch.text(x, screenX, canvasHeight + 5);
            }

            for (let y = 0; y <= yMax; y += yTick) {
                const { screenY } = dataToScreen(0, y);
                sketch.line(0, screenY, 5, screenY);
                sketch.textAlign(sketch.RIGHT, sketch.CENTER);
                sketch.text(y, -5, screenY);
            }

            sketch.textAlign(sketch.CENTER, sketch.TOP);
            sketch.text(xLabel, canvasWidth / 2, canvasHeight + 30);
            sketch.textAlign(sketch.RIGHT, sketch.CENTER);
            sketch.text(yLabel, -30, canvasHeight / 2);
        }

        sketches.push(new p5(sketch => {
            sketch.setup = () => {
                const canvas = sketch.createCanvas(300, 200);
                canvas.parent('plot-canvas-1');
                sketch.noLoop();
            };
            sketch.draw = () => {
                drawPlot(sketch, P, 't (hr)', 'P (in)', 5, 10, 0);
            };
        }, 'plot-canvas-1'));

        sketches.push(new p5(sketch => {
            sketch.setup = () => {
                const canvas = sketch.createCanvas(300, 200);
                canvas.parent('plot-canvas-2');
                sketch.noLoop();
            };
            sketch.draw = () => {
                drawPlot(sketch, U, 't (hr)', 'Q (cfs/in)', 5, 10, 0);
            };
        }, 'plot-canvas-2'));

        sketches.push(new p5(sketch => {
            sketch.setup = () => {
                const canvas = sketch.createCanvas(300, 200);
                canvas.parent('plot-canvas-3');
                sketch.noLoop();
            };
            sketch.draw = () => {
                drawPlot(sketch, C1, 't (hr)', 'Q (cfs)', 8, 100, 0);
            };
        }, 'plot-canvas-3'));

        sketches.push(new p5(sketch => {
            sketch.setup = () => {
                const canvas = sketch.createCanvas(300, 200);
                canvas.parent('plot-canvas-4');
                sketch.noLoop();
            };
            sketch.draw = () => {
                drawPlot(sketch, C2, 't (hr)', 'Q (cfs)', 8, 100, 1);
            };
        }, 'plot-canvas-4'));

        sketches.push(new p5(sketch => {
            sketch.setup = () => {
                const canvas = sketch.createCanvas(300, 200);
                canvas.parent('plot-canvas-5');
                sketch.noLoop();
            };
            sketch.draw = () => {
                drawPlot(sketch, C3, 't (hr)', 'Q (cfs)', 8, 100, 2);
            };
        }, 'plot-canvas-5'));

        sketches.push(new p5(sketch => {
            sketch.setup = () => {
                const canvas = sketch.createCanvas(300, 200);
                canvas.parent('plot-canvas-6');
                sketch.noLoop();
            };
            sketch.draw = () => {
                drawPlot(sketch, Q, 't (hr)', 'Q (cfs)', 8, 100, 0);
            };
        }, 'plot-canvas-6'));

        update();
    </script>
