    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <script>
        // Helper function to convert decimal to fraction
        function toFraction(decimal, tolerance = 1e-6) {
            if (Math.abs(decimal - Math.round(decimal)) < tolerance) {
                return Math.round(decimal).toString();
            }
            let frac = Math.abs(decimal);
            let n = 1, d = 1, whole = Math.floor(frac);
            frac -= whole;
            let n1 = 0, d1 = 1, n2 = 1, d2 = 0;
            while (true) {
                let a = Math.floor(frac);
                let temp = n2; n2 = a * n2 + n1; n1 = temp;
                temp = d2; d2 = a * d2 + d1; d1 = temp;
                let error = Math.abs(frac - n2/d2);
                if (error < tolerance) break;
                if (n2/d2 > frac) {
                    n2 = n1; d2 = d1; break;
                }
                frac = 1 / (frac - a);
            }
            n = n2 + whole * d2;
            d = d2;
            return (whole === 0 ? `${n}/${d}` : `${whole} + ${n}/${d}`).replace(/1 \+/, '1');
        }

        // Global variables for matrix values
        let a = [-5, -3, -1, -4];
        let b = [-5, -3, -1, -4];

        // Update payoff table
        function updatePayoffTable() {
            const cells = [
                [a[0], b[0]], [a[1], b[1]],
                [a[2], b[2]], [a[3], b[3]]
            ];
            for (let i = 0; i < 4; i++) {
                const cell = document.getElementById(`cell-${i+1}`);
                cell.textContent = `${cells[i][0]}, ${cells[i][1]}`;
            }
        }

        // Update best responses and redraw
        function updateVisualization() {
            // Recalculate best responses
            const a11 = a[0], a12 = a[1], a21 = a[2], a22 = a[3];
            const b11 = b[0], b12 = b[1], b21 = b[2], b22 = b[3];

            // Player 1 indifference
            const den1 = a11 - a12 - a21 + a22;
            let y1_indiff = den1 !== 0 ? (a22 - a12) / den1 : 0;
            let p1Dominant = null;
            if (den1 === 0) {
                if (a12 - a22 > 0) p1Dominant = 1;
                else if (a12 - a22 < 0) p1Dominant = 0;
            } else if (y1_indiff <= 0) {
                p1Dominant = (a11 + a22) > (a12 + a21) ? 0 : 1;
            } else if (y1_indiff >= 1) {
                p1Dominant = (a11 + a22) > (a12 + a21) ? 1 : 0;
            }

            // Player 2 indifference
            const den2 = b11 - b12 - b21 + b22;
            let x1_indiff = den2 !== 0 ? (b22 - b21) / den2 : 0;
            let p2Dominant = null;
            if (den2 === 0) {
                if (b21 - b22 > 0) p2Dominant = 1;
                else if (b21 - b22 < 0) p2Dominant = 0;
            } else if (x1_indiff <= 0) {
                p2Dominant = (b11 + b22) > (b12 + b21) ? 0 : 1;
            } else if (x1_indiff >= 1) {
                p2Dominant = (b11 + b22) > (b12 + b21) ? 1 : 0;
            }

            // Store values for p5.js
            window.p5Data = {
                p1: { dominant: p1Dominant, y: y1_indiff, den: den1 },
                p2: { dominant: p2Dominant, x: x1_indiff, den: den2 }
            };

            // Redraw plot
            if (window.myp5) window.my_p5.redraw();
        }

        // Initialize slider controls
        function initSliders() {
            const sliders = [
                'a11', 'a12', 'a21', 'a22',
                'b11', 'b12', 'b21', 'b22'
            ];

            sliders.forEach((id, index) => {
                const slider = document.getElementById(`slider-${id}`);
                const valueSpan = document.getElementById(`value-${id}`);
                const minusBtn = document.getElementById(`btn-minus-${id}`);
                const plusBtn = document.getElementById(`btn-plus-${id}`);

                // Update function
                const update = () => {
                    const val = parseInt(slider.value);
                    valueSpan.textContent = val;
                    if (id.startsWith('a')) a[index % 4] = val;
                    else b[index % 4] = val;
                    updatePayoffTable();
                    updateVisualization();
                };

                // Initial update
                update();

                // Event listeners
                slider.addEventListener('input', update);
                minusBtn.addEventListener('click', () => {
                    slider.value = Math.max(slider.min, parseInt(slider.value) - 1);
                    update();
                });
                plusBtn.addEventListener('click', () => {
                    slider.value = Math.min(slider.max, parseInt(slider.value) + 1);
                    update();
                });
            });
        }

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(400, 400);
            canvas.parent('canvas-container');
            noLoop();
        }

        function draw() {
            if (!window.p5Data) return;
            const data = window.p5Data;
            background(255);
            translate(width * 0.1, height * 0.9);
            scale(width * 0.8, -height * 0.8);

            // Draw axes
            stroke(0);
            line(0, 0, 1, 0);
            line(0, 0, 0, 1);

            // Draw ticks
            for (let i = 0; i <= 5; i++) {
                const t = i * 0.2;
                line(t, -0.02, t, 0.02);
                line(-0.02, t, 0.02, t);
                if (t > 0 && t < 1) {
                    text(t.toFixed(1), t, -0.05);
                    text(t.toFixed(1), -0.05, t);
                }
            }

            // Draw Player 1's best response (blue)
            stroke(0, 0, 255);
            if (data.p1.dominant !== null) {
                line(data.p1.dominant, 0, data.p1.dominant, 1);
            } else {
                line(0, data.p1.y, 1, data.p1.y);
                if (data.p1.den > 0) {
                    line(0, 0, 0, data.p1.y);
                    line(1, data.p1.y, 1, 1);
                } else {
                    line(0, data.p1.y, 0, 1);
                    line(1, 0, 1, data.p1.y);
                }
            }

            // Draw Player 2's best response (green)
            stroke(0, 255, 0);
            if (data.p2.dominant !== null) {
                line(0, data.p2.dominant, 1, data.p2.dominant);
            } else {
                line(data.p2.x, 0, data.p2.x, 1);
                if (data.p2.den > 0) {
                    line(0, 0, data.p2.x, 0);
                    line(data.p2.x, 1, 1, 1);
                } else {
                    line(0, 1, data.p2.x, 1);
                    line(data.p2.x, 0, 1, 0);
                }
            }

            // Calculate intersections
            const intersections = [];
            if (data.p1.dominant !== null && data.p2.dominant !== null) {
                intersections.push([data.p1.dominant, data.p2.dominant]);
            } else if (data.p1.dominant !== null) {
                intersections.push([data.p1.dominant, 0], [data.p1.dominant, 1]);
            } else if (data.p2.dominant !== null) {
                intersections.push([0, data.p2.dominant], [1, data.p2.dominant]);
            } else {
                // Axis-aligned intersections
                if (data.p1.y >= 0 && data.p1.y <= 1) {
                    if (data.p2.x >= 0 && data.p2.x <= 1) {
                        intersections.push([data.p2.x, data.p1.y]);
                    }
                    if (data.p2.den > 0) {
                        if (data.p1.y <= 0) intersections.push([0, 0], [1, 0]);
                        if (data.p1.y >= 1) intersections.push([0, 1], [1, 1]);
                    } else {
                        if (data.p1.y <= 0) intersections.push([0, 0], [1, 0]);
                        if (data.p1.y >= 1) intersections.push([0, 1], [1, 1]);
                    }
                }
            }

            // Draw intersections
            stroke(255, 0, 0);
            strokeWeight(4);
            fill(255, 0, 0);
            noFill();

            // Convert intersections to unique points
            const uniquePoints = [...new Set(intersections.map(p => JSON.stringify(p)))].map(p => JSON.parse(p));

            // Draw lines between points
            for (let i = 0; i < uniquePoints.length; i++) {
                for (let j = i + 1; j < uniquePoints.length; j++) {
                    const p1 = uniquePoints[i];
                    const p2 = uniquePoints[j];
                    if (p1[0] === p2[0] || p1[1] === p2[1]) {
                        line(p1[0], p1[1], p2[0], p2[1]);
                    }
                }
            }

            // Draw points
            uniquePoints.forEach(p => {
                ellipse(p[0], p[1], 0.05, 0.05);
            });

            // Update equilibria display
            const vertices = uniquePoints.map(p => {
                const x = toFraction(p[0]);
                const y = toFraction(p[1]);
                return `(${x}, ${y})`;
            });
            const latex = `\\{ ${vertices.join(', ')} \\}`;
            document.getElementById('equilibria-display').innerHTML = `\\( ${latex} \\)`;
            MathJax.typesetPromise([document.getElementById('equilibria-display')]);
        }

        // Initialize everything
        window.addEventListener('DOMContentLoaded', () => {
            initSliders();
            updatePayoffTable();
            updateVisualization();
        });

        // Store p5 instance
        window.my_p5 = new p5();
    </script>
</body>
