</head>
<body>
    <div id="demo-container">
        <div id="controls-container">
            <div id="slider-group">
                <label for="slider-t">choose quadratic</label>
                <input type="range" id="slider-t" min="-5" max="5" step="0.01" value="0.5">
                <input type="number" id="input-t" min="-5" max="5" step="0.01" value="0.5">
                <button id="btn-reset" style="font-family: 'Courier New', monospace;">&#x21BB;</button>
            </div>
            <div id="checkbox-group">
                <input type="checkbox" id="checkbox-family">
                <label for="checkbox-family">family</label>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Global state
        let locators = [
            { x: -4, y: 1 },
            { x: -2, y: 1.8 },
            { x: 1, y: -1 },
            { x: 4, y: -1 }
        ];
        let selectedLocator = null;
        let cubicCoeffs = { a: 0, b: 0, c: 0, d: 0 };
        let currentT = 0.5;
        let showFamily = false;
        const colorPalette = [
            [255, 0, 0, 100], [0, 255, 0, 100], [0, 0, 255, 100],
            [255, 255, 0, 100], [255, 0, 255, 100], [0, 255, 255, 100]
        ];

        function setup() {
            const canvas = createCanvas(800, 500);
            canvas.parent('canvas-container');
            noLoop();
            calculateCubicCoefficients();
        }

        function draw() {
            background(255);
            drawAxes();
            drawLocators();

            if (!showFamily) {
                drawCubicCurve();
                drawQuadraticCurve(currentT);
            } else {
                drawQuadraticFamily();
                drawQuadraticCurve(currentT);
            }
        }

        function drawAxes() {
            const scale = 50;
            const originX = width / 2;
            const originY = height / 2;

            // Draw axes
            stroke(200);
            line(0, originY, width, originY);
            line(originX, 0, originX, height);

            // Draw ticks
            for (let x = -6; x <= 6; x++) {
                const px = originX + x * scale;
                line(px, originY - 5, px, originY + 5);
                text(x, px, originY + 15);
            }
            for (let y = -5; y <= 5; y++) {
                const py = originY - y * scale;
                line(originX - 5, py, originX + 5, py);
                text(y, originX + 10, py);
            }
        }

        function drawLocators() {
            const scale = 50;
            const originX = width / 2;
            const originY = height / 2;

            stroke(0);
            strokeWeight(2);
            fill(255);

            locators.forEach(loc => {
                const px = originX + loc.x * scale;
                const py = originY - loc.y * scale;
                ellipse(px, py, 15, 15);
                line(px - 7, py, px + 7, py);
                line(px, py - 7, px, py + 7);
            });
        }

        function drawCubicCurve() {
            const scale = 50;
            const originX = width / 2;
            const originY = height / 2;

            stroke('#3f51b5');
            strokeWeight(3);
            noFill();

            beginShape();
            for (let px = 0; px <= width; px++) {
                const x = (px - originX) / scale;
                const y = cubicCoeffs.a * Math.pow(x, 3) + 
                         cubicCoeffs.b * Math.pow(x, 2) + 
                         cubicCoeffs.c * x + 
                         cubicCoeffs.d;
                const py = originY - y * scale;
                vertex(px, py);
            }
            endShape();
        }

        function drawQuadraticCurve(t) {
            const scale = 50;
            const originX = width / 2;
            const originY = height / 2;
            const qCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);

            stroke('#f57c00');
            strokeWeight(3);
            noFill();

            beginShape();
            for (let px = 0; px <= width; px++) {
                const x = (px - originX) / scale;
                const y = qCoeffs.p * Math.pow(x, 2) + 
                         qCoeffs.q * x + 
                         qCoeffs.r;
                const py = originY - y * scale;
                vertex(px, py);
            }
            endShape();
        }

        function drawQuadraticFamily() {
            const scale = 50;
            const originX = width / 2;
            const originY = height / 2;

            for (let i = 0; i < 20; i++) {
                const t = -5 + i * 0.5;
                const qCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
                const color = colorPalette[i % colorPalette.length];

                fill(color);
                noStroke();

                beginShape();
                const xMin = -6;
                const xMax = 6;
                const yMin = -5;
                const yMax = 5;

                // Determine parabola direction
                const a = qCoeffs.p;
                const yBoundary = a > 0 ? yMax : yMin;

                // Start from left boundary
                vertex(originX + xMin * scale, originY - yBoundary * scale);

                // Draw curve points
                for (let x = xMin; x <= xMax; x += 0.1) {
                    const y = qCoeffs.p * Math.pow(x, 2) + 
                             qCoeffs.q * x + 
                             qCoeffs.r;
                    vertex(originX + x * scale, originY - y * scale);
                }

                // End at right boundary
                vertex(originX + xMax * scale, originY - yBoundary * scale);
                endShape(CLOSE);
            }
        }

        function mousePressed() {
            const scale = 50;
            const originX = width / 2;
            const originY = height / 2;

            for (let i = 0; i < locators.length; i++) {
                const loc = locators[i];
                const px = originX + loc.x * scale;
                const py = originY - loc.y * scale;
                if (dist(mouseX, mouseY, px, py) < 10) {
                    selectedLocator = loc;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (selectedLocator !== null) {
                const scale = 50;
                const originX = width / 2;
                const originY = height / 2;
                selectedLocator.x = (mouseX - originX) / scale;
                selectedLocator.y = (originY - mouseY) / scale;
                calculateCubicCoefficients();
                redraw();
            }
        }

        function mouseReleased() {
            selectedLocator = null;
        }

        function calculateCubicCoefficients() {
            // Solve system of equations for cubic coefficients
            const points = locators.map(p => ({ x: p.x, y: p.y }));
            const M = [
                [Math.pow(points[0].x, 3), points[0].x * points[0].x, points[0].x, 1],
                [Math.pow(points[1].x, 3), points[1].x * points[1].x, points[1].x, 1],
                [Math.pow(points[2].x, 3), points[2].x * points[2].x, points[2].x, 1],
                [Math.pow(points[3].x, 3), points[3].x * points[3].x, points[3].x, 1]
            ];
            const Y = [points[0].y, points[1].y, points[2].y, points[3].y];

            // Gaussian elimination
            const n = 4;
            for (let i = 0; i < n; i++) {
                // Partial pivoting
                let maxRow = i;
                for (let k = i + 1; k < n; k++) {
                    if (Math.abs(M[k][i]) > Math.abs(M[maxRow][i])) {
                        maxRow = k;
                    }
                }
                [M[i], M[maxRow]] = [M[maxRow], M[i]];
                [Y[i], Y[maxRow]] = [Y[maxRow], Y[i]];

                // Eliminate
                for (let k = i + 1; k < n; k++) {
                    const factor = M[k][i] / M[i][i];
                    for (let j = i; j < n; j++) {
                        M[k][j] -= factor * M[i][j];
                    }
                    Y[k] -= factor * Y[i];
                }
            }

            // Back substitution
            const coeffs = [0, 0, 0, 0];
            for (let i = n - 1; i >= 0; i--) {
                coeffs[i] = Y[i];
                for (let j = i + 1; j < n; j++) {
                    coeffs[i] -= M[i][j] * coeffs[j];
                }
                coeffs[i] /= M[i][i];
            }

            cubicCoeffs = { a: coeffs[0], b: coeffs[1], c: coeffs[2], d: coeffs[3] };
        }

        function getOsculatingQuadraticCoeffs(t, cubicCoeffs) {
            const p = 3 * cubicCoeffs.a * t + cubicCoeffs.b;
            const q = -3 * cubicCoeffs.a * Math.pow(t, 2) + cubicCoeffs.c;
            const r = cubicCoeffs.a * Math.pow(t, 3) + cubicCoeffs.d;
            return { p, q, r };
        }

        // Event listeners
        document.getElementById('slider-t').addEventListener('input', function(e) {
            currentT = parseFloat(e.target.value);
            document.getElementById('input-t').value = currentT.toFixed(2);
            redraw();
        });

        document.getElementById('input-t').addEventListener('input', function(e) {
            currentT = parseFloat(e.target.value);
            document.getElementById('slider-t').value = currentT;
            redraw();
        });

        document.getElementById('checkbox-family').addEventListener('change', function(e) {
            showFamily = e.target.checked;
            redraw();
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            currentT = 0.5;
            showFamily = false;
            locators = [
                { x: -4, y: 1 },
                { x: -2, y: 1.8 },
                { x: 1, y: -1 },
                { x: 4, y: -1 }
            ];
            document.getElementById('slider-t').value = 0.5;
            document.getElementById('input-t').value = '0.5';
            document.getElementById('checkbox-family').checked = false;
            calculateCubicCoefficients();
            redraw();
        });
    </script>
</body>
</html>
