    .units-group label {
      font-weight: normal;
    }
    #visualization-area {
      display: flex;
      gap: 20px;
      justify-content: space-between;
    }
    #plot-container {
      flex: 1;
      min-height: 400px;
    }
    #piston-container {
      width: 300px;
      height: 400px;
      border: 1px solid #ccc;
