    input[type="range"] {
      width: 100%;
    }
    .toggle-radio {
      display: none;
    }
    .toggle-label {
      padding: 5px 10px;
      border: 1px solid #ccc;
      border-radius: 4px;
      cursor: pointer;
      transition: background-color 0.2s;
    }
    .toggle-radio:checked + .toggle-label {
      background-color: #007bff;
      color: white;
      border-color: #007bff;
    }
  </style>
</head>
<body>
  <div id="demo-container">
    <div id="control-panel">
      <div class="control-group">
        <label for="slider-v0">initial volume v₀</label>
        <input type="range" id="slider-v0" min="100" max="500" step="1" value="211">
      </div>
      <div class="control-group">
        <label for="slider-T">temperature T</label>
        <input type="range" id="slider-T" min="-273" max="727" step="1" value="270">
      </div>
      <div class="control-group units-group">
        <label>temperature units</label>
        <input type="radio" id="radio-kelvin" name="temp-units" value="K" class="toggle-radio">
        <label for="radio-kelvin" class="toggle-label">Kelvin</label>
        <input type="radio" id="radio-celsius" name="temp-units" value="C" class="toggle-radio" checked>
        <label for="radio-celsius" class="toggle-label">Celsius</label>
      </div>
    </div>
    <div id="visualization-area">
      <div id="plot-container"></div>
      <div id="piston-container"></div>
    </div>
  </div>

  <script>
    // Helper functions
    function map(value, in_min, in_max, out_min, out_max) {
      return ((value - in_min) * (out_max - out_min)) / (in_max - in_min) + out_min;
    }
    function constrain(value, min, max) {
      return Math.max(min, Math.min(max, value));
    }

    // Global state
    let v0 = 211;
    let T = 270; // Current temperature in selected unit
    let unit = 'C';
    let T_K = T + 273.15; // Current temperature in Kelvin
    let k = v0 / 273.15;

    // Precompute temperature values for the line plot
    const T_K_values = [];
    for (let t = 0; t <= 1000; t += 1) {
      T_K_values.push(t);
    }
    let V_values = T_K_values.map(t => k * t);

    // Get DOM elements
    const sliderV0 = document.getElementById('slider-v0');
    const sliderT = document.getElementById('slider-T');
    const radioKelvin = document.getElementById('radio-kelvin');
    const radioCelsius = document.getElementById('radio-celsius');
    const plotDiv = document.getElementById('plot-container');

    // Initialize Plotly plot
    function initPlot() {
      const xData = unit === 'C' ? T_K_values.map(t => t - 273.15) : T_K_values;
      const currentT = unit === 'C' ? T : T_K;
      const currentV = k * T_K;

      Plotly.newPlot(plotDiv, [
        {
          x: xData,
          y: V_values,
          mode: 'lines',
          name: 'Charles\'s Law',
          marker: {
            color: T_K_values,
            colorscale: 'Viridis',
            cmin: 0,
            cmax: 1000
          }
        },
        {
          x: [currentT],
          y: [currentV],
          mode: 'markers',
          name: 'Current State',
          marker: {
            size: 12,
            color: getColor(T_K)
          }
        }
      ], {
        yaxis: {
          title: 'volume',
          range: [0, 2000]
        },
        xaxis: {
          title: unit === 'C' ? 'temperature °C' : 'temperature K',
          range: unit === 'C' ? [-273, 727] : [0, 1000]
        },
        showlegend: false,
        margin: { t: 40, b: 40, l: 40, r: 20 }
      });
    }

    // Update plot data
    function updatePlot() {
      const xData = unit === 'C' ? T_K_values.map(t => t - 273.15) : T_K_values;
      const currentT = unit === 'C' ? T : T_K;
      const currentV = k * T_K;

      Plotly.restyle(plotDiv, {
        x: [xData, [currentT]],
        y: [V_values, [currentV]],
        marker: [{ color: getColor(T_K) }]
      }, [0, 1]);

      const xaxis = {
        title: unit === 'C' ? 'temperature °C' : 'temperature K',
        range: unit === 'C' ? [-273, 727] : [0, 1000]
      };
      Plotly.relayout(plotDiv, { xaxis });
    }

    // Color mapping function for both Plotly and p5
    function getColor(T_K) {
      let t = constrain(T_K, 0, 1000);
      let r, g, b;
      if (t <= 500) {
        r = map(t, 0, 500, 128, 255);
        g = map(t, 0, 500, 0, 255);
        b = map(t, 0, 500, 128, 0);
      } else {
        r = map(t, 500, 1000, 255, 255);
        g = map(t, 500, 1000, 255, 0);
        b = map(t, 500, 1000, 0, 0);
      }
      return `rgb(${Math.round(r)}, ${Math.round(g)}, ${Math.round(b)})`;
    }

    // Event listeners
    sliderV0.addEventListener('input', () => {
      v0 = parseInt(sliderV0.value);
      k = v0 / 273.15;
      V_values = T_K_values.map(t => k * t);
      updatePlot();
    });

    sliderT.addEventListener('input', () => {
      T = parseInt(sliderT.value);
      T_K = unit === 'C' ? T + 273.15 : T;
      updatePlot();
    });

    radioKelvin.addEventListener('change', () => {
      if (radioKelvin.checked) {
        unit = 'K';
        T_K = T + 273.15;
        T = T_K;
        sliderT.min = 0;
        sliderT.max = 1000;
        sliderT.value = Math.round(T);
        updatePlot();
      }
    });

    radioCelsius.addEventListener('change', () => {
      if (radioCelsius.checked) {
        unit = 'C';
        T = T_K - 273.15;
        sliderT.min = -273;
        sliderT.max = 727;
        sliderT.value = Math.round(T);
        updatePlot();
      }
    });

    // Initialize plot
    initPlot();

    // p5.js sketch for piston animation
    function setup() {
      let canvas = createCanvas(300, 400);
      canvas.parent('piston-container');
      noLoop();
    }

    function draw() {
      background(255);

      // Draw cylinder walls
      fill(0, 0, 255);
      rect(50, 50, 20, height - 100);
      rect(width - 70, 50, 20, height - 100);

      // Draw base
      line(50, height - 50, width - 70, height - 50);

      // Calculate gas height
      let gasHeight = map(k * T_K, 100, 500, 0, height - 100);
      gasHeight = constrain(gasHeight, 0, height - 100);

      // Draw gas
      let gasColor = getColor(T_K);
      fill(gasColor);
      rect(70, height - 50 - gasHeight, width - 140, gasHeight);

      // Draw piston
      fill(200);
      rect(70, height - 50 - gasHeight - 20, width - 140, 20);
      rect(width/2 - 10, height - 50 - gasHeight - 40, 20, 20);

      // Draw pressure arrows
      fill(255, 165, 0);
      for (let i = 0; i < 5; i++) {
        triangle(
          70 + i*20, height - 50 - gasHeight - 20,
          70 + i*20 + 5, height - 50 - gasHeight - 30,
          70 + i*20 + 15, height - 50 - gasHeight - 20
        );
      }
    }

    // Update piston animation on plot update
    function updatePiston() {
      redraw();
    }

    // Initial draw
    draw();
  </script>
</body>
</html>
