    </style>
</head>
<body>
    <h1>Circles & Triangles</h1>
    <p>Circles have 360 degrees, and the sum of angles in a triangle is 180 degrees. This visualization demonstrates the relationship between central angles and inscribed angles in a circle.</p>

    <div id="main-container">
        <div id="canvas-container"></div>
        <div id="info-panel">
            <span class="info-header">Arc</span>
            <span class="info-header">Vertex</span>
            <span id="arc-label-1">Arc AB</span>
            <span id="arc-value-1">108°</span>
            <span id="vertex-label-1">Vertex C</span>
            <span id="vertex-value-1">54.0°</span>
            <span id="arc-label-2"></span>
            <span id="arc-value-2"></span>
            <span id="vertex-label-2"></span>
            <span id="vertex-value-2"></span>
            <span id="arc-label-3"></span>
            <span id="arc-value-3"></span>
            <span id="vertex-label-3"></span>
            <span id="vertex-value-3"></span>
            <hr style="grid-column: 1 / -1;">
            <span class="info-total-label">Total</span>
            <span id="arc-total">?</span>
            <span class="info-total-label"></span>
            <span id="vertex-total">?</span>
        </div>
    </div>

    <div id="controls-container">
        <div class="control-group">
            <label>vertex</label>
            <input type="radio" id="radio-a" name="vertex-select" value="A">
            <label for="radio-a">A</label>
            <input type="radio" id="radio-b" name="vertex-select" value="B">
            <label for="radio-b">B</label>
            <input type="radio" id="radio-c" name="vertex-select" value="C" checked>
            <label for="radio-c">C</label>
            <input type="radio" id="radio-none" name="vertex-select" value="None">
            <label for="radio-none">None</label>
        </div>
        <div class="control-group">
            <label for="slider-a">A</label>
            <input type="range" id="slider-a" min="0" max="360" step="0.5" value="306">
        </div>
        <div class="control-group">
            <label for="slider-b">B</label>
            <input type="range" id="slider-b" min="0" max="360" step="0.5" value="54">
        </div>
        <div class="control-group">
            <label for="slider-c">C</label>
            <input type="range" id="slider-c" min="0" max="360" step="0.5" value="180">
        </div>
    </div>

    <script>
        let angleA = 306;
        let angleB = 54;
        let angleC = 180;
        let selectedVertex = 'C';
        let draggingVertex = null;
        const radius = 150;
        const centerX = 200;
        const centerY = 200;

        function setup() {
            const canvas = createCanvas(400, 400);
            canvas.parent('canvas-container');

            document.getElementById('slider-a').addEventListener('input', (e) => {
                angleA = parseFloat(e.target.value);
                updateVisualization();
            });

            document.getElementById('slider-b').addEventListener('input', (e) => {
                angleB = parseFloat(e.target.value);
                updateVisualization();
            });

            document.getElementById('slider-c').addEventListener('input', (e) => {
                angleC = parseFloat(e.target.value);
                updateVisualization();
            });

            document.querySelectorAll('input[name="vertex-select"]').forEach(radio => {
                radio.addEventListener('change', (e) => {
                    selectedVertex = e.target.value;
                    updateVisualization();
                });
            });

            updateVisualization();
        }

        function draw() {
            background(255);

            // Draw background sectors
            drawSector(angleB, angleC, '#e0f0e0'); // Arc BC
            drawSector(angleC, angleA, '#f5f5d5'); // Arc CA
            drawSector(angleA, angleB, '#e0e8f8'); // Arc AB

            // Draw main circle
            fill(170, 204, 222, 100);
            stroke(102, 102, 102);
            strokeWeight(2);
            ellipse(centerX, centerY, radius*2);

            // Draw triangle
            fill(173, 216, 230, 128);
            stroke(102, 102, 102);
            strokeWeight(2);
            beginShape();
            vertex(...getVertexCoords(angleA));
            vertex(...getVertexCoords(angleB));
            vertex(...getVertexCoords(angleC));
            endShape(CLOSE);

            // Draw vertices
            noStroke();
            fill(0);
            textSize(20);
            textAlign(CENTER, CENTER);
            text('A', ...getLabelPosition(angleA));
            text('B', ...getLabelPosition(angleB));
            text('C', ...getLabelPosition(angleC));

            // Draw selected angle highlights
            if (selectedVertex !== 'None') {
                const arcInfo = getArcInfo(selectedVertex);
                drawCentralAngle(arcInfo.start, arcInfo.end, arcInfo.arcValue);
                drawInscribedAngle(selectedVertex, arcInfo.arcValue);
            }
        }

        function getVertexCoords(angle) {
            const rad = radians(angle);
            return [
                centerX + radius * cos(rad),
                centerY + radius * sin(rad)
            ];
        }

        function getLabelPosition(angle) {
            const rad = radians(angle);
            return [
                centerX + (radius + 25) * cos(rad),
                centerY + (radius + 25) * sin(rad)
            ];
        }

        function getArcInfo(vertex) {
            if (vertex === 'A') {
                const arcBC = (angleC - angleB + 360) % 360;
                return { start: angleB, end: angleC, arcValue: arcBC, vertexAngle: arcBC / 2 };
            } else if (vertex === 'B') {
                const arcCA = (angleA - angleC + 360) % 360;
                return { start: angleC, end: angleA, arcValue: arcCA, vertexAngle: arcCA / 2 };
            } else {
                const arcAB = (angleB - angleA + 360) % 360;
                return { start: angleA, end: angleB, arcValue: arcAB, vertexAngle: arcAB / 2 };
            }
        }

        function drawSector(startAngle, endAngle, color) {
            fill(color);
            noStroke();
            arc(centerX, centerY, radius*2, radius*2, radians(startAngle), radians(endAngle), PIE);
        }

        function drawCentralAngle(start, end, arcValue) {
            const angle = (end - start + 360) % 360;
            const mid = start + angle/2;

            stroke(0, 128, 0);
            strokeWeight(3);
            noFill();
            arc(centerX, centerY, radius*1.8, radius*1.8, radians(start), radians(end));

            // Arrowhead
            const arrowRad = radians(end);
            const arrowX = centerX + radius*1.8 * cos(arrowRad);
            const arrowY = centerY + radius*1.8 * sin(arrowRad);
            triangle(arrowX, arrowY, 
                     arrowX + 10*cos(arrowRad - PI/6), arrowY + 10*sin(arrowRad - PI/6),
                     arrowX + 10*cos(arrowRad + PI/6), arrowY + 10*sin(arrowRad + PI/6));

            // Label
            fill(0, 128, 0);
            noStroke();
            textSize(14);
            textAlign(CENTER);
            text(`θ = ${arcValue.toFixed(1)}°`, centerX + 50*cos(radians(mid)), centerY + 50*sin(radians(mid)));
        }

        function drawInscribedAngle(vertex, arcValue) {
            const [x, y] = getVertexCoords(vertex === 'A' ? angleA : vertex === 'B' ? angleB : angleC);
            const vertexAngle = arcValue / 2;

            stroke(255, 0, 0);
            strokeWeight(3);
            noFill();
            arc(x, y, 60, 60, 
                vertex === 'A' ? radians(angleC) : vertex === 'B' ? radians(angleA) : radians(angleB),
                vertex === 'A' ? radians(angleB) : vertex === 'B' ? radians(angleC) : radians(angleA));

            fill(255, 0, 0);
            noStroke();
            textSize(14);
            textAlign(CENTER);
            text(`θ/2 = ${vertexAngle.toFixed(1)}°`, x, y - 40);
        }

        function updateVisualization() {
            // Update sliders
            document.getElementById('slider-a').value = angleA;
            document.getElementById('slider-b').value = angleB;
            document.getElementById('slider-c').value = angleC;

            // Calculate arc and vertex angles
            const arcAB = (angleB - angleA + 360) % 360;
            const arcBC = (angleC - angleB + 360) % 360;
            const arcCA = (angleA - angleC + 360) % 360;

            const vertexA = arcBC / 2;
            const vertexB = arcCA / 2;
            const vertexC = arcAB / 2;

            // Update info panel
            if (selectedVertex === 'None') {
                document.getElementById('arc-label-1').innerText = 'Arc BC';
                document.getElementById('arc-value-1').innerText = `${arcBC.toFixed(1)}°`;
                document.getElementById('vertex-label-1').innerText = 'Vertex A';
                document.getElementById('vertex-value-1').innerText = `${vertexA.toFixed(1)}°`;

                document.getElementById('arc-label-2').innerText = 'Arc CA';
                document.getElementById('arc-value-2').innerText = `${arcCA.toFixed(1)}°`;
                document.getElementById('vertex-label-2').innerText = 'Vertex B';
                document.getElementById('vertex-value-2').innerText = `${vertexB.toFixed(1)}°`;

                document.getElementById('arc-label-3').innerText = 'Arc AB';
                document.getElementById('arc-value-3').innerText = `${arcAB.toFixed(1)}°`;
                document.getElementById('vertex-label-3').innerText = 'Vertex C';
                document.getElementById('vertex-value-3').innerText = `${vertexC.toFixed(1)}°`;

                document.getElementById('arc-total').innerText = '360°';
                document.getElementById('vertex-total').innerText = '180°';
            } else {
                const arcInfo = getArcInfo(selectedVertex);
                document.getElementById('arc-label-1').innerText = `Arc ${arcInfo.start === angleA ? 'AB' : arcInfo.start === angleB ? 'BC' : 'CA'}`;
                document.getElementById('arc-value-1').innerText = `${arcInfo.arcValue.toFixed(1)}°`;
                document.getElementById('vertex-label-1').innerText = `Vertex ${selectedVertex}`;
                document.getElementById('vertex-value-1').innerText = `${arcInfo.vertexAngle.toFixed(1)}°`;

                document.getElementById('arc-label-2').innerText = '';
                document.getElementById('arc-value-2').innerText = '';
                document.getElementById('vertex-label-2').innerText = '';
                document.getElementById('vertex-value-2').innerText = '';

                document.getElementById('arc-label-3').innerText = '';
                document.getElementById('arc-value-3').innerText = '';
                document.getElementById('vertex-label-3').innerText = '';
                document.getElementById('vertex-value-3').innerText = '';

                document.getElementById('arc-total').innerText = '?';
                document.getElementById('vertex-total').innerText = '?';
            }
        }

        // Mouse interaction
        function mousePressed() {
            const vertices = [
                { angle: angleA, name: 'A' },
                { angle: angleB, name: 'B' },
                { angle: angleC, name: 'C' }
            ];

            for (const v of vertices) {
                const [x, y] = getVertexCoords(v.angle);
                if (dist(mouseX, mouseY, x, y) < 10) {
                    draggingVertex = v.name;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (draggingVertex) {
                const dx = mouseX - centerX;
                const dy = mouseY - centerY;
                let angle = degrees(atan2(dy, dx));
                if (angle < 0) angle += 360;

                if (draggingVertex === 'A') angleA = angle;
                else if (draggingVertex === 'B') angleB = angle;
                else if (draggingVertex === 'C') angleC = angle;

                updateVisualization();
            }
        }

        function mouseReleased() {
            draggingVertex = null;
        }
    </script>
</body>
</html>
