      </div>
      <div class="control-group">
        <label>φ</label>
        <button id="btn-dec-phi">-</button>
        <input type="range" id="slider-phi" min="0.1" max="10" step="0.1" value="2.0">
        <button id="btn-inc-phi">+</button>
        <span id="value-phi">2.0</span>
      </div>
      <div class="control-group">
        <label>β</label>
        <button id="btn-dec-beta">-</button>
        <input type="range" id="slider-beta" min="-0.5" max="0.5" step="0.01" value="0.2">
        <button id="btn-inc-beta">+</button>
        <span id="value-beta">0.20</span>
      </div>
      <div class="control-group">
        <label>γ</label>
        <button id="btn-dec-gamma">-</button>
        <input type="range" id="slider-gamma" min="0" max="40" step="1" value="20">
        <button id="btn-inc-gamma">+</button>
        <span id="value-gamma">20</span>
      </div>
      <div class="control-group">
        <label>ξ</label>
        <button id="btn-dec-xi">-</button>
        <input type="range" id="slider-xi" min="0.001" max="1.0" step="0.001" value="0.4">
        <button id="btn-inc-xi">+</button>
        <span id="value-xi">0.400</span>
      </div>
    </div>
    <div id="visualization-panel">
      <div id="plot-concentration"></div>
      <div id="plot-temperature"></div>
    </div>
  </div>

  <script>
    const sliders = {
      tau: document.getElementById('slider-tau'),
      phi: document.getElementById('slider-phi'),
      beta: document.getElementById('slider-beta'),
      gamma: document.getElementById('slider-gamma'),
      xi: document.getElementById('slider-xi')
    };

    const buttons = {
      decTau: document.getElementById('btn-dec-tau'),
      incTau: document.getElementById('btn-inc-tau'),
      decPhi: document.getElementById('btn-dec-phi'),
      incPhi: document.getElementById('btn-inc-phi'),
      decBeta: document.getElementById('btn-dec-beta'),
      incBeta: document.getElementById('btn-inc-beta'),
      decGamma: document.getElementById('btn-dec-gamma'),
      incGamma: document.getElementById('btn-inc-gamma'),
      decXi: document.getElementById('btn-dec-xi'),
      incXi: document.getElementById('btn-inc-xi')
    };

    const values = {
      tau: document.getElementById('value-tau'),
      phi: document.getElementById('value-phi'),
      beta: document.getElementById('value-beta'),
      gamma: document.getElementById('value-gamma'),
      xi: document.getElementById('value-xi')
    };

    function updateValueDisplays() {
      values.tau.textContent = parseFloat(sliders.tau.value).toFixed(2);
      values.phi.textContent = parseFloat(sliders.phi.value).toFixed(1);
      values.beta.textContent = parseFloat(sliders.beta.value).toFixed(2);
      values.gamma.textContent = parseInt(sliders.gamma.value);
      values.xi.textContent = parseFloat(sliders.xi.value).toFixed(3);
    }

    function adjustSlider(slider, direction) {
      const step = parseFloat(slider.step);
      const newValue = Math.max(
        parseFloat(slider.min),
        Math.min(parseFloat(slider.max), parseFloat(slider.value) + direction * step)
      );
      slider.value = newValue;
      slider.dispatchEvent(new Event('input'));
    }

    buttons.decTau.addEventListener('click', () => adjustSlider(sliders.tau, -1));
    buttons.incTau.addEventListener('click', () => adjustSlider(sliders.tau, 1));
    buttons.decPhi.addEventListener('click', () => adjustSlider(sliders.phi, -1));
    buttons.incPhi.addEventListener('click', () => adjustSlider(sliders.phi, 1));
    buttons.decBeta.addEventListener('click', () => adjustSlider(sliders.beta, -1));
    buttons.incBeta.addEventListener('click', () => adjustSlider(sliders.beta, 1));
    buttons.decGamma.addEventListener('click', () => adjustSlider(sliders.gamma, -1));
    buttons.incGamma.addEventListener('click', () => adjustSlider(sliders.gamma, 1));
    buttons.decXi.addEventListener('click', () => adjustSlider(sliders.xi, -1));
    buttons.incXi.addEventListener('click', () => adjustSlider(sliders.xi, 1));

    let simulationData = null;
    let concentrationPlot = null;
    let temperaturePlot = null;

    function simulate() {
      const tau = parseFloat(sliders.tau.value);
      const phi = parseFloat(sliders.phi.value);
      const beta = parseFloat(sliders.beta.value);
      const gamma = parseFloat(sliders.gamma.value);

      const xi_values = Array.from({length: 100}, (_, i) => i / 99);

      const c = xi_values.map(xi => {
        return 1 - Math.exp(-phi * tau) * (1 - xi**2) * (1 + beta * gamma / 100);
      });

      const theta = xi_values.map(xi => {
        return 1 + beta * phi**2 * Math.exp(gamma / 10) * (1 - xi**2) * (1 - Math.exp(-tau));
      });

      return { xi: xi_values, c, theta };
    }

    function initPlots() {
      if (!simulationData) return;

      const { xi, c, theta } = simulationData;
      const xi_current = parseFloat(sliders.xi.value);

      const closestIndex = xi.reduce((prev, curr, i) => 
        Math.abs(curr - xi_current) < Math.abs(xi[prev] - xi_current) ? i : prev, 0
      );
      const c_at_xi = c[closestIndex];
      const theta_at_xi = theta[closestIndex];

      // Concentration Plot
      const concentrationData = [
        {
          x: xi,
          y: c,
          type: 'scatter',
          mode: 'lines',
          name: 'Concentration Profile'
        },
        {
          x: [xi_current, xi_current, 0],
          y: [0, c_at_xi, c_at_xi],
          type: 'scatter',
          mode: 'lines',
          line: {color: '#333333'},
          name: 'Marker'
        }
      ];

      const concentrationLayout = {
        yaxis: {range: [0, 1.1], title: 'c'},
        xaxis: {range: [0, 1], title: 'ξ'},
        margin: {t: 20, b: 40, l: 40, r: 20},
        showlegend: false,
        annotations: [{
          x: 0.7,
          y: 0.5,
          xref: 'paper',
          yref: 'paper',
          text: `τ = ${sliders.tau.value}<br>ξ = ${sliders.xi.value}<br>c = ${c_at_xi.toFixed(5)}`,
          showarrow: false,
          font: {size: 12}
        }]
      };

      concentrationPlot = document.getElementById('plot-concentration');
      Plotly.newPlot(concentrationPlot, concentrationData, concentrationLayout);

      // Temperature Plot
      const temperatureData = [
        {
          x: xi,
          y: theta,
          type: 'scatter',
          mode: 'lines',
          name: 'Temperature Profile'
        },
        {
          x: [xi_current, xi_current, 0],
          y: [Math.min(...theta), theta_at_xi, theta_at_xi],
          type: 'scatter',
          mode: 'lines',
          line: {color: '#333333'},
          name: 'Marker'
        }
      ];

      const temperatureLayout = {
        yaxis: {autorange: true, title: 'θ'},
        xaxis: {range: [0, 1], title: 'ξ'},
        margin: {t: 20, b: 40, l: 40, r: 20},
        showlegend: false,
        annotations: [{
          x: 0.7,
          y: 0.5,
          xref: 'paper',
          yref: 'paper',
          text: `τ = ${sliders.tau.value}<br>ξ = ${sliders.xi.value}<br>θ = ${theta_at_xi.toFixed(5)}`,
          showarrow: false,
          font: {size: 12}
        }]
      };

      temperaturePlot = document.getElementById('plot-temperature');
      Plotly.newPlot(temperaturePlot, temperatureData, temperatureLayout);
    }

    function updatePlots() {
      if (!simulationData) return;

      const { xi: xi_values, c, theta } = simulationData;
      const xi_current = parseFloat(sliders.xi.value);

      const closestIndex = xi_values.reduce((prev, curr, i) => 
        Math.abs(curr - xi_current) < Math.abs(xi_values[prev] - xi_current) ? i : prev, 0
      );
      const c_at_xi = c[closestIndex];
      const theta_at_xi = theta[closestIndex];

      // Update concentration plot
      const newConcentrationData = [
        {
          x: xi_values,
          y: c,
          type: 'scatter',
          mode: 'lines',
          name: 'Concentration Profile'
        },
        {
          x: [xi_current, xi_current, 0],
          y: [0, c_at_xi, c_at_xi],
          type: 'scatter',
          mode: 'lines',
          line: {color: '#333333'},
          name: 'Marker'
        }
      ];

      Plotly.react(concentrationPlot, newConcentrationData);

      // Update concentration annotation
      const concentrationAnnotation = {
        text: `τ = ${sliders.tau.value}<br>ξ = ${sliders.xi.value}<br>c = ${c_at_xi.toFixed(5)}`
      };

      Plotly.relayout(concentrationPlot, 'annotations[0].text', concentrationAnnotation.text);

      // Update temperature plot
      const newTemperatureData = [
        {
          x: xi_values,
          y: theta,
          type: 'scatter',
          mode: 'lines',
          name: 'Temperature Profile'
        },
        {
          x: [xi_current, xi_current, 0],
          y: [Math.min(...theta), theta_at_xi, theta_at_xi],
          type: 'scatter',
          mode: 'lines',
          line: {color: '#333333'},
          name: 'Marker'
        }
      ];

      Plotly.react(temperaturePlot, newTemperatureData);

      // Update temperature annotation
      const temperatureAnnotation = {
        text: `τ = ${sliders.tau.value}<br>ξ = ${sliders.xi.value}<br>θ = ${theta_at_xi.toFixed(5)}`
      };

      Plotly.relayout(temperaturePlot, 'annotations[0].text', temperatureAnnotation.text);
    }

    function updateMarkersAndAnnotations() {
      if (!simulationData) return;

      const { xi: xi_values, c, theta } = simulationData;
      const xi_current = parseFloat(sliders.xi.value);

      const closestIndex = xi_values.reduce((prev, curr, i) => 
        Math.abs(curr - xi_current) < Math.abs(xi_values[prev] - xi_current) ? i : prev, 0
      );
      const c_at_xi = c[closestIndex];
      const theta_at_xi = theta[closestIndex];

      // Update concentration marker
      const concentrationMarker = {
        x: [[xi_current, xi_current, 0]],
        y: [[0, c_at_xi, c_at_xi]]
      };

      Plotly.restyle(concentrationPlot, concentrationMarker, [1]);

      // Update concentration annotation
      const concentrationAnnotation = {
        text: `τ = ${sliders.tau.value}<br>ξ = ${sliders.xi.value}<br>c = ${c_at_xi.toFixed(5)}`
      };

      Plotly.relayout(concentrationPlot, 'annotations[0].text', concentrationAnnotation.text);

      // Update temperature marker
      const temperatureMarker = {
        x: [[xi_current, xi_current, 0]],
        y: [[Math.min(...theta), theta_at_xi, theta_at_xi]]
      };

      Plotly.restyle(temperaturePlot, temperatureMarker, [1]);

      // Update temperature annotation
      const temperatureAnnotation = {
        text: `τ = ${sliders.tau.value}<br>ξ = ${sliders.xi.value}<br>θ = ${theta_at_xi.toFixed(5)}`
      };

      Plotly.relayout(temperaturePlot, 'annotations[0].text', temperatureAnnotation.text);
    }

    // Event Listeners
    sliders.tau.addEventListener('input', () => {
      updateValueDisplays();
      simulationData = simulate();
      updatePlots();
    });
    sliders.phi.addEventListener('input', () => {
      updateValueDisplays();
      simulationData = simulate();
      updatePlots();
    });
    sliders.beta.addEventListener('input', () => {
      updateValueDisplays();
      simulationData = simulate();
      updatePlots();
    });
    sliders.gamma.addEventListener('input', () => {
      updateValueDisplays();
      simulationData = simulate();
      updatePlots();
    });
    sliders.xi.addEventListener('input', () => {
      updateValueDisplays();
      updateMarkersAndAnnotations();
    });

    // Initialize on load
    document.addEventListener('DOMContentLoaded', () => {
      updateValueDisplays();
      simulationData = simulate();
      initPlots();
    });
  </script>
</body>
</html>
