</head>
<body>
    <div id="main-container">
        <h1>Richards Growth Curve</h1>
        <p>This demo shows the Richards growth model with adjustable parameters and comparison to the 4/5 law approximation.</p>

        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-bmax">maximum biomass \(b_{max}\)</label>
                <div class="slider-row">
                    <input type="range" id="slider-bmax" min="1000" max="2500" step="1" value="1566">
                    <span>+</span>
                    <span id="value-bmax">1566</span>
                </div>
            </div>
            <div class="slider-group">
                <label for="slider-b0">initial biomass \(b_0\)</label>
                <div class="slider-row">
                    <input type="range" id="slider-b0" min="1" max="100" step="0.1" value="30">
                    <span>+</span>
                    <span id="value-b0">30</span>
                </div>
            </div>
            <div class="slider-group">
                <label for="slider-r">intrinsic growth rate \(r\)</label>
                <div class="slider-row">
                    <input type="range" id="slider-r" min="0.01" max="0.2" step="0.001" value="0.113">
                    <span>+</span>
                    <span id="value-r">0.113</span>
                </div>
            </div>
            <div class="slider-group">
                <label for="slider-beta">shape exponent \(\beta\)</label>
                <div class="slider-row">
                    <input type="range" id="slider-beta" min="0.1" max="1.5" step="0.001" value="0.477">
                    <span>+</span>
                    <span id="value-beta">0.477</span>
                </div>
            </div>
        </div>

        <div id="visualization-area">
            <div class="plot-table-container">
                <div id="plot-container"></div>
                <div id="table-container">
                    <table>
                        <thead>
                            <tr>
                                <th>Age (years)</th>
                                <th>Volume (m³ ha⁻¹)</th>
                            </tr>
                        </thead>
                        <tbody id="data-table-body">
                            <!-- Data will be populated by JavaScript -->
                        </tbody>
                    </table>
                </div>
            </div>
            <div id="results-display">
                <p>rejuvenation bias u = <span id="result-u">-</span></p>
                <p>site productivity p = <span id="result-p">-</span></p>
            </div>
        </div>
    </div>

    <script>
        // Static data
        const ages = [30, 35, 40, 45, 50, 55];
        const volumes = [289, 445, 565, 704, 833, 924];

        // Initialize table
        const tableBody = document.getElementById('data-table-body');
        ages.forEach((age, i) => {
            const row = document.createElement('tr');
            row.innerHTML = `<td>${age}</td><td>${volumes[i]}</td>`;
            tableBody.appendChild(row);
        });

        // Initialize plot
        const plotDiv = document.getElementById('plot-container');
        const data = [
            // Data points
            {
                x: ages,
                y: volumes,
                mode: 'markers',
                marker: {color: 'blue', size: 8},
                name: 'Data Points'
            },
            // Richards curve
            {
                x: [],
                y: [],
                mode: 'lines',
                line: {color: 'orange', width: 2},
                name: 'Richards Curve'
            },
            // 4/5 Law curve
            {
                x: [],
                y: [],
                mode: 'lines',
                line: {color: 'blue', width: 2},
                name: '4/5 Law'
            }
        ];

        const layout = {
            title: 'forest growth curve',
            xaxis: {
                title: 'stand age (years)',
                range: [15, 80],
                gridcolor: 'lightgrey',
                gridwidth: 1,
                griddash: 'dash'
            },
            yaxis: {
                title: 'wood volume, m³ ha⁻¹<br>Richards curve vs 4/5 law',
                range: [0, 1600],
                gridcolor: 'lightgrey',
                gridwidth: 1,
                griddash: 'dash',
                autorange: false
            },
            plot_bgcolor: '#FFFFF0',
            showlegend: false,
            margin: {t: 40, b: 40, l: 60, r: 20}
        };

        Plotly.newPlot('plot-container', data, layout);

        // Slider event listeners
        const sliders = [
            {id: 'slider-bmax', valueId: 'value-bmax', decimals: 0},
            {id: 'slider-b0', valueId: 'value-b0', decimals: 1},
            {id: 'slider-r', valueId: 'value-r', decimals: 3},
            {id: 'slider-beta', valueId: 'value-beta', decimals: 3}
        ];

        sliders.forEach(slider => {
            const input = document.getElementById(slider.id);
            const valueSpan = document.getElementById(slider.valueId);
            input.addEventListener('input', () => {
                valueSpan.textContent = parseFloat(input.value).toFixed(slider.decimals);
                update();
            });
        });

        function update() {
            // Get current values
            const bmax = parseFloat(document.getElementById('slider-bmax').value);
            const b0 = parseFloat(document.getElementById('slider-b0').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            // Update value displays
            document.getElementById('value-bmax').textContent = bmax.toFixed(0);
            document.getElementById('value-b0').textContent = b0.toFixed(1);
            document.getElementById('value-r').textContent = r.toFixed(3);
            document.getElementById('value-beta').textContent = beta.toFixed(3);

            // Calculate Richards curve
            const tValues = Array.from({length: 100}, (_, i) => 15 + i * 0.65);
            const m = Math.pow(bmax / b0, beta) - 1;
            const richardsY = tValues.map(t => 
                bmax / Math.pow(1 + m * Math.exp(-r * t), 1 / beta)
            );

            // Calculate inflection point
            const ti = Math.log(m * beta) / r;
            const vi = bmax * Math.pow((beta + 1) / (2 * beta), 1 / beta);
            const vPrimeI = (r / beta) * vi * (1 - Math.pow(vi / bmax, beta));

            // Calculate u and p
            const u = ti - (4/5) * vi / vPrimeI;
            const p = vi / Math.pow(ti - u, 4/5);

            // Update results display
            document.getElementById('result-u').textContent = u.toFixed(4);
            document.getElementById('result-p').textContent = p.toFixed(4);

            // Calculate 4/5 Law curve
            const law45Y = tValues.map(t => {
                if (t <= u) return null;
                return p * Math.pow(t - u, 4/5);
            });

            // Update plot
            Plotly.react(plotDiv, {
                data: [
                    {}, // Data points (unchanged)
                    {x: tValues, y: richardsY},
                    {x: tValues, y: law45Y}
                ]
            }, layout);
        }

        // Initial update
        update();
    </script>
</body>
</html>
