    <script>
        // Initialize three.js scene
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xffffff);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.z = 500;

        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas') });
        renderer.setSize(window.innerWidth, window.innerHeight);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        // Main group for all segments
        const pineConeGroup = new THREE.Group();
        scene.add(pineConeGroup);

        // Redraw function
        function redraw() {
            // Clear existing meshes
            while (pineConeGroup.children.length) {
                pineConeGroup.remove(pineConeGroup.children[0]);
            }

            // Get current values
            const basePolygon = parseInt(document.querySelector('input[name="base-polygon"]:checked').value);
            const units = parseInt(document.getElementById('slider-units').value);
            const angleDeg = parseFloat(document.getElementById('slider-angle').value);
            const conical = parseFloat(document.getElementById('slider-conical').value);
            const rotateX = parseFloat(document.getElementById('slider-rotate-x').value);
            const rotateZ = parseFloat(document.getElementById('slider-rotate-z').value);

            // Convert angle to radians
            const angularAdvanceRad = angleDeg * (Math.PI / 180);

            // Create segments
            for (let i = 0; i < units; i++) {
                // Calculate position and scale
                const angle = i * angularAdvanceRad;
                const radius = 4 * Math.sqrt(i);
                const scale = 0.5 * Math.log(i + 2);
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = conical * radius;

                // Create polygon geometry
                const shape = new THREE.Shape();
                const points = [];
                for (let j = 0; j < basePolygon; j++) {
                    const theta = (j / basePolygon) * 2 * Math.PI;
                    points.push(new THREE.Vector2(
                        Math.cos(theta) * 10,
                        Math.sin(theta) * 10
                    ));
                }
                shape.moveTo(points[0].x, points[0].y);
                for (let j = 1; j < points.length; j++) {
                    shape.lineTo(points[j].x, points[j].y);
                }
                shape.closePath();

                const geometry = new THREE.ShapeGeometry(shape);
                const material = new THREE.MeshStandardMaterial({
                    color: new THREE.Color().setHSL((angle / (2 * Math.PI)) % 1.0, 0.8, 0.6)
                });

                const mesh = new THREE.Mesh(geometry, material);
                mesh.position.set(x, y, z);
                mesh.scale.set(scale, scale, scale);
                mesh.rotation.z = angle;

                pineConeGroup.add(mesh);
            }

            // Apply rotations
            pineConeGroup.rotation.x = THREE.MathUtils.degToRad(rotateX);
            pineConeGroup.rotation.z = THREE.MathUtils.degToRad(rotateZ);

            // Render
            renderer.render(scene, camera);
        }

        // Event listeners for controls
        document.querySelectorAll('input[name="base-polygon"]').forEach(radio => {
            radio.addEventListener('change', redraw);
        });

        const sliders = [
            'slider-units', 'slider-angle', 'slider-conical',
            'slider-rotate-x', 'slider-rotate-z'
        ];

        sliders.forEach(id => {
            const slider = document.getElementById(id);
            slider.addEventListener('input', redraw);

            const button = document.getElementById(`btn-${id.split('-')[1]}-plus`);
            button.addEventListener('click', () => {
                slider.value = Math.min(
                    parseFloat(slider.max),
                    parseFloat(slider.value) + parseFloat(slider.step)
                );
                redraw();
            });
        });

        // Initial render
        redraw();

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
            redraw();
        });
    </script>
</body>
</html>
