        <button id="btn-bcc">BCC</button>
        <button id="btn-fcc">FCC</button>
      </div>
      <div id="miller-controls">
        <label>Miller indices</label>
        <label for="input-h">h</label>
        <input type="number" id="input-h" min="-10" max="10" step="1" value="1">
        <label for="input-k">k</label>
        <input type="number" id="input-k" min="-10" max="10" step="1" value="1">
        <label for="input-l">l</label>
        <input type="number" id="input-l" min="-10" max="10" step="1" value="1">
      </div>
      <div id="opacity-controls">
        <label for="slider-opacity">plane opacity</label>
        <input type="range" id="slider-opacity" min="0" max="1" step="0.01" value="0.8">
      </div>
    </div>
    <div id="canvas-container"></div>
  </div>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r150/three.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r150/examples/js/controls/OrbitControls.min.js"></script>
  <script>
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xeeeeee);

    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 100);
    camera.position.set(2, 2, 2);

    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.getElementById('canvas-container').appendChild(renderer.domElement);

    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(5, 5, 5).normalize();
    scene.add(directionalLight);

    const atomsGroup = new THREE.Group();
    const planesGroup = new THREE.Group();
    scene.add(atomsGroup);
    scene.add(planesGroup);

    const cubeGeometry = new THREE.BoxGeometry(1, 1, 1);
    const edges = new THREE.EdgesGeometry(cubeGeometry);
    const line = new THREE.LineSegments(edges, new THREE.LineBasicMaterial({ color: 0x000000 }));
    scene.add(line);

    const axes = new THREE.AxesHelper(0.6);
    scene.add(axes);

    let currentLattice = 'sc';
    document.getElementById('input-h').value = 1;
    document.getElementById('input-k').value = 1;
    document.getElementById('input-l').value = 1;
    document.getElementById('slider-opacity').value = 0.8;

    document.getElementById('btn-sc').addEventListener('click', () => {
      currentLattice = 'sc';
      updateActiveButton();
      updateScene();
    });

    document.getElementById('btn-bcc').addEventListener('click', () => {
      currentLattice = 'bcc';
      updateActiveButton();
      updateScene();
    });

    document.getElementById('btn-fcc').addEventListener('click', () => {
      currentLattice = 'fcc';
      updateActiveButton();
      updateScene();
    });

    function updateActiveButton() {
      document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
      document.getElementById(`btn-${currentLattice}`).classList.add('active');
    }

    document.getElementById('input-h').addEventListener('input', updateScene);
    document.getElementById('input-k').addEventListener('input', updateScene);
    document.getElementById('input-l').addEventListener('input', updateScene);

    document.getElementById('slider-opacity').addEventListener('input', (e) => {
      const opacity = parseFloat(e.target.value);
      planesGroup.children.forEach(mesh => {
        mesh.material.opacity = opacity;
      });
    });

    updateScene();

    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }
    animate();

    function updateScene() {
      atomsGroup.clear();
      planesGroup.clear();

      const h = parseInt(document.getElementById('input-h').value);
      const k = parseInt(document.getElementById('input-k').value);
      const l = parseInt(document.getElementById('input-l').value);

      drawAtoms(currentLattice);

      if (h === 0 && k === 0 && l === 0) return;

      const gcd = computeGCD(h, k, l);
      const hPrime = h / gcd;
      const kPrime = k / gcd;
      const lPrime = l / gcd;

      const cubeVertices = [
        new THREE.Vector3(-0.5, -0.5, -0.5),
        new THREE.Vector3(0.5, -0.5, -0.5),
        new THREE.Vector3(0.5, 0.5, -0.5),
        new THREE.Vector3(-0.5, 0.5, -0.5),
        new THREE.Vector3(-0.5, -0.5, 0.5),
        new THREE.Vector3(0.5, -0.5, 0.5),
        new THREE.Vector3(0.5, 0.5, 0.5),
        new THREE.Vector3(-0.5, 0.5, 0.5),
      ];

      let min = Infinity, max = -Infinity;
      cubeVertices.forEach(v => {
        const val = hPrime * v.x + kPrime * v.y + lPrime * v.z;
        if (val < min) min = val;
        if (val > max) max = val;
      });

      const nMin = Math.floor(min);
      const nMax = Math.ceil(max);

      for (let n = nMin; n <= nMax; n++) {
        const points = findIntersectionPoints(hPrime, kPrime, lPrime, n);
        if (points.length >= 3) {
          const normal = new THREE.Vector3(hPrime, kPrime, lPrime).normalize();
          const sortedPoints = sortPointsAngularly(points, normal);
          const mesh = createPlaneMesh(sortedPoints, 0x0000ff, parseFloat(document.getElementById('slider-opacity').value));
          planesGroup.add(mesh);
        }
      }
    }

    function computeGCD(a, b, c) {
      const gcd2 = (x, y) => (!y) ? Math.abs(x) : gcd2(y, x % y);
      return gcd2(gcd2(a, b), c);
    }

    function findIntersectionPoints(h, k, l, n) {
      const cubeEdges = [
        [0, 1], [1, 2], [2, 3], [3, 0],
        [4, 5], [5, 6], [6, 7], [7, 4],
        [0, 4], [1, 5], [2, 6], [3, 7]
      ];

      const cubeVertices = [
        new THREE.Vector3(-0.5, -0.5, -0.5),
        new THREE.Vector3(0.5, -0.5, -0.5),
        new THREE.Vector3(0.5, 0.5, -0.5),
        new THREE.Vector3(-0.5, 0.5, -0.5),
        new THREE.Vector3(-0.5, -0.5, 0.5),
        new THREE.Vector3(0.5, -0.5, 0.5),
        new THREE.Vector3(0.5, 0.5, 0.5),
        new THREE.Vector3(-0.5, 0.5, 0.5),
      ];

      const points = [];

      cubeEdges.forEach(edge => {
        const A = cubeVertices[edge[0]];
        const B = cubeVertices[edge[1]];

        const denominator = h * (B.x - A.x) + k * (B.y - A.y) + l * (B.z - A.z);
        if (denominator === 0) return;

        const numerator = n - (h * A.x + k * A.y + l * A.z);
        const t = numerator / denominator;

        if (t >= 0 && t <= 1) {
          const x = A.x + t * (B.x - A.x);
          const y = A.y + t * (B.y - A.y);
          const z = A.z + t * (B.z - A.z);
          points.push(new THREE.Vector3(x, y, z));
        }
      });

      return points;
    }

    function sortPointsAngularly(points, normal) {
      if (points.length < 3) return points;

      const centroid = new THREE.Vector3();
      points.forEach(p => centroid.add(p));
      centroid.divideScalar(points.length);

      const up = new THREE.Vector3(0, 1, 0);
      let tangent = new THREE.Vector3().crossVectors(normal, up);
      if (tangent.length() < 0.001) {
        tangent.crossVectors(normal, new THREE.Vector3(1, 0, 0));
      }
      tangent.normalize();
      const bitangent = new THREE.Vector3().crossVectors(normal, tangent).normalize();

      points.sort((a, b) => {
        const va = a.clone().sub(centroid);
        const vb = b.clone().sub(centroid);

        const angleA = Math.atan2(va.dot(bitangent), va.dot(tangent));
        const angleB = Math.atan2(vb.dot(bitangent), vb.dot(tangent));

        return angleA - angleB;
      });

      return points;
    }

    function createPlaneMesh(points, color, opacity) {
      const geometry = new THREE.BufferGeometry();
      const vertices = [];
      for (const p of points) {
        vertices.push(p.x, p.y, p.z);
      }
      geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));

      const indices = [];
      for (let i = 1; i < points.length - 1; i++) {
        indices.push(0, i, i + 1);
      }
      geometry.setIndex(indices);

      const material = new THREE.MeshBasicMaterial({
        color: color,
        transparent: true,
        opacity: opacity,
        side: THREE.DoubleSide
      });

      return new THREE.Mesh(geometry, material);
    }

    function drawAtoms(latticeType) {
      const atomGeometry = new THREE.SphereGeometry(0.05, 32, 32);
      const atomMaterial = new THREE.MeshPhongMaterial({ color: 0x00ff00, specular: 0x11ee11, shininess: 30 });

      let positions = [];

      switch (latticeType) {
        case 'sc':
          positions = [
            [-0.5, -0.5, -0.5],
            [0.5, -0.5, -0.5],
            [0.5, 0.5, -0.5],
            [-0.5, 0.5, -0.5],
            [-0.5, -0.5, 0.5],
            [0.5, -0.5, 0.5],
            [0.5, 0.5, 0.5],
            [-0.5, 0.5, 0.5]
          ];
          break;
        case 'bcc':
          positions = [
            [-0.5, -0.5, -0.5],
            [0.5, -0.5, -0.5],
            [0.5, 0.5, -0.5],
            [-0.5, 0.5, -0.5],
            [-0.5, -0.5, 0.5],
            [0.5, -0.5, 0.5],
            [0.5, 0.5, 0.5],
            [-0.5, 0.5, 0.5],
            [0, 0, 0]
          ];
          break;
        case 'fcc':
          positions = [
            [-0.5, -0.5, -0.5],
            [0.5, -0.5, -0.5],
            [0.5, 0.5, -0.5],
            [-0.5, 0.5, -0.5],
            [-0.5, -0.5, 0.5],
            [0.5, -0.5, 0.5],
            [0.5, 0.5, 0.5],
            [-0.5, 0.5, 0.5],
            [0.5, 0, 0],
            [-0.5, 0, 0],
            [0, 0.5, 0],
            [0, -0.5, 0],
            [0, 0, 0.5],
            [0, 0, -0.5]
          ];
          break;
      }

      positions.forEach(pos => {
        const atom = new THREE.Mesh(atomGeometry, atomMaterial);
        atom.position.set(pos[0], pos[1], pos[2]);
        atomsGroup.add(atom);
      });
    }
  </script>
</body>
</html>
