        const sphereMaterial = new THREE.MeshLambertMaterial({ color: 0xFFFFE0, transparent: true, opacity: 0.6 });
        const mainSphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(mainSphere);

        // Point group for rotation
        const pointGroup = new THREE.Group();
        scene.add(pointGroup);

        // Orbit controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        camera.position.z = 3;
        controls.target.set(0, 0, 0);
        controls.update();

        // 2D Canvas Setup
        const canvas2d = document.getElementById('canvas-2d');
        const ctx = canvas2d.getContext('2d');
        canvas2d.width = 800;
        canvas2d.height = 600;

        // State management
        let currentPoints = [];
        let pointMeshes = [];
        let is3DView = true;

        // Initialize with default value
        updatePoints(20);

        // Event Listeners
        document.getElementById('slider-points').addEventListener('input', (e) => {
            const value = parseInt(e.target.value);
            document.getElementById('label-points').textContent = `+ ${value}`;
            updatePoints(value);
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            is3DView = true;
            document.getElementById('btn-3d').classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'block';
            document.getElementById('div-2d-plot').style.display = 'none';
            animate3D();
        });

        document.getElementById('btn-2d').addEventListener('click', () => {
            is3DView = false;
            document.getElementById('btn-2d').classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'none';
            document.getElementById('div-2d-plot').style.display = 'block';
            draw2DPlot();
        });

        // Functions
        function updatePoints(count) {
            if (THOMSON_POINTS[count]) {
                currentPoints = THOMSON_POINTS[count];
            } else {
                currentPoints = generateRandomPoints(count);
            }
            if (is3DView) {
                update3DPoints();
            } else {
                draw2DPlot();
            }
        }

        function generateRandomPoints(count) {
            const points = [];
            for (let i = 0; i < count; i++) {
                const theta = Math.random() * Math.PI * 2;
                const phi = Math.acos(Math.random() * 2 - 1);
                const x = Math.sin(phi) * Math.cos(theta);
                const y = Math.sin(phi) * Math.sin(theta);
                const z = Math.cos(phi);
                points.push([x, y, z]);
            }
            return points;
        }

        function update3DPoints() {
            // Remove existing points
            pointMeshes.forEach(pair => {
                pair.forEach(mesh => scene.remove(mesh));
            });
            pointMeshes = [];

            // Add new points
            currentPoints.forEach(coord => {
                const [x, y, z] = coord;

                // Solid sphere
                const solidGeo = new THREE.SphereGeometry(0.025, 16, 16);
                const solidMat = new THREE.MeshBasicMaterial({ color: 0x0000ff });
                const solid = new THREE.Mesh(solidGeo, solidMat);
                solid.position.set(x, y, z);

                // Hollow ring
                const ringGeo = new THREE.RingGeometry(0.02, 0.025, 32);
                const ringMat = new THREE.MeshBasicMaterial({ 
                    color: 0x0000ff, 
                    side: THREE.DoubleSide 
                });
                const ring = new THREE.Mesh(ringGeo, ringMat);
                ring.position.set(x, y, z);

                pointGroup.add(solid);
                pointGroup.add(ring);
                pointMeshes.push([solid, ring]);
            });
        }

        function animate3D() {
            requestAnimationFrame(animate3D);

            // Update point visibility based on camera position
            pointMeshes.forEach(([solid, ring]) => {
                solid.getWorldPosition(solid.position);
                const z = solid.position.z;

                if (z > 0) {
                    solid.visible = true;
                    ring.visible = false;
                } else {
                    solid.visible = false;
                    ring.visible = true;
                    ring.lookAt(camera.position);
                }
            });

            controls.update();
            renderer.render(scene, camera);
        }

        function draw2DPlot() {
            ctx.clearRect(0, 0, canvas2d.width, canvas2d.height);

            // Convert 3D to 2D
            const points2D = currentPoints.map(([x, y, z]) => {
                const theta = Math.atan2(y, x);
                const phi = Math.acos(z);
                return [
                    (theta + Math.PI) / (2 * Math.PI) * canvas2d.width,
                    phi / Math.PI * canvas2d.height
                ];
            });

            // Draw points
            ctx.fillStyle = '#0000ff';
            points2D.forEach([px, py]) => {
                ctx.beginPath();
                ctx.arc(px, py, 3, 0, Math.PI * 2);
                ctx.fill();
            });

            // Calculate distances
            let minDist = Infinity;
            const maxDist = 2.0;
            for (let i = 0; i < currentPoints.length; i++) {
                for (let j = i + 1; j < currentPoints.length; j++) {
                    const dx = currentPoints[i][0] - currentPoints[j][0];
                    const dy = currentPoints[i][1] - currentPoints[j][1];
                    const dz = currentPoints[i][2] - currentPoints[j][2];
                    const dist = Math.sqrt(dx*dx + dy*dy + dz*dz);
                    if (dist < minDist) minDist = dist;
                }
            }

            // Update text
            const textDiv = document.getElementById('div-2d-text');
            textDiv.innerHTML = `
                minimum distance between points = ${minDist.toFixed(5)}<br>
                maximum distance between points = ${maxDist}
            `;
        }

        // Start animation loop
        animate3D();
    </script>
</body>
</html>
