    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        // Global variables to hold slider values
        let angle = 23.45;
        let longitude = 102.95;
        let eccentricity = 0.016722;

        // Update value displays on load
        document.getElementById('value-angle').textContent = angle.toFixed(2);
        document.getElementById('value-longitude').textContent = longitude.toFixed(2);
        document.getElementById('value-eccentricity').textContent = eccentricity.toFixed(6);

        // Add event listeners to sliders
        document.getElementById('slider-angle').addEventListener('input', (e) => {
            angle = parseFloat(e.target.value);
            document.getElementById('value-angle').textContent = angle.toFixed(2);
            redrawVisualizations();
        });

        document.getElementById('slider-longitude').addEventListener('input', (e) => {
            longitude = parseFloat(e.target.value);
            document.getElementById('value-longitude').textContent = longitude.toFixed(2);
            redrawVisualizations();
        });

        document.getElementById('slider-eccentricity').addEventListener('input', (e) => {
            eccentricity = parseFloat(e.target.value);
            document.getElementById('value-eccentricity').textContent = eccentricity.toFixed(6);
            redrawVisualizations();
        });

        // Redraw function for both visualizations
        function redrawVisualizations() {
            orbitSketch.draw();
            equationPlot.draw();
        }

        // Orbit Sketch with p5.js
        const orbitSketch = new p5(sketch => {
            let canvas;

            sketch.setup = () => {
                canvas = sketch.createCanvas(800, 400);
                canvas.parent('orbit-canvas');
                sketch.noLoop();
            };

            sketch.draw = () => {
                sketch.background(240);
                sketch.translate(sketch.width/2, sketch.height/2);
                sketch.rotate(sketch.radians(longitude));

                const a = sketch.width * 0.4;
                const b = a * Math.sqrt(1 - eccentricity**2);

                sketch.noFill();
                sketch.stroke(160, 82, 45);
                sketch.strokeWeight(2);
                sketch.ellipse(0, 0, a*2, b*2);
            };
        });

        // Equation of Time Plot with p5.js
        const equationPlot = new p5(sketch => {
            let canvas;

            sketch.setup = () => {
                canvas = sketch.createCanvas(800, 400);
                canvas.parent('plot-canvas');
                sketch.noLoop();
            };

            sketch.draw = () => {
                sketch.background(255);

                // Calculate EOT data
                const data = calculateEOT(angle, longitude, eccentricity);
                if (data.length === 0) return;

                // Determine axis ranges
                const xMin = 0, xMax = 2 * Math.PI;
                const yMin = Math.min(...data.map(p => p.y));
                const yMax = Math.max(...data.map(p => p.y));
                const padding = 40;

                // Scale functions
                const xScale = (x) => sketch.map(x, xMin, xMax, padding, sketch.width - padding);
                const yScale = (y) => sketch.map(y, yMin, yMax, sketch.height - padding, padding);

                // Draw axes
                sketch.stroke(0);
                sketch.strokeWeight(1);
                sketch.line(padding, padding, padding, sketch.height - padding);
                sketch.line(padding, sketch.height - padding, sketch.width - padding, sketch.height - padding);

                // Draw grid
                sketch.stroke(220);
                for (let i = 1; i <= 5; i++) {
                    const x = xScale(i);
                    sketch.line(x, padding, x, sketch.height - padding);
                }
                for (let y = Math.ceil(yMin); y <= Math.floor(yMax); y++) {
                    const yPos = yScale(y);
                    sketch.line(padding, yPos, sketch.width - padding, yPos);
                }

                // Draw ticks and labels
                sketch.stroke(0);
                sketch.textSize(12);
                sketch.textAlign(sketch.CENTER, sketch.TOP);
                for (let i = 1; i <= 6; i++) {
                    const x = xScale(i);
                    sketch.line(x, sketch.height - padding, x, sketch.height - padding + 5);
                    sketch.text(i, x, sketch.height - padding + 8);
                }

                sketch.textAlign(sketch.RIGHT, sketch.CENTER);
                sketch.text('minutes', padding - 10, (padding + sketch.height - padding)/2);

                // Draw curve
                sketch.stroke(255, 140, 0);
                sketch.strokeWeight(2);
                sketch.noFill();
                sketch.beginShape();
                data.forEach(p => {
                    sketch.vertex(xScale(p.x), yScale(p.y));
                });
                sketch.endShape();
            };
        });

        // Equation of Time calculation
        function calculateEOT(epsilonDeg, omegaDeg, e) {
            const epsilon = sketch.radians(epsilonDeg);
            const omega = sketch.radians(omegaDeg);
            const M_values = [];
            const step = 0.05;

            for (let M = 0; M <= 2 * Math.PI + step; M += step) {
                // Solve Kepler's equation
                let E = M;
                for (let iter = 0; iter < 10; iter++) {
                    E = E - (E - e * Math.sin(E) - M) / (1 - e * Math.cos(E));
                }

                // Calculate true anomaly
                const nu = 2 * Math.atan2(
                    Math.sqrt(1 + e) * Math.sin(E/2),
                    Math.sqrt(1 - e) * Math.cos(E/2)
                );

                // True longitude
                const lambda = nu + omega;

                // Right ascension
                const alpha = Math.atan2(Math.cos(epsilon) * Math.sin(lambda), Math.cos(lambda));

                // Equation of time in radians
                let EOT_rad = M - alpha;

                // Phase unwrapping
                if (M > 0) {
                    const prev = M_values[M_values.length - 1].y * Math.PI / 720;
                    if (EOT_rad - prev > Math.PI) EOT_rad -= 2 * Math.PI;
                    else if (prev - EOT_rad > Math.PI) EOT_rad += 2 * Math.PI;
                }

                // Convert to minutes
                const EOT_min = EOT_rad * (720 / Math.PI);
                M_values.push({x: M, y: EOT_min});
            }
            return M_values;
        }
    </script>
</body>
</html>
