    <script>
        let currentParams = {
            c: 4,
            h_C: 1.9,
            delta: 0.5,
            step: 5,
            verification: true,
            plotRange: 1
        };

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            noLoop();
            setupEventListeners();
            redrawScene();
        }

        function draw() {}

        function setupEventListeners() {
            const elements = [
                { id: 'slider-c', param: 'c', span: 'c-value' },
                { id: 'slider-h_C', param: 'h_C', span: 'h_C-value' },
                { id: 'slider-delta', param: 'delta', span: 'delta-value' },
                { id: 'slider-plot-range', param: 'plotRange', span: 'plot-range-value' }
            ];

            elements.forEach(el => {
                const input = document.getElementById(el.id);
                const span = document.getElementById(el.span);
                input.addEventListener('input', () => {
                    span.textContent = input.value;
                    currentParams[el.param] = parseFloat(input.value);
                    redrawScene();
                });
            });

            document.querySelectorAll('input[name="steps"]').forEach(radio => {
                radio.addEventListener('change', () => {
                    currentParams.step = parseInt(radio.value);
                    redrawScene();
                });
            });

            document.getElementById('check-verification').addEventListener('change', (e) => {
                currentParams.verification = e.target.checked;
                redrawScene();
            });
        }

        function redrawScene() {
            clear();
            background(224);
            const c = currentParams.c;
            const h_C = currentParams.h_C;
            const delta = currentParams.delta;
            const step = currentParams.step;
            const verification = currentParams.verification;
            const plotRange = currentParams.plotRange;

            const baseScale = 50;
            const scale = baseScale * plotRange;

            translate(width / 2, height * 0.6);
            scale(1, -1);
            scale(scale);
            translate(-width / (2 * scale), -height * 0.6 / scale);

            stroke(0);
            strokeWeight(1 / scale);
            noFill();

            // Calculate points
            const F = { x: 0, y: 0 };
            const C = { x: 0, y: h_C };
            const D = { x: c / 2, y: 0 };
            const K = { x: c, y: 0 };

            // Solve for M
            const a = c;
            const b = -2 * h_C;
            const d = h_C ** 2 - c ** 2 / 4;
            const A = 1;
            const B = -3 * c / 2;
            const C_eq = c ** 2 / 2;
            const tan_delta = Math.tan(delta);
            const denom = tan_delta * (c / 2);
            const A2 = (c ** 2) / (4 * denom ** 2) + 1;
            const B2 = (2 * c * d) / (2 * denom ** 2) - 3 * c / 2;
            const C2 = (d ** 2) / denom ** 2 + c ** 2 / 2;
            const discriminant = B2 ** 2 - 4 * A2 * C2;
            let y_M = 0, x_M = 0;
            if (discriminant >= 0) {
                const y1 = (-B2 - Math.sqrt(discriminant)) / (2 * A2);
                const y2 = (-B2 + Math.sqrt(discriminant)) / (2 * A2);
                y_M = Math.min(y1, y2);
                x_M = (2 * h_C * y_M - h_C ** 2 + c ** 2 / 4) / c;
            }

            const M = { x: x_M, y: y_M };
            const R = Math.sqrt((M.x - C.x) ** 2 + (M.y - C.y) ** 2);
            const x_B = 2 * x_M - c / 2;
            const B = { x: x_B, y: 0 };
            const A = { x: x_B - c, y: 0 };
            const E = verification ? { x: c - x_B, y: 0 } : null;

            // Draw elements
            const drawPoint = (p, label) => {
                ellipse(p.x, p.y, 0.05, 0.05);
                push();
                resetMatrix();
                translate(width / 2 + p.x * scale, height * 0.6 - p.y * scale);
                textSize(12);
                text(label, 10, -5);
                pop();
            };

            // Step 1
            if (step >= 1) {
                // Line λ
                line(-100, 0, 100, 0);
                // Points F, C, D
                drawPoint(F, 'F');
                drawPoint(C, 'C');
                drawPoint(D, 'D');
                // Line FC
                line(F.x, F.y, C.x, C.y);
            }

            // Step 2
            if (step >= 2) {
                drawPoint(K, 'K');
                stroke(0, 0, 0, 128);
                line(C.x, C.y, D.x, D.y);
                line(D.x, D.y, K.x, K.y);
                stroke(0);
                drawPoint(M, 'M');
                stroke(0, 0, 0, 128);
                line(M.x, M.y, C.x, C.y);
                line(M.x, M.y, D.x, D.y);
                line(M.x, M.y, K.x, K.y);
                stroke(0);
                // Arc κ
                const startAngle = Math.atan2(D.y - M.y, D.x - M.x);
                const endAngle = Math.atan2(K.y - M.y, K.x - M.x);
                arc(M.x, M.y, 2 * R, 2 * R, startAngle, endAngle);
            }

            // Step 3
            if (step >= 3) {
                drawPoint(B, 'B');
            }

            // Step 4
            if (step >= 4) {
                drawPoint(A, 'A');
            }

            // Step 5
            if (step >= 5) {
                strokeWeight(2 / scale);
                line(A.x, A.y, B.x, B.y);
                line(B.x, B.y, C.x, C.y);
                line(C.x, C.y, A.x, A.y);
                strokeWeight(1 / scale);
            }

            // Verification
            if (verification) {
                if (E) {
                    drawPoint(E, 'E');
                    stroke(0, 0, 0, 128);
                    line(C.x, C.y, E.x, E.y);
                    line(K.x, K.y, B.x, B.y);
                    stroke(0);
                }
            }
        }
    </script>
</body>
</html>
