      <div id="plot-intensity"></div>
      <div id="plot-population"></div>
      <div id="plot-coherence"></div>
    </div>
  </div>
  <script>
    const t_values = Array.from({length: 301}, (_, i) => i - 50);

    // Precompute red curves (z=0)
    const I_red = t_values.map(t => {
      if (t < -15 || t > 170) return 0;
      return t > 15 ? 1 : 0.5 * (1 + Math.tanh(t/4));
    });

    const P_red = t_values.map(t => {
      if (t < -15) return 0;
      return 4.5 * (1 - Math.exp(-(t + 15)/50));
    });

    const C_red = t_values.map(t => {
      if (t < -15) return 0;
      return 2.2 * (1 - Math.exp(-(t + 15)/50));
    });

    // Initialize plots
    function createPlot(divId, data, layout) {
      Plotly.newPlot(divId, data, layout);
    }

    // Intensity Plot
    createPlot('plot-intensity', [
      { x: t_values, y: I_red, name: "z = 0 meters", mode: 'lines', line: { color: 'red', width: 2 } },
      { x: t_values, y: I_red, name: "z = 0. meters", mode: 'lines', line: { color: 'blue', width: 2, dash: 'dash' } }
    ], {
      title: 'Intensity',
      xaxis: { title: 'time (ns)', range: [-50, 250], zeroline: true },
      yaxis: { title: 'intensity (arb)', range: [-0.1, 1.1], zeroline: true },
      margin: { l: 80, r: 20, t: 40, b: 40 },
      showlegend: true,
      legend: { x: 0.7, y: 0.9 },
      shapes: [
        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } },
        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } }
      ],
      annotations: [
        { x: -45, y: 0.9, xref: 'x', yref: 'y', text: '(a)', showarrow: false },
        { x: 0, y: 0.7, xref: 'x', yref: 'y', text: 'pulse<br>midpoint', showarrow: false, xanchor: 'left', yanchor: 'bottom' }
      ]
    });

    // Population Plot
    createPlot('plot-population', [
      { x: t_values, y: P_red, name: "z = 0 meters", mode: 'lines', line: { color: 'red', width: 2 } },
      { x: t_values, y: P_red, name: "z = 0. meters", mode: 'lines', line: { color: 'blue', width: 2, dash: 'dash' } }
    ], {
      title: 'Excited-state Population',
      xaxis: { title: 'time (ns)', range: [-50, 250], zeroline: true },
      yaxis: { title: 'excited-state population × 10<sup>4</sup>', range: [-0.5, 5.5], zeroline: true },
      margin: { l: 80, r: 20, t: 40, b: 40 },
      showlegend: true,
      legend: { x: 0.7, y: 0.9 },
      shapes: [
        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } },
        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } }
      ],
      annotations: [
        { x: -45, y: 0.9, xref: 'x', yref: 'y', text: '(b)', showarrow: false },
        { x: 0, y: 0.7, xref: 'x', yref: 'y', text: 'pulse<br>midpoint', showarrow: false, xanchor: 'left', yanchor: 'bottom' }
      ]
    });

    // Coherence Plot
    createPlot('plot-coherence', [
      { x: t_values, y: C_red, name: "z = 0 meters", mode: 'lines', line: { color: 'red', width: 2 } },
      { x: t_values, y: C_red, name: "z = 0. meters", mode: 'lines', line: { color: 'blue', width: 2, dash: 'dash' } }
    ], {
      title: 'Coherence',
      xaxis: { title: 'time (ns)', range: [-50, 250], zeroline: true },
      yaxis: { title: 'coherence × 10<sup>2</sup>', range: [-0.2, 2.4], zeroline: true },
      margin: { l: 80, r: 20, t: 40, b: 40 },
      showlegend: true,
      legend: { x: 0.7, y: 0.9 },
      shapes: [
        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } },
        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } }
      ],
      annotations: [
        { x: -45, y: 0.9, xref: 'x', yref: 'y', text: '(c)', showarrow: false },
        { x: 0, y: 0.7, xref: 'x', yref: 'y', text: 'pulse<br>midpoint', showarrow: false, xanchor: 'left', yanchor: 'bottom' }
      ]
    });

    // Slider interaction
    const slider = document.getElementById('slider-z');
    const labelZ = document.getElementById('label-z');

    function formatZ(z) {
      return Number.isInteger(z) ? `${z}.` : z.toFixed(5);
    }

    function updatePlots(z) {
      labelZ.textContent = formatZ(z);
      const t_delay = z / 0.3;

      // Generate blue data
      const I_blue = t_values.map(t => {
        const t_prime = t - t_delay;
        let base = 0;
        if (t_prime >= -15 && t_prime <= 170) {
          base = t_prime > 15 ? 1 : 0.5 * (1 + Math.tanh(t_prime / 4));
        }
        const envelope = t_prime > 0 ? Math.exp(-0.035 * z * t_prime / 32) : 1;
        let value = base * envelope;
        if (t > 170 + t_delay) value = 0;
        return value;
      });

      const scaling_factor_p = 1 - 0.45 * (z / 32);
      const P_blue = t_values.map(t => {
        const t_prime = t - t_delay;
        let value = t_prime >= -15 ? 4.5 * (1 - Math.exp(-(t_prime + 15)/50)) : 0;
        value *= scaling_factor_p;
        if (t > 170 + t_delay) value = 0;
        return value;
      });

      const scaling_factor_c = 1 - 0.45 * (z / 32);
      const C_blue = t_values.map(t => {
        const t_prime = t - t_delay;
        let value = t_prime >= -15 ? 2.2 * (1 - Math.exp(-(t_prime + 15)/50)) : 0;
        value *= scaling_factor_c;
        if (t > 170 + t_delay) value = 0;
        return value;
      });

      // Update traces
      const updateTrace = (plotDiv, data) => {
        Plotly.restyle(plotDiv, { y: [data] }, [1]);
      };
      updateTrace('plot-intensity', I_blue);
      updateTrace('plot-population', P_blue);
      updateTrace('plot-coherence', C_blue);

      // Update vertical lines
      const updateLine = (plotDiv, t) => {
        Plotly.relayout(plotDiv, {
          'shapes[1].x0': t,
          'shapes[1].x1': t
        });
      };
      updateLine('plot-intensity', t_delay);
      updateLine('plot-population', t_delay);
      updateLine('plot-coherence', t_delay);

      // Update legend
      const updateLegend = (plotDiv, z) => {
        Plotly.restyle(plotDiv, { name: `z = ${formatZ(z)} meters` }, [1]);
      };
      updateLegend('plot-intensity', z);
      updateLegend('plot-population', z);
      updateLegend('plot-coherence', z);
    }

    slider.addEventListener('input', () => updatePlots(parseFloat(slider.value)));
    updatePlots(0); // Initialize with z=0
  </script>
</body>
</html>
