            width: 100%;
            height: 500px;
            background-color: #ffffff;
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <div>
                <label for="slider-ua">heat transfer coefficient (cal/(dm² K s))</label>
                <input type="range" id="slider-ua">
                <span id="ua-value"></span>
            </div>
            <div>
                <label for="slider-kr0">reverse reaction pre-exponential factor (1/s)</label>
                <input type="range" id="slider-kr0">
                <span id="kr0-value"></span>
            </div>
            <div>
                <label for="slider-tf">feed temperature (K)</label>
                <input type="range" id="slider-tf">
                <span id="tf-value"></span>
            </div>
            <div>
                <label for="slider-tau">residence time (s)</label>
                <input type="range" id="slider-tau">
                <span id="tau-value"></span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        window.onload = function() {
            const sliderUa = document.getElementById('slider-ua');
            const sliderKr0 = document.getElementById('slider-kr0');
            const sliderTf = document.getElementById('slider-tf');
            const sliderTau = document.getElementById('slider-tau');

            // Set slider attributes
            sliderUa.min = 0;
            sliderUa.max = 20;
            sliderUa.step = 0.1;
            sliderUa.value = 0;

            sliderKr0.min = 0;
            sliderKr0.max = 10;
            sliderKr0.step = 0.1;
            sliderKr0.value = 0;

            sliderTf.min = 250;
            sliderTf.max = 350;
            sliderTf.step = 1;
            sliderTf.value = 265;

            sliderTau.min = 100;
            sliderTau.max = 1000;
            sliderTau.step = 10;
            sliderTau.value = 400;

            // Update display function
            function updateDisplays(UA, kr0, Tf, tau) {
                document.getElementById('ua-value').textContent = UA.toFixed(1);
                document.getElementById('kr0-value').textContent = `${kr0.toFixed(1)} x 10¹²`;
                document.getElementById('tf-value').textContent = Tf.toFixed(0);
                document.getElementById('tau-value').textContent = tau.toFixed(0);
            }

            // Initial display
            updateDisplays(0, 0, 265, 400);

            // Event listeners
            sliderUa.addEventListener('input', update);
            sliderKr0.addEventListener('input', update);
            sliderTf.addEventListener('input', update);
            sliderTau.addEventListener('input', update);

            // Constants
            const C_A0 = 10;
            const R = 1.987;
            const E_f = 25000;
            const deltaH_rxn = -200000;
            const E_r = E_f - deltaH_rxn;
            const k_f0 = 1e15;
            const rhoCp = 20000;

            let plotInitialized = false;

            function update() {
                const UA = parseFloat(sliderUa.value);
                const kr0 = parseFloat(sliderKr0.value);
                const Tf = parseFloat(sliderTf.value);
                const tau = parseFloat(sliderTau.value);

                updateDisplays(UA, kr0, Tf, tau);

                // Calculate mass balance
                const T_mb = [];
                const C_B_mb = [];
                for (let T = 250; T <= 400; T++) {
                    T_mb.push(T);
                    const k_f = k_f0 * Math.exp(-E_f / (R * T));
                    const k_r = kr0 * 1e12 * Math.exp(-E_r / (R * T));
                    const denominator = 1 + tau * (k_f + k_r);
                    const C_B = (tau * k_f * C_A0) / denominator;
                    C_B_mb.push(C_B);
                }

                // Calculate energy balance
                const T_eb = [];
                const C_B_eb = [];
                for (let T = 250; T <= 400; T++) {
                    T_eb.push(T);
                    const numerator = (rhoCp + UA * tau) * (T - Tf);
                    const C_B = numerator / (-deltaH_rxn);
                    C_B_eb.push(C_B);
                }

                // Annotation positions
                const T_mb_annotation = 360;
                const k_f_annotation = k_f0 * Math.exp(-E_f / (R * T_mb_annotation));
                const k_r_annotation = kr0 * 1e12 * Math.exp(-E_r / (R * T_mb_annotation));
                const C_B_mb_annotation = (tau * k_f_annotation * C_A0) / (1 + tau * (k_f_annotation + k_r_annotation));

                const T_eb_annotation = 280;
                const C_B_eb_annotation = ((rhoCp + UA * tau) * (T_eb_annotation - Tf)) / (-deltaH_rxn);

                // Plot data and layout
                const data = [
                    {
                        x: T_mb,
                        y: C_B_mb,
                        type: 'scatter',
                        mode: 'lines',
                        name: 'mass balance',
                        line: { color: '#008000' }
                    },
                    {
                        x: T_eb,
                        y: C_B_eb,
                        type: 'scatter',
                        mode: 'lines',
                        name: 'energy balance',
                        line: { color: '#0000FF' }
                    }
                ];

                const layout = {
                    xaxis: {
                        title: 'temperature (K)',
                        range: [250, 400]
                    },
                    yaxis: {
                        title: 'product concentration (mmol/dm³)',
                        range: [0, 12]
                    },
                    annotations: [
                        {
                            x: T_mb_annotation,
                            y: C_B_mb_annotation,
                            text: 'mass balance',
                            showarrow: false,
                            font: { color: '#008000' }
                        },
                        {
                            x: T_eb_annotation,
                            y: C_B_eb_annotation,
                            text: 'energy balance',
                            showarrow: false,
                            font: { color: '#0000FF' }
                        }
                    ],
                    font: { family: 'sans-serif' },
                    plot_bgcolor: '#ffffff',
                    margin: { t: 40, b: 40, l: 40, r: 40 }
                };

                if (plotInitialized) {
                    Plotly.react('plot-container', data, layout);
                } else {
                    Plotly.newPlot('plot-container', data, layout);
                    plotInitialized = true;
                }
            }

            // Initial plot
            update();
        };
    </script>
</body>
</html>
