<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Wankel Rotary Engine: Epitrochoidal Envelopes</title>
  <style>
    body {
      font-family: Arial, sans-serif;
      margin: 0;
      padding: 20px;
      background: #f0f0f0;
    }
    #demo-container {
      max-width: 1000px;
      margin: 0 auto;
      background: white;
      padding: 20px;
      border-radius: 8px;
      box-shadow: 0 0 10px rgba(0,0,0,0.1);
    }
    h1 {
      margin-top: 0;
    }
    #control-panel {
      background: #f8f8f8;
      padding: 15px;
      border-radius: 6px;
      margin: 20px 0;
    }
    #control-panel div {
      margin-bottom: 10px;
    }
    #control-panel label, #control-panel span {
      display: inline-block;
      width: 120px;
    }
    #slider-eccentricity, #slider-rotation {
      width: 200px;
    }
    #canvas-container {
      text-align: center;
    }
  </style>
</head>
<body>
  <div id="demo-container">
    <h1>Wankel Rotary Engine: Epitrochoidal Envelopes</h1>
    <p>This Demonstration gives an animation of an epitrochoid and associated planetary-motion envelope curve. The configuration shown has applications in the internal combustion engines invented by Felix Wankel and popularized by Mazda in RX-7 and RX-8 cars. The "eccentricity ratio" changes the shapes of the curves. The "reference frame" determines what is held stationary in the animation: either the epitrochoid (blue), the envelope curve (purple), or the centers of rotation of both curves. The "inner" envelope is the triangular rotor shape used in place of a piston in a Wankel rotary engine, whereas the "outer" envelope is the continuation of the envelope curve along the opposite extreme of motion.</p>

    <div id="control-panel">
      <div>
        <label for="slider-eccentricity">eccentricity ratio</label>
        <input type="range" id="slider-eccentricity">
        <span id="label-eccentricity"></span>
      </div>
      <div>
        <span>reference frame</span>
        <input type="radio" name="ref-frame" id="radio-epitrochoid" value="epitrochoid" checked>
        <label for="radio-epitrochoid">epitrochoid</label>
        <input type="radio" name="ref-frame" id="radio-envelope" value="envelope">
        <label for="radio-envelope">envelope</label>
        <input type="radio" name="ref-frame" id="radio-fixed-centers" value="fixed centers">
        <label for="radio-fixed-centers">fixed centers</label>
      </div>
      <div>
        <span>envelope to show</span>
        <input type="radio" name="envelope-type" id="radio-inner" value="inner" checked>
        <label for="radio-inner">inner</label>
        <input type="radio" name="envelope-type" id="radio-outer" value="outer">
        <label for="radio-outer">outer</label>
        <input type="radio" name="envelope-type" id="radio-both" value="both">
        <label for="radio-both">both</label>
      </div>
      <div>
        <label for="slider-rotation">rotation angle</label>
        <input type="range" id="slider-rotation">
        <span id="label-rotation"></span>
      </div>
    </div>

    <div id="canvas-container">
      <!-- p5.js canvas will be inserted here -->
    </div>
  </div>

  <!-- CDN Libraries -->
  <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
  <script>
    let k = 0.14;
    let phi = 0;
    let refFrame = 'epitrochoid';
    let envelopeType = 'inner';

    function setup() {
      const canvas = createCanvas(800, 800);
      canvas.parent('canvas-container');

      // Initialize labels
      select('#label-eccentricity').html(k.toFixed(4));
      select('#label-rotation').html(phi.toFixed(4));

      // Set slider attributes
      select('#slider-eccentricity').attribute('min', 0);
      select('#slider-eccentricity').attribute('max', 0.3);
      select('#slider-eccentricity').attribute('step', 0.0001);
      select('#slider-eccentricity').value(k);
      select('#slider-rotation').attribute('min', 0);
      select('#slider-rotation').attribute('max', TWO_PI);
      select('#slider-rotation').attribute('step', 0.0001);
      select('#slider-rotation').value(phi);

      // Event listeners for sliders
      select('#slider-eccentricity').changed(function() {
        k = parseFloat(this.value());
        select('#label-eccentricity').html(k.toFixed(4));
        redraw();
      });

      select('#slider-rotation').changed(function() {
        phi = parseFloat(this.value());
        select('#label-rotation').html(phi.toFixed(4));
        redraw();
      });

      // Event listeners for radio buttons
      select('#radio-epitrochoid').changed(function() {
        if (this.checked()) {
          refFrame = 'epitrochoid';
          redraw();
        }
      });
      select('#radio-envelope').changed(function() {
        if (this.checked()) {
          refFrame = 'envelope';
          redraw();
        }
      });
      select('#radio-fixed-centers').changed(function() {
        if (this.checked()) {
          refFrame = 'fixed centers';
          redraw();
        }
      });
      select('#radio-inner').changed(function() {
        if (this.checked()) {
          envelopeType = 'inner';
          redraw();
        }
      });
      select('#radio-outer').changed(function() {
        if (this.checked()) {
          envelopeType = 'outer';
          redraw();
        }
      });
      select('#radio-both').changed(function() {
        if (this.checked()) {
          envelopeType = 'both';
          redraw();
        }
      });
    }

    function draw() {
      background(255);
      push();
      translate(width/2, height/2);
      drawAxes();
      const scale = 150;
      const phi_shaft = 2 * phi;
      const theta = -phi_shaft / 3;
      const Cx = k * cos(phi_shaft);
      const Cy = k * sin(phi_shaft);

      // Draw blue curve (epitrochoid)
      if (refFrame === 'envelope') {
        push();
        translate(-Cx * scale, -Cy * scale);
        rotate(-theta);
        drawEpitrochoid(k, scale);
        pop();
      } else {
        drawEpitrochoid(k, scale);
      }

      // Draw orange curves (envelope)
      if (refFrame === 'envelope') {
        if (envelopeType === 'inner' || envelopeType === 'both') {
          drawInnerEnvelope(k, scale);
        }
        if (envelopeType === 'outer' || envelopeType === 'both') {
          drawOuterEnvelope(k, scale);
        }
      } else {
        push();
        if (refFrame === 'epitrochoid') {
          translate(Cx * scale, Cy * scale);
          rotate(theta);
        }
        if (envelopeType === 'inner' || envelopeType === 'both') {
          drawInnerEnvelope(k, scale);
        }
        if (envelopeType === 'outer' || envelopeType === 'both') {
          drawOuterEnvelope(k, scale);
        }
        pop();
      }

      pop();
    }

    function drawAxes() {
      const scale = 150;
      stroke(150);
      strokeWeight(1);
      line(-width/2, 0, width/2, 0);
      line(0, -height/2, 0, height/2);

      const tickStep = 0.5;
      const maxAxis = 2.0;
      for (let x = -maxAxis; x <= maxAxis; x += tickStep) {
        if (x === 0) continue;
        const px = x * scale;
        line(px, -5, px, 5);
        textAlign(CENTER, TOP);
        text(x.toFixed(1), px, 5 + 5);
      }
      for (let y = -maxAxis; y <= maxAxis; y += tickStep) {
        if (y === 0) continue;
        const py = y * scale;
        line(-5, py, 5, py);
        textAlign(RIGHT, CENTER);
        text(y.toFixed(1), -5 - 5, py);
      }
    }

    function drawEpitrochoid(k, scale) {
      const R = 1;
      const points = [];
      const res = 200;
      for (let i = 0; i <= res; i++) {
        const t = map(i, 0, res, 0, TWO_PI);
        const x = cos(t) - k * cos(3 * t);
        const y = sin(t) - k * sin(3 * t);
        points.push(createVector(x, y));
      }
      stroke(50, 100, 200);
      strokeWeight(2);
      noFill();
      beginShape();
      for (const p of points) {
        vertex(p.x * scale, p.y * scale);
      }
      endShape();
    }

    function drawInnerEnvelope(k, scale) {
      const R = 1;
      const e = k;
      const points = [];
      const res = 200;
      for (let i = 0; i <= res; i++) {
        const t = map(i, 0, res, 0, TWO_PI);
        const x = (2/3)*R*cos(t) + e*cos(2*t);
        const y = (2/3)*R*sin(t) - e*sin(2*t);
        points.push(createVector(x, y));
      }
      stroke(230, 150, 0);
      strokeWeight(2);
      noFill();
      beginShape();
      for (const p of points) {
        vertex(p.x * scale, p.y * scale);
      }
      endShape();
    }

    function drawOuterEnvelope(k, scale) {
      const R = 1;
      const e = k;
      const points = [];
      const res = 200;
      for (let i = 0; i <= res; i++) {
        const t = map(i, 0, res, 0, TWO_PI);
        const x = (2/3)*R*cos(t) - e*cos(2*t);
        const y = (2/3)*R*sin(t) + e*sin(2*t);
        points.push(createVector(x, y));
      }
      stroke(230, 150, 0);
      strokeWeight(2);
      noFill();
      beginShape();
      for (const p of points) {
        vertex(p.x * scale, p.y * scale);
      }
      endShape();
    }
  </script>
</body>
</html>
