    <title>Emulsion Stability Visualization</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            height: 100vh;
        }
        #container {
            display: flex;
            width: 100%;
        }
        #control-panel {
            width: 25%;
            padding: 20px;
            background: #f8f9fa;
            overflow-y: auto;
        }
        #vis-area {
            flex: 1;
            padding: 20px;
            display: flex;
            flex-direction: column;
        }
        .slider-group {
            margin-bottom: 15px;
        }
        label {
            display: block;
            margin-bottom: 5px;
        }
        input[type="range"] {
            width: 100%;
        }
        #info-display {
            margin-bottom: 20px;
        }
        #plot-container {
            flex: 1;
        }
    </style>
</head>
<body>
    <div id="container">
        <div id="control-panel">
            <h4>Stability of Multilayered Emulsions</h4>
            <div class="slider-group">
                <label for="slider-phi">ϕ</label>
                <input type="range" id="slider-phi" min="0.0001" max="0.05" step="0.0001" value="0.005">
                <span id="phi-value-display">0.005</span>
            </div>
            <hr>
            <div class="slider-group">
                <label for="slider-gamma-sat">Γ<sub>sat</sub> (mg/m²)</label>
                <input type="range" id="slider-gamma-sat" min="0.1" max="10" step="0.1" value="1.5">
                <span id="gamma-sat-value-display">1.5</span>
            </div>
            <div class="slider-group">
                <label for="slider-r">r (nm)</label>
                <input type="range" id="slider-r" min="50" max="1000" step="10" value="300">
                <span id="r-value-display">300</span>
            </div>
            <div class="slider-group">
                <label for="slider-rpe">r<sub>pe</sub> (nm)</label>
                <input type="range" id="slider-rpe" min="5" max="50" step="1" value="20">
                <span id="rpe-value-display">20</span>
            </div>
            <div class="slider-group">
                <label for="slider-mw">M<sub>W</sub> (kDa)</label>
                <input type="range" id="slider-mw" min="10" max="1000" step="1" value="166">
                <span id="mw-value-display">166</span>
            </div>
            <hr>
            <div class="slider-group">
                <label for="slider-phi-axis">ϕ axis max.</label>
                <input type="range" id="slider-phi-axis" min="0.01" max="0.5" step="0.01" value="0.05">
                <span id="phi-axis-value-display">0.05</span>
            </div>
            <div class="slider-group">
                <label for="slider-c-axis">C axis max.</label>
                <input type="range" id="slider-c-axis" min="1.0" max="5.0" step="0.1" value="2.0">
                <span id="c-axis-value-display">2.0</span>
            </div>
        </div>
        <div id="vis-area">
            <div id="info-display">
                <p id="formula-display">
                    <span style="color:blue;">y₁(ϕ)</span>, 
                    <span style="color:red;">y₂(ϕ)</span>, and 
                    <span style="color:green;">y₃(ϕ)</span>
                </p>
                <p id="phimax-display">ϕ<sub>max</sub>: </p>
                <p id="range-display">Stability range: </p>
            </div>
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;
        let phiMax = 0;

        // Get elements
        const sliders = {
            phi: document.getElementById('slider-phi'),
            gammaSat: document.getElementById('slider-gamma-sat'),
            r: document.getElementById('slider-r'),
            rpe: document.getElementById('slider-rpe'),
            mw: document.getElementById('slider-mw'),
            phiAxis: document.getElementById('slider-phi-axis'),
            cAxis: document.getElementById('slider-c-axis')
        };

        const displays = {
            phi: document.getElementById('phi-value-display'),
            gammaSat: document.getElementById('gamma-sat-value-display'),
            r: document.getElementById('r-value-display'),
            rpe: document.getElementById('rpe-value-display'),
            mw: document.getElementById('mw-value-display'),
            phiAxis: document.getElementById('phi-axis-value-display'),
            cAxis: document.getElementById('c-axis-value-display'),
            phiMax: document.getElementById('phimax-display'),
            range: document.getElementById('range-display')
        };

        // Initialize values
        function updateDisplays() {
            displays.phi.textContent = sliders.phi.value;
            displays.gammaSat.textContent = sliders.gammaSat.value;
            displays.r.textContent = sliders.r.value;
            displays.rpe.textContent = sliders.rpe.value;
            displays.mw.textContent = sliders.mw.value;
            displays.phiAxis.textContent = sliders.phiAxis.value;
            displays.cAxis.textContent = sliders.cAxis.value;
        }

        function getParameters() {
            return {
                phi: parseFloat(sliders.phi.value),
                gammaSat: parseFloat(sliders.gammaSat.value),
                r: parseFloat(sliders.r.value),
                rpe: parseFloat(sliders.rpe.value),
                mw: parseFloat(sliders.mw.value),
                phiAxis: parseFloat(sliders.phiAxis.value),
                cAxis: parseFloat(sliders.cAxis.value)
            };
        }

        function calculatePhiMax(params) {
            const A = (3 * params.gammaSat / params.r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * params.mw / Math.pow(params.rpe, 3);
            return Math.sqrt(B / A);
        }

        function calculateCurves(params) {
            const phiMax = calculatePhiMax(params);
            const x = [];
            const y1 = [];
            const y2 = [];
            const y3 = [];
            const step = 0.001;
            for (let phi = 0.001; phi <= params.phiAxis; phi += step) {
                x.push(phi);
                y1.push((3 * params.gammaSat / params.r) * C_UNIT_CONVERSION * phi);
                y2.push(y1[y1.length - 1] / (1 - phi));
                y3.push(y1[y1.length - 1] + (K_DEPLETION * params.mw / Math.pow(params.rpe, 3)) / phi);
            }
            return { x, y1, y2, y3, phiMax };
        }

        function updatePlot() {
            const params = getParameters();
            const { x, y1, y2, y3, phiMax: newPhiMax } = calculateCurves(params);
            phiMax = newPhiMax;

            // Update phi slider max
            sliders.phi.max = params.phiAxis;

            // Update phiMax display
            displays.phiMax.innerHTML = `ϕ<sub>max</sub>: ${phiMax.toFixed(4)}`;

            // Update stability range
            const currentPhi = params.phi;
            const y2Val = (3 * params.gammaSat / params.r) * C_UNIT_CONVERSION * currentPhi / (1 - currentPhi);
            const y3Val = (3 * params.gammaSat / params.r) * C_UNIT_CONVERSION * currentPhi + 
                         (K_DEPLETION * params.mw / Math.pow(params.rpe, 3)) / currentPhi;
            displays.range.textContent = `Stability range: ${y2Val.toFixed(2)} - ${y3Val.toFixed(2)} kg/m³`;

            // Prepare data for Plotly
            const data = [
                { x, y: y1, type: 'scatter', mode: 'lines', line: { color: 'blue' }, name: 'y1' },
                { x, y: y2, type: 'scatter', mode: 'lines', line: { color: 'red' }, name: 'y2' },
                { x, y: y3, type: 'scatter', mode: 'lines', line: { color: 'green' }, name: 'y3' },
                // Shaded region
                { x: x.filter(phi => phi <= phiMax), y: y2.filter((_, i) => x[i] <= phiMax), 
                  type: 'scatter', mode: 'lines', line: { color: 'transparent' }, fill: 'tonexty', 
                  fillcolor: 'rgba(173, 216, 230, 0.5)', name: 'Stability Region' },
                // Markers
                { x: [params.phi], y: [y1.find((_, i) => x[i] === params.phi)], 
                  type: 'scatter', mode: 'markers', marker: { color: 'blue', size: 10 }, name: 'y1 marker' },
                { x: [params.phi], y: [y2.find((_, i) => x[i] === params.phi)], 
                  type: 'scatter', mode: 'markers', marker: { color: 'red', size: 10 }, name: 'y2 marker' },
                { x: [params.phi], y: [y3.find((_, i) => x[i] === params.phi)], 
                  type: 'scatter', mode: 'markers', marker: { color: 'green', size: 10 }, name: 'y3 marker' }
            ];

            const layout = {
                xaxis: { title: 'ϕ (volume fraction)', range: [0, params.phiAxis] },
                yaxis: { title: 'C (kg/m³)', range: [0, params.cAxis] },
                showlegend: false,
                shapes: [{
                    type: 'line',
                    x0: params.phi, x1: params.phi,
                    y0: 0, y1: params.cAxis,
                    line: { color: 'grey', dash: 'dash' }
                }]
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        // Event listeners
        function setupEventListeners() {
            sliders.phi.addEventListener('input', () => {
                displays.phi.textContent = sliders.phi.value;
                updatePlot();
            });

            sliders.gammaSat.addEventListener('input', () => {
                displays.gammaSat.textContent = sliders.gammaSat.value;
                updatePlot();
            });

            sliders.r.addEventListener('input', () => {
                displays.r.textContent = sliders.r.value;
                updatePlot();
            });

            sliders.rpe.addEventListener('input', () => {
                displays.rpe.textContent = sliders.rpe.value;
                updatePlot();
            });

            sliders.mw.addEventListener('input', () => {
                displays.mw.textContent = sliders.mw.value;
                updatePlot();
            });

            sliders.phiAxis.addEventListener('input', () => {
                displays.phiAxis.textContent = sliders.phiAxis.value;
                sliders.phi.max = sliders.phiAxis.value;
                updatePlot();
            });

            sliders.cAxis.addEventListener('input', () => {
                displays.cAxis.textContent = sliders.cAxis.value;
                Plotly.relayout('plot-container', { 'yaxis.range': [0, sliders.cAxis.value] });
            });
        }

        // Initial setup
        updateDisplays();
        setupEventListeners();
        updatePlot();
    </script>
</body>
</html>
