<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Elastic Collision Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            height: 100vh;
        }
        #main-container {
            display: flex;
            width: 100%;
        }
        #control-panel {
            width: 300px;
            padding: 10px;
            background: #f0f0f0;
            overflow-y: auto;
        }
        #canvas-container {
            flex-grow: 1;
            position: relative;
        }
        .control-section {
            margin-bottom: 15px;
        }
        label {
            display: block;
            margin-top: 8px;
        }
        input[type="range"] {
            width: 100%;
        }
        #joystick-container {
            width: 150px;
            height: 150px;
            border: 2px solid #333;
            border-radius: 50%;
            margin: 10px 0;
            position: relative;
            background: #fff;
        }
        #joystick-knob {
            width: 30px;
            height: 30px;
            background: #000;
            border-radius: 50%;
            position: absolute;
            top: 60px;
            left: 60px;
            cursor: pointer;
        }
        .value-display {
            font-size: 14px;
            color: #333;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-section">
                <label for="slider-time">time relative to collision (s)</label>
                <input type="range" id="slider-time" min="-30" max="30" step="0.1" value="-10">
                <span class="value-display" id="label-time">-10.0</span>
            </div>
            <div class="control-section">
                <h4>object 1</h4>
                <label for="slider-m1">mass (kg)</label>
                <input type="range" id="slider-m1" min="1" max="10" step="0.01" value="5">
                <span class="value-display" id="label-m1">5.00</span>
                <label for="slider-v1">speed (m/s)</label>
                <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">
                <span class="value-display" id="label-v1">10.0</span>
            </div>
            <div class="control-section">
                <h4>object 2</h4>
                <label for="slider-m2">mass (kg)</label>
                <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
                <span class="value-display" id="label-m2">3.00</span>
                <label for="slider-v2">speed (m/s)</label>
                <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5" disabled>
                <span class="value-display" id="label-v2">5.0</span>
            </div>
            <div class="control-section">
                <label>observer velocity (m/s)</label>
                <div id="joystick-container">
                    <div id="joystick-knob"></div>
                </div>
                <span class="value-display" id="label-v0x">v0x: 1.1</span>
                <span class="value-display" id="label-v0y">v0y: 1.1</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let timeSlider, m1Slider, v1Slider, m2Slider, v2Slider;
        let v0x = 1.1, v0y = 1.1;
        let joystickKnob;
        let canvasContainer;

        function setup() {
            canvasContainer = document.getElementById('canvas-container');
            let canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            canvas.style('display', 'block');

            timeSlider = document.getElementById('slider-time');
            m1Slider = document.getElementById('slider-m1');
            v1Slider = document.getElementById('slider-v1');
            m2Slider = document.getElementById('slider-m2');
            v2Slider = document.getElementById('slider-v2');

            timeSlider.addEventListener('input', updateDisplay);
            m1Slider.addEventListener('input', updatePhysics);
            v1Slider.addEventListener('input', updatePhysics);
            m2Slider.addEventListener('input', updatePhysics);

            joystickKnob = document.getElementById('joystick-knob');
            let joystickContainer = document.getElementById('joystick-container');
            let isDragging = false;

            joystickContainer.addEventListener('mousedown', (e) => {
                isDragging = true;
                updateJoystick(e);
            });

            document.addEventListener('mousemove', (e) => {
                if (isDragging) updateJoystick(e);
            });

            document.addEventListener('mouseup', () => {
                isDragging = false;
            });

            updatePhysics();
        }

        function updateJoystick(e) {
            const rect = joystickContainer.getBoundingClientRect();
            const centerX = rect.left + rect.width / 2;
            const centerY = rect.top + rect.height / 2;
            let dx = e.clientX - centerX;
            let dy = e.clientY - centerY;
            const maxDist = rect.width / 2;
            const dist = Math.sqrt(dx*dx + dy*dy);

            if (dist > maxDist) {
                dx = dx * maxDist / dist;
                dy = dy * maxDist / dist;
            }

            const x = (dx / maxDist) * 5;
            const y = -(dy / maxDist) * 5;

            v0x = Math.max(-5, Math.min(5, x));
            v0y = Math.max(-5, Math.min(5, y));

            joystickKnob.style.left = `${(dx + maxDist) - 15}px`;
            joystickKnob.style.top = `${(dy + maxDist) - 15}px`;

            document.getElementById('label-v0x').textContent = `v0x: ${v0x.toFixed(1)}`;
            document.getElementById('label-v0y').textContent = `v0y: ${v0y.toFixed(1)}`;
            updateDisplay();
        }

        function updatePhysics() {
            const m1 = parseFloat(m1Slider.value);
            const m2 = parseFloat(m2Slider.value);
            const s1_cm = parseFloat(v1Slider.value);
            const s2_cm = (m1 / m2) * s1_cm;

            // Pre-collision velocities in CM frame
            const u1_cm = {x: s1_cm, y: 0};
            const u2_cm = {x: -s2_cm, y: 0};

            // Post-collision velocities (90° rotation)
            const angle = -45 * Math.PI / 180;
            v1_cm = {
                x: u1_cm.x * Math.cos(angle) - u1_cm.y * Math.sin(angle),
                y: u1_cm.x * Math.sin(angle) + u1_cm.y * Math.cos(angle)
            };
            v2_cm = {
                x: u2_cm.x * Math.cos(angle) - u2_cm.y * Math.sin(angle),
                y: u2_cm.x * Math.sin(angle) + u2_cm.y * Math.cos(angle)
            };

            updateDisplay();
        }

        function updateDisplay() {
            document.getElementById('label-time').textContent = timeSlider.value;
            document.getElementById('label-m1').textContent = m1Slider.value;
            document.getElementById('label-v1').textContent = v1Slider.value;
            document.getElementById('label-m2').textContent = m2Slider.value;
            document.getElementById('label-v2').textContent = v2Slider.value;
        }

        let u1_cm = {x: 0, y: 0}, u2_cm = {x: 0, y: 0};
        let v1_cm = {x: 0, y: 0}, v2_cm = {x: 0, y: 0};

        function draw() {
            background(255);
            translate(width/2, height/2);

            const t = parseFloat(timeSlider.value);
            const m1 = parseFloat(m1Slider.value);
            const m2 = parseFloat(m2Slider.value);

            // Calculate observer frame velocities
            const u1_obs = {x: u1_cm.x + v0x, y: u1_cm.y + v0y};
            const u2_obs = {x: u2_cm.x + v0x, y: u2_cm.y + v0y};

            // Calculate positions
            const pos1 = {x: u1_cm.x * t, y: u1_cm.y * t};
            const pos2 = {x: u2_cm.x * t, y: u2_cm.y * t};

            // Draw trajectories
            strokeWeight(1);
            stroke(255, 0, 0, 100);
            line(-width/2, 0, width/2, 0);
            stroke(0, 0, 255, 100);
            line(-width/2, 0, width/2, 0);

            // Draw particles
            const r1 = 10 * Math.sqrt(m1);
            const r2 = 10 * Math.sqrt(m2);

            fill(255, 0, 0);
            ellipse(pos1.x, pos1.y, r1*2);
            fill(0, 0, 255);
            ellipse(pos2.x, pos2.y, r2*2);

            // Draw observer velocity vector
            stroke(0);
            strokeWeight(2);
            const arrowLen = 50;
            const vx = -v0x * 10;
            const vy = -v0y * 10;
            line(0, 0, vx, vy);
            fill(0);
            triangle(vx, vy, vx - 5, vy - 5, vx - 5, vy + 5);

            // Draw text
            fill(0);
            noStroke();
            textSize(14);
            const v0_mag = Math.sqrt(v0x*v0x + v0y*v0y).toFixed(1);
            const v1_mag = Math.sqrt(u1_obs.x*u1_obs.x + u1_obs.y*u1_obs.y).toFixed(1);
            const v2_mag = Math.sqrt(u2_obs.x*u2_obs.x + u2_obs.y*u2_obs.y).toFixed(1);
            const energy = (0.5*m1*v1_mag*v1_mag + 0.5*m2*v2_mag*v2_mag).toFixed(2);
            text(`v₀: ${v0_mag} m/s`, -width/2 + 10, -height/2 + 20);
            text(`v₁: ${v1_mag} m/s`, -width/2 + 10, -height/2 + 40);
            text(`v₂: ${v2_mag} m/s`, -width/2 + 10, -height/2 + 60);
            text(`Energy: ${energy} J`, -width/2 + 10, -height/2 + 80);
        }
    </script>
</body>
</html>
