<body>
    <div class="main-container">
        <div class="control-panel">
            <div class="control-group">
                <label for="shift-slider">shift</label>
                <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">
            </div>
            <div class="control-group">
                <label for="text-input">text</label>
                <input type="text" id="text-input" list="phrase-list" value="abcd">
                <datalist id="phrase-list">
                    <option value="abcd">
                    <option value="test phrase">
                    <option value="to be or not to be">
                    <option value="the quick brown fox jumped over the lazy dog">
                </datalist>
            </div>
        </div>

        <div class="display-area">
            <div class="alphabet-container">
                <div class="alphabet-row" id="plaintext-alphabet"></div>
                <div class="alphabet-row" id="ciphertext-alphabet"></div>
            </div>

            <div class="key-container">
                <p id="encryption-key-display">encryption key: 12</p>
                <p id="decryption-key-display">decryption key: 14</p>
            </div>

            <div class="text-output">
                <p id="plaintext-output">plaintext : <span class="plaintext">abcd</span></p>
                <p id="ciphertext-output">ciphertext: <span class="ciphertext">MNOP</span></p>
            </div>
        </div>
    </div>

    <script>
        const ALPHABET = 'abcdefghijklmnopqrstuvwxyz';

        function encrypt(text, shift) {
            let result = '';
            for (let i = 0; i < text.length; i++) {
                const char = text[i].toLowerCase();
                if (char >= 'a' && char <= 'z') {
                    const index = char.charCodeAt(0) - 'a'.charCodeAt(0);
                    const shiftedIndex = (index + shift) % 26;
                    const encryptedChar = ALPHABET[shiftedIndex].toUpperCase();
                    result += encryptedChar;
                } else {
                    result += char;
                }
            }
            return result;
        }

        function renderAlphabet(container, alphabet, className) {
            container.innerHTML = '';
            for (let i = 0; i < alphabet.length; i++) {
                const span = document.createElement('span');
                span.textContent = alphabet[i];
                span.className = className;
                container.appendChild(span);
            }
        }

        function updateCipher() {
            const shiftValue = parseInt(document.getElementById('shift-slider').value);
            const textValue = document.getElementById('text-input').value;

            // Update plaintext alphabet
            const plaintextContainer = document.getElementById('plaintext-alphabet');
            renderAlphabet(plaintextContainer, ALPHABET, 'plaintext-char');

            // Update ciphertext alphabet
            const shiftedAlphabet = ALPHABET.slice(shiftValue) + ALPHABET.slice(0, shiftValue);
            const ciphertextContainer = document.getElementById('ciphertext-alphabet');
            renderAlphabet(ciphertextContainer, shiftedAlphabet.toUpperCase(), 'ciphertext-char');

            // Update keys
            document.getElementById('encryption-key-display').textContent = `encryption key: ${shiftValue}`;
            const decryptionKey = (26 - shiftValue) % 26;
            document.getElementById('decryption-key-display').textContent = `decryption key: ${decryptionKey}`;

            // Update text output
            document.getElementById('plaintext-output').innerHTML = 
                `plaintext : <span class="plaintext">${textValue}</span>`;
            const encrypted = encrypt(textValue, shiftValue);
            document.getElementById('ciphertext-output').innerHTML = 
                `ciphertext: <span class="ciphertext">${encrypted}</span>`;
        }

        // Initialize on page load
        document.addEventListener('DOMContentLoaded', function() {
            updateCipher();

            // Add event listeners
            document.getElementById('shift-slider').addEventListener('input', updateCipher);
            document.getElementById('text-input').addEventListener('input', updateCipher);
        });
    </script>
</body>
</html>
