<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Inclined Plane Simulation</title>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/mathjax/3.2.2/es5/tex-mml-chtml.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.0/p5.min.js"></script>
  <style>
    html, body {
      margin: 0;
      padding: 0;
      height: 100%;
      font-family: sans-serif;
      background: #f2f2f2;
    }
    #main-container {
      display: flex;
      height: 100vh;
    }
    #control-panel {
      width: 160px;
      background: #f8f8f8;
      padding: 1em;
      display: flex;
      flex-direction: column;
      gap: 1em;
      border-right: 1px solid #ccc;
      align-items: center;
    }
    #visualization-area {
      flex: 1;
      display: flex;
      flex-direction: column;
      padding: 1em;
    }
    #info-display {
      margin: 0 0 1em 0;
      font-size: 1.2em;
    }
    #p5-canvas {
      flex: 1;
      border: none;
    }
    #slider-container {
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 0.5em;
    }
    label[for="slider-angle"] {
      font-weight: bold;
    }
    #slider-angle {
      transform: rotate(270deg) scale(1);
      width: 120px;
      margin-top: 60px;
      margin-bottom: 60px;
    }
    #animation-controls {
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 0.5em;
    }
    #animation-controls button {
      font-size: 1.2em;
    }
    #animation-controls p {
      margin: 0;
      font-weight: bold;
    }
  </style>
</head>

<body>
  <div id="main-container">
    <div id="control-panel">
      <div id="slider-container">
        <label for="slider-angle">slope θ</label>
        <input type="range" id="slider-angle" min="10" max="80" step="1" value="30">
      </div>
      <div id="animation-controls">
        <p>release block</p>
        <button id="btn-play">▶</button>
        <button id="btn-pause">||</button>
        <button id="btn-reset">|<</button>
      </div>
    </div>
    <div id="visualization-area">
      <p id="info-display">
        slope = \( \theta \) = 30°; acceleration = \( g \sin(\theta) \) = 4.905 m/s²
      </p>
      <canvas id="p5-canvas"></canvas>
    </div>
  </div>

  <script>
    // State variables
    let theta = 30; // degrees
    let g = 9.81;

    let blockPos = 0; // along the slope (0 to maxS)
    let isPlaying = false;
    let startTime = 0;
    let maxS = 300; // pixels

    // Elements
    const angleSlider = document.getElementById('slider-angle');
    const infoDisplay = document.getElementById('info-display');
    const btnPlay = document.getElementById('btn-play');
    const btnPause = document.getElementById('btn-pause');
    const btnReset = document.getElementById('btn-reset');

    // Update slider change handler
    angleSlider.addEventListener('input', e => {
      theta = Number(e.target.value);
      updateInfoDisplay();
      resetAnimation();
    });

    // Buttons handlers
    btnPlay.addEventListener('click', () => {
      isPlaying = true;
      startTime = millis();
    });
    btnPause.addEventListener('click', () => {
      isPlaying = false;
      startTime = 0;
    });
    btnReset.addEventListener('click', () => {
      isPlaying = false;
      updateInfoDisplay();
      resetAnimation();
    });

    function updateInfoDisplay() {
      let acc = (9.81 * Math.sin(theta * Math.PI / 180)).toFixed(3);
      infoDisplay.innerHTML =
        `slope = \\( \\theta \\) = ${theta}°; acceleration = \\( g \\sin(\\theta) \\) = ${acc} m/s²`;
      MathJax.typesetPromise([infoDisplay]);
    }

    function resetAnimation() {
      blockPos = 0;
      startTime = 0;
    }

    // p5.js sketch
    function setup() {
      let canvasDiv = select('#p5-canvas');
      let canvas = createCanvas(canvasDiv.width, canvasDiv.height);
      canvas.parent('p5-canvas');
      updateInfoDisplay();
    }

    function draw() {
      clear();
      background("#fff");
      const angRad = theta * Math.PI / 180;

      // Physics calculation
      if (isPlaying) {
        const a = 9.81 * Math.sin(angRad); // m/s²
        // Convert pixels to meters (assuming 1px = 0.01m)
        let s = 0.5 * a * ((millis() - startTime) / 1000) ** 2 * 100;
        blockPos = s;
        if (blockPos >= maxS) {
          blockPos = maxS;
          isPlaying = false;
        }
      }

      // Drawing
      push();
      // Move origin to bottom-left of ramp
      translate(100, height - 100);

      // Draw inclined plane triangle
      strokeWeight(2);
      stroke('yellow');
      fill('lightblue');
      const rampLength = 500;
      const h = rampLength * Math.sin(angRad);
      const w = rampLength * Math.cos(angRad);
      triangle(0, 0, w, -h, 0, -h);

      // Block along slope
      stroke('black');
      fill('darkgrey');
      const blockSize = 30;
      const x = blockPos * Math.cos(angRad);
      const y = -blockPos * Math.sin(angRad);
      push();
      translate(x, y);
      rotate(-angRad);
      rect(-blockSize/2, 0, blockSize, -blockSize);
      pop();

      // Coordinate axes (rotated with slope)
      stroke('black');
      strokeWeight(1.5);
      drawArrow(50 - blockSize/2, -blockSize/2, 100 - blockSize/2 - 50, -blockSize/2, 'black', 'x');
      drawArrow(50 - blockSize/2, -blockSize/2, 50 - blockSize/2, -blockSize/2 - 100, 'black', 'y');

      // Force vectors originate from block center
      push();
      translate(x, y);
      rotate(-angRad);
      // Gravity (vertical)
      drawArrow(0, 0, 0, -100, 'red', 'F_{grav}=mg');
      // Normal force
      const normalLen = 100 * Math.cos(angRad);
      drawArrow(0, 0, 0, -normalLen, 'deepskyblue', '"normal" force');
      // Net force
      const netLen = 100 * 0.5 * Math.sin(angRad); // reduced for visibility
      drawArrow(0, 0, netLen, 0, 'purple', 'F_{net}=m g\\sin(\\theta)');

      // Dashed gravity components
      stroke('black');
      strokeWeight(1);
      drawingContext.setLineDash([5, 5]);
      // perp component horizontal
      line(0, -100, -100 * Math.sin(angRad), -100);
      line(-100 * Math.sin(angRad), -100, -100 * Math.sin(angRad), 0);
      // parallel component
      line(0, 0, 100 * Math.sin(angRad), -100 * Math.sin(angRad));
      drawingContext.setLineDash([]);
      fill('black');
      noStroke();
      text('m g\\cos(\\theta)', -100*Math.sin(angRad) - 30, -100/2);
      text('\\theta', -15, -40);
      // Angle arc
      stroke('black');
      noFill();
      arc(0, 0, 40, 40, -angRad, 0);
      pop();

      pop(); // reset transforms
    }

    // Helper arrow drawing
    function drawArrow(x1, y1, x2, y2, color, label = '') {
      push();
      stroke(color);
      strokeWeight(2);
      line(x1, y1, x2, y2);
      let angle = atan2(y2 - y1, x2 - x1);
      let len = dist(x1, y1, x2, y2);
      if (len > 5) {
        push();
        translate(x2, y2);
        rotate(angle);
        line(-5, 0, 0, 0);
        line(-5, 0, -5*sin(0.3), 5*cos(0.3));
        line(-5, 0, -5*sin(0.3), -5*cos(0.3));
        pop();
      }
      if (label) {
        noStroke();
        fill(color);
        text(label, x2 + 8, y2 - 5);
      }
      pop();
    }
  </script>
</body>
</html>
