    </style>
</head>
<body>
    <div id="control-panel">
        <div id="elements-control">
            <label for="slider-elements">elements (set, then click "randomize")</label>
            <input type="range" id="slider-elements" min="2" max="50" value="10" step="1">
            <span id="elements-value">10</span>
        </div>

        <div id="step-control">
            <label for="slider-step">step</label>
            <input type="range" id="slider-step" min="1" max="1" value="1" step="1">
            <button id="btn-play-pause">▶</button>
            <button id="btn-step-backward">-</button>
            <button id="btn-step-forward">+</button>
            <button id="btn-reset-step">«</button>
            <span id="step-value">1</span>
        </div>

        <div id="sort-control">
            <label>sort</label>
            <button id="btn-shellsort" class="sort-btn active">shellsort</button>
            <button id="btn-bubblesort" class="sort-btn">bubblesort</button>
            <button id="btn-insertionsort" class="sort-btn">insertionsort</button>
            <button id="btn-cocktail" class="sort-btn">cocktail</button>
            <button id="btn-bogosort" class="sort-btn">bogosort</button>
            <button id="btn-randomize">randomize</button>
        </div>
    </div>

    <div id="visualization-area">
        <canvas id="bar-chart-canvas"></canvas>
    </div>

    <script>
        // State management
        const state = {
            currentAlgorithm: 'shellsort',
            numElements: 10,
            sortHistory: [],
            currentStep: 1,
            isPlaying: false,
            animationInterval: null
        };

        // DOM elements
        const elementsSlider = document.getElementById('slider-elements');
        const elementsValue = document.getElementById('elements-value');
        const stepSlider = document.getElementById('slider-step');
        const stepValue = document.getElementById('step-value');
        const playPauseBtn = document.getElementById('btn-play-pause');
        const stepBackwardBtn = document.getElementById('btn-step-backward');
        const stepForwardBtn = document.getElementById('btn-step-forward');
        const resetStepBtn = document.getElementById('btn-reset-step');
        const randomizeBtn = document.getElementById('btn-randomize');
        const sortButtons = document.querySelectorAll('.sort-btn');

        // p5.js variables
        let sketch = function(p) {
            const margin = 60;
            const maxValue = 20;
            const chartHeight = 400 - 2 * margin;

            p.setup = function() {
                let canvas = p.createCanvas(800, 400);
                canvas.parent('visualization-area');
                p.noLoop();
                generateAndSort();
            };

            p.draw = function() {
                p.background(255);
                p.strokeWeight(1);

                // Draw y-axis
                p.line(margin, margin, margin, p.height - margin);

                // Draw y-axis labels
                p.fill(0);
                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 0; i <= 5; i++) {
                    let y = p.height - margin - (i * chartHeight / 5);
                    let value = Math.round(i * maxValue / 5);
                    p.text(value, margin - 10, y);
                    p.line(margin, y, margin + 5, y);
                }

                if (state.sortHistory.length > 0) {
                    let currentArray = state.sortHistory[state.currentStep - 1];
                    let barWidth = (p.width - 2 * margin) / currentArray.length;

                    // Draw bars
                    for (let i = 0; i < currentArray.length; i++) {
                        let x = margin + i * barWidth + 1;
                        let height = p.map(currentArray[i], 1, maxValue, 0, chartHeight);
                        let y = p.height - margin - height;

                        // Color based on value
                        let hue = p.map(currentArray[i], 1, maxValue, 0, 240);
                        p.colorMode(p.HSB, 360, 100, 100);
                        p.fill(hue, 80, 90);
                        p.stroke(0);

                        p.rect(x, y, barWidth - 2, height);
                    }
                }
            };
        };

        let myP5 = new p5(sketch);

        // Event listeners
        elementsSlider.addEventListener('input', function() {
            state.numElements = parseInt(this.value);
            elementsValue.textContent = state.numElements;
        });

        stepSlider.addEventListener('input', function() {
            state.currentStep = parseInt(this.value);
            stepValue.textContent = state.currentStep;
            myP5.redraw();
        });

        playPauseBtn.addEventListener('click', togglePlayPause);
        stepBackwardBtn.addEventListener('click', stepBackward);
        stepForwardBtn.addEventListener('click', stepForward);
        resetStepBtn.addEventListener('click', resetSteps);
        randomizeBtn.addEventListener('click', generateAndSort);

        sortButtons.forEach(btn => {
            btn.addEventListener('click', function() {
                sortButtons.forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                state.currentAlgorithm = this.id.replace('btn-', '');

                if (state.currentAlgorithm === 'bogosort') {
                    elementsSlider.disabled = true;
                    elementsSlider.value = 7;
                    state.numElements = 7;
                    elementsValue.textContent = 7;
                } else {
                    elementsSlider.disabled = false;
                }

                generateAndSort();
            });
        });

        function generateAndSort() {
            const array = Array.from({length: state.numElements}, (_, i) => i + 1);
            shuffleArray(array);

            state.sortHistory = [array.slice()];

            switch(state.currentAlgorithm) {
                case 'shellsort':
                    shellsort(array.slice());
                    break;
                case 'bubblesort':
                    bubblesort(array.slice());
                    break;
                case 'insertionsort':
                    insertionsort(array.slice());
                    break;
                case 'cocktail':
                    cocktailShakerSort(array.slice());
                    break;
                case 'bogosort':
                    bogosort(array.slice());
                    break;
            }

            updateStepSlider();
            state.currentStep = 1;
            stepSlider.value = 1;
            stepValue.textContent = 1;
            stopAnimation();
            myP5.redraw();
        }

        function updateStepSlider() {
            stepSlider.max = state.sortHistory.length;
            stepSlider.value = state.currentStep;
        }

        function togglePlayPause() {
            state.isPlaying = !state.isPlaying;
            if (state.isPlaying) {
                playPauseBtn.textContent = '❚❚';
                state.animationInterval = setInterval(() => {
                    if (state.currentStep < state.sortHistory.length) {
                        state.currentStep++;
                        stepSlider.value = state.currentStep;
                        stepValue.textContent = state.currentStep;
                        myP5.redraw();
                    } else {
                        stopAnimation();
                    }
                }, 200);
            } else {
                stopAnimation();
            }
        }

        function stopAnimation() {
            state.isPlaying = false;
            playPauseBtn.textContent = '▶';
            if (state.animationInterval) {
                clearInterval(state.animationInterval);
                state.animationInterval = null;
            }
        }

        function stepBackward() {
            if (state.currentStep > 1) {
                state.currentStep--;
                stepSlider.value = state.currentStep;
                stepValue.textContent = state.currentStep;
                myP5.redraw();
            }
        }

        function stepForward() {
            if (state.currentStep < state.sortHistory.length) {
                state.currentStep++;
                stepSlider.value = state.currentStep;
                stepValue.textContent = state.currentStep;
                myP5.redraw();
            }
        }

        function resetSteps() {
            state.currentStep = 1;
            stepSlider.value = state.currentStep;
            stepValue.textContent = state.currentStep;
            myP5.redraw();
        }

        function shuffleArray(array) {
            for (let i = array.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [array[i], array[j]] = [array[j], array[i]];
            }
        }

        // Sorting algorithms
        function shellsort(arr) {
            const n = arr.length;
            for (let gap = Math.floor(n/2); gap > 0; gap = Math.floor(gap/2)) {
                for (let i = gap; i < n; i++) {
                    let temp = arr[i];
                    let j;
                    for (j = i; j >= gap && arr[j-gap] > temp; j -= gap) {
                        arr[j] = arr[j-gap];
                        state.sortHistory.push(arr.slice());
                    }
                    arr[j] = temp;
                    state.sortHistory.push(arr.slice());
                }
            }
        }

        function bubblesort(arr) {
            const n = arr.length;
            for (let i = 0; i < n; i++) {
                for (let j = 0; j < n - i - 1; j++) {
                    if (arr[j] > arr[j + 1]) {
                        [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
                        state.sortHistory.push(arr.slice());
                    }
                }
            }
        }

        function insertionsort(arr) {
            for (let i = 1; i < arr.length; i++) {
                let key = arr[i];
                let j = i - 1;
                while (j >= 0 && arr[j] > key) {
                    arr[j + 1] = arr[j];
                    j -= 1;
                    state.sortHistory.push(arr.slice());
                }
                arr[j + 1] = key;
                state.sortHistory.push(arr.slice());
            }
        }

        function cocktailShakerSort(arr) {
            const n = arr.length;
            let swapped;
            do {
                swapped = false;
                for (let i = 0; i < n - 1; i++) {
                    if (arr[i] > arr[i + 1]) {
                        [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]];
                        state.sortHistory.push(arr.slice());
                        swapped = true;
                    }
                }
                if (!swapped) break;
                swapped = false;
                for (let i = n - 1; i > 0; i--) {
                    if (arr[i] < arr[i - 1]) {
                        [arr[i], arr[i - 1]] = [arr[i - 1], arr[i]];
                        state.sortHistory.push(arr.slice());
                        swapped = true;
                    }
                }
            } while (swapped);
        }

        function bogosort(arr) {
            function isSorted(arr) {
                for (let i = 1; i < arr.length; i++) {
                    if (arr[i-1] > arr[i]) return false;
                }
                return true;
            }
            while (!isSorted(arr)) {
                shuffleArray(arr);
                state.sortHistory.push(arr.slice());
            }
        }
    </script>
</body>
</html>
