</head>
<body>

    <!-- Main container -->
    <div id="demo-container">

        <!-- Control Panel Section -->
        <div id="control-panel">
            <button id="btn-reset" title="Reset to defaults">+</button>
            <div class="slider-row">
                <label for="slider-x1">x₁</label>
                <input type="range" id="slider-x1" min="-1000" max="1000" step="1" value="500">
                <span id="value-x1">500</span>
            </div>
            <div class="slider-row">
                <label for="slider-v1">v₁</label>
                <input type="range" id="slider-v1" min="-15" max="15" step="0.01" value="-10">
                <span id="value-v1">-10.00</span>
            </div>
            <div class="slider-row">
                <label for="slider-x2">x₂</label>
                <input type="range" id="slider-x2" min="-1000" max="1000" step="1" value="100">
                <span id="value-x2">100</span>
            </div>
            <div class="slider-row">
                <label for="slider-v2">v₂</label>
                <input type="range" id="slider-v2" min="-15" max="15" step="0.01" value="5">
                <span id="value-v2">5.00</span>
            </div>
        </div>

        <!-- Visualization Section -->
        <div id="visualization-area">
            <h2 id="result-text">T = 40.00 s | X = 100.00 m</h2>
            <div id="plot-container"></div>
        </div>

    </div>

    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        // Get references
        const sliderX1 = document.getElementById('slider-x1');
        const sliderV1 = document.getElementById('slider-v1');
        const sliderX2 = document.getElementById('slider-x2');
        const sliderV2 = document.getElementById('slider-v2');
        const valueX1 = document.getElementById('value-x1');
        const valueV1 = document.getElementById('value-v1');
        const valueX2 = document.getElementById('value-x2');
        const valueV2 = document.getElementById('value-v2');
        const resultText = document.getElementById('result-text');
        const btnReset = document.getElementById('btn-reset');
        const plotContainer = document.getElementById('plot-container');

        // Default values
        const defaults = {
            x1: 500,
            v1: -10,
            x2: 100,
            v2: 5
        };

        // Central update function
        function updateEverything() {
            // Read values
            const x1 = parseFloat(sliderX1.value);
            const v1 = parseFloat(sliderV1.value);
            const x2 = parseFloat(sliderX2.value);
            const v2 = parseFloat(sliderV2.value);

            // Update displayed values
            valueX1.textContent = x1;
            valueV1.textContent = v1.toFixed(2);
            valueX2.textContent = x2;
            valueV2.textContent = v2.toFixed(2);

            // Calculate intersection
            let T, X;
            if (Math.abs(v1 - v2) < 1e-6) {
                // Parallel
                resultText.textContent = "The runners' paths never intersect";
                T = null;
                X = null;
            } else {
                T = (x1 - x2) / (v2 - v1);
                X = x1 + v1 * T;
                if (T < 0 || T > 1000) {
                    resultText.textContent = "The runners' paths never intersect";
                    T = null;
                    X = null;
                } else {
                    resultText.textContent = `T = ${T.toFixed(2)} s | X = ${X.toFixed(2)} m`;
                }
            }

            // Prepare traces
            const t0 = 0;
            const t1 = 1000;
            const pos1_0 = x1 + v1 * t0;
            const pos1_1 = x1 + v1 * t1;
            const pos2_0 = x2 + v2 * t0;
            const pos2_1 = x2 + v2 * t1;

            const trace1 = {
                x: [t0, t1],
                y: [pos1_0, pos1_1],
                mode: 'lines',
                name: 'Runner 1',
                line: { color: 'rgb(70,130,180)', width: 2 },
                showlegend: false
            };

            const trace2 = {
                x: [t0, t1],
                y: [pos2_0, pos2_1],
                mode: 'lines',
                name: 'Runner 2',
                line: { color: 'rgb(255,165,0)', width: 2 },
                showlegend: false
            };

            const traceInter = {
                x: (T !== null) ? [T] : [],
                y: (X !== null) ? [X] : [],
                mode: 'markers',
                marker: { color: 'green', size: 10, line: { color: 'darkgreen', width: 2 } },
                showlegend: false
            };

            const layout = {
                xaxis: { title: 't (s)', range: [0, 1000], gridcolor: '#e0e0e0' },
                yaxis: { title: 'distance (m)', autorange: true, gridcolor: '#e0e0e0', zerolinecolor: '#a0a0a0' },
                margin: { t: 20, r: 20, b: 40, l: 60 },
                plot_bgcolor: '#ffffff'
            };

            Plotly.react(plotContainer, [trace1, trace2, traceInter], layout, { responsive: true });
        }

        // Event listeners
        [sliderX1, sliderV1, sliderX2, sliderV2].forEach(slider =>
            slider.addEventListener('input', updateEverything)
        );
        btnReset.addEventListener('click', () => {
            sliderX1.value = defaults.x1;
            sliderV1.value = defaults.v1;
            sliderX2.value = defaults.x2;
            sliderV2.value = defaults.v2;
            updateEverything();
        });

        // Initial draw
        updateEverything();
    </script>

</body>
</html>
