        function setup() {
            const canvas = createCanvas(750, 400);
            canvas.parent('canvas-container');
            frameRate(30);
        }

        function draw() {
            background(255);
            stroke(0);

            const data = demoData[currentExample];
            const step = data.steps[currentStep];

            // Draw edges
            strokeWeight(2);
            data.edges.forEach((edge, idx) => {
                const [u, v] = edge;
                const isMatched = step.matching.includes(idx);

                if (isMatched) {
                    stroke(step.isFinal ? '#4d4dff' : '#ff4d4d');
                    strokeWeight(6);
                } else {
                    stroke('#cccccc');
                    strokeWeight(2);
                }

                line(data.vertices[u].x, data.vertices[u].y, 
                    data.vertices[v].x, data.vertices[v].y);
            });

            // Draw blossom if exists
            if (step.blossom) {
                fill(0, 0, 255, 30);
                stroke(0);
                strokeWeight(1);
                beginShape();
                step.blossom.forEach(vIndex => {
                    vertex(data.vertices[vIndex].x, data.vertices[vIndex].y);
                });
                endShape(CLOSE);

                // Draw yellow super-vertex
                fill(255, 255, 0);
                stroke(0);
                strokeWeight(2);
                const topV = data.vertices[step.topVertex];
                circle(topV.x, topV.y, 20);
            }

            // Draw tree edges
            if (step.tree) {
                stroke(0, 200, 0);
                strokeWeight(4);
                for (let i = 1; i < step.tree.length; i += 2) {
                    const u = step.tree[i];
                    const v = step.tree[i + 1];
                    if (v !== undefined) {
                        line(data.vertices[u].x, data.vertices[u].y,
                            data.vertices[v].x, data.vertices[v].y);
                    }
                }
            }

            // Draw vertices
            fill(0);
            noStroke();
            data.vertices.forEach(vertex => {
                circle(vertex.x, vertex.y, 8);
            });
        }

        // Handle button clicks
        function manageControls() {
            const exampleButtons = document.querySelectorAll('#example-controls .btn');
            const stepButtons = document.querySelectorAll('#step-controls .btn');

            exampleButtons.forEach(btn => {
                btn.addEventListener('click', (e) => {
                    // Update active example
                    exampleButtons.forEach(b => b.classList.remove('active'));
                    e.target.classList.add('active');

                    currentExample = e.target.id.split('-')[2];
                    currentStep = 0;

                    // Reset step buttons and select step 1
                    stepButtons.forEach(b => b.classList.remove('active'));
                    document.getElementById('btn-step-1').classList.add('active');

                    // Update display
                    updateDisplay();
                });
            });

            stepButtons.forEach(btn => {
                btn.addEventListener('click', (e) => {
                    stepButtons.forEach(b => b.classList.remove('active'));
                    e.target.classList.add('active');

                    currentStep = parseInt(e.target.id.split('-')[2]) - 1;

                    updateDisplay();
                });
            });
        }

        function updateDisplay() {
            const data = demoData[currentExample];
            const step = data.steps[currentStep];

            document.getElementById('description-text').textContent = step.description;

            // Ensure all step buttons are visible (both examples have 11 steps)
            for (let i = 1; i <= 11; i++) {
                const btn = document.getElementById(`btn-step-${i}`);
                btn.style.display = 'block';
            }

            // Update active step button
            document.querySelectorAll('#step-controls .btn').forEach(b => {
                b.classList.remove('active');
            });
            document.getElementById(`btn-step-${currentStep + 1}`).classList.add('active');

            redraw();
        }

        // Initialize controls
        document.addEventListener('DOMContentLoaded', () => {
            manageControls();
            updateDisplay();
        });
    </script>
</body>
</html>
