    <script type="module">
        import * as THREE from 'https://unpkg.com/three@0.158.0/build/three.module.js';
        import { OrbitControls } from 'https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js';
        import { Brush, Evaluator } from 'https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js';

        let scene, camera, renderer, controls;
        let cylinderMesh, drillMesh, csgResult, boundaryCurveFront, boundaryCurveBack;
        let evaluator;

        const params = {
            cylinderRadius: 2.1,
            opacity: 0.95,
            drillRadius: 1.35,
            inclination: 0.76,
            rotation: 0.9,
            offset: -0.92,
            boundaryOnly: false
        };

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xe0e0e0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(5, 5, 5);

            // Renderer setup
            const canvas = document.getElementById('main-canvas');
            renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
            renderer.shadowMap.enabled = true;

            // Controls setup
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(10, 10, 5);
            directionalLight.castShadow = true;
            scene.add(directionalLight);

            evaluator = new Evaluator();

            // Initial setup
            updateScene();
            setupControls();

            window.addEventListener('resize', onWindowResize);
        }

        function updateScene() {
            // Remove existing geometries
            if (csgResult) scene.remove(csgResult);
            if (boundaryCurveFront) scene.remove(boundaryCurveFront);
            if (boundaryCurveBack) scene.remove(boundaryCurveBack);
            if (cylinderMesh) scene.remove(cylinderMesh);

            // Create main cylinder
            const cylinderGeometry = new THREE.CylinderGeometry(params.cylinderRadius, params.cylinderRadius, 8, 64);
            const cylinderMaterial = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                transparent: true,
                opacity: params.boundaryOnly ? 0.15 : params.opacity
            });
            cylinderMesh = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
            cylinderMesh.castShadow = true;
            cylinderMesh.receiveShadow = true;

            // Create drill cylinder
            const drillGeometry = new THREE.CylinderGeometry(params.drillRadius, params.drillRadius, 10, 32);
            const drillMaterial = new THREE.MeshStandardMaterial({ color: 0x8B4513 });
            drillMesh = new THREE.Mesh(drillGeometry, drillMaterial);

            // Transform drill cylinder
            drillMesh.rotation.z = Math.PI / 2;
            drillMesh.rotation.x = params.inclination;
            drillMesh.rotation.y = params.rotation;
            drillMesh.position.y = params.offset;

            if (!params.boundaryOnly) {
                // CSG operation
                const brush1 = new Brush(cylinderMesh);
                const brush2 = new Brush(drillMesh);
                csgResult = evaluator.evaluate(brush1, brush2, SUBTRACTION);
                csgResult.material = cylinderMaterial;
                scene.add(csgResult);
            }

            // Add ghost cylinder when boundary only
            if (params.boundaryOnly) {
                const ghostMaterial = new THREE.MeshStandardMaterial({
                    color: 0xffffff,
                    transparent: true,
                    opacity: 0.15
                });
                const ghostCylinder = new THREE.Mesh(cylinderGeometry, ghostMaterial);
                scene.add(ghostCylinder);
                cylinderMesh = ghostCylinder;
            } else {
                scene.add(cylinderMesh);
            }

            // Generate boundary curve
            generateBoundaryCurve();
        }

        function generateBoundaryCurve() {
            const points = [];
            const numPoints = 100;

            for (let i = 0; i <= numPoints; i++) {
                const t = (i / numPoints) * Math.PI * 2;

                // Point on drill cylinder edge
                const x = params.drillRadius * Math.cos(t);
                const y = 0;
                const z = params.drillRadius * Math.sin(t);

                // Transform point
                const point = new THREE.Vector3(x, y, z);

                // Apply drill transformations
                point.applyAxisAngle(new THREE.Vector3(0, 0, 1), Math.PI / 2);
                point.applyAxisAngle(new THREE.Vector3(1, 0, 0), params.inclination);
                point.applyAxisAngle(new THREE.Vector3(0, 1, 0), params.rotation);
                point.y += params.offset;

                // Find intersection with main cylinder
                const ray = new THREE.Vector3(point.x, point.y, point.z);
                const rayLength = ray.length();
                const scale = params.cylinderRadius / Math.sqrt(ray.x * ray.x + ray.z * ray.z);

                const intersection1 = new THREE.Vector3(
                    ray.x * scale,
                    point.y,
                    ray.z * scale
                );

                const intersection2 = new THREE.Vector3(
                    -ray.x * scale,
                    point.y,
                    -ray.z * scale
                );

                points.push(intersection1);
                points.push(intersection2);
            }

            // Separate front and back curves
            const frontPoints = [];
            const backPoints = [];

            points.forEach(point => {
                const angle = Math.atan2(point.z, point.x);
                if (Math.sin(angle + params.rotation) > 0) {
                    frontPoints.push(point);
                } else {
                    backPoints.push(point);
                }
            });

            // Create tubes
            const tubeRadius = 0.05;
            const tubeSegments = 32;

            if (frontPoints.length > 1) {
                const frontCurve = new THREE.CatmullRomCurve3(frontPoints);
                const frontGeometry = new THREE.TubeGeometry(frontCurve, 64, tubeRadius, tubeSegments, false);
                const frontMaterial = new THREE.MeshStandardMaterial({ color: 0xFFA500 });
                boundaryCurveFront = new THREE.Mesh(frontGeometry, frontMaterial);
                scene.add(boundaryCurveFront);
            }

            if (backPoints.length > 1) {
                const backCurve = new THREE.CatmullRomCurve3(backPoints);
                const backGeometry = new THREE.TubeGeometry(backCurve, 64, tubeRadius, tubeSegments, false);
                const backMaterial = new THREE.MeshStandardMaterial({ color: 0x4169E1 });
                boundaryCurveBack = new THREE.Mesh(backGeometry, backMaterial);
                scene.add(boundaryCurveBack);
            }
        }

        function setupControls() {
            const sliders = {
                'slider-cylinder-radius': { param: 'cylinderRadius', label: 'label-cylinder-radius' },
                'slider-opacity': { param: 'opacity', label: 'label-opacity' },
                'slider-drill-radius': { param: 'drillRadius', label: 'label-drill-radius' },
                'slider-inclination': { param: 'inclination', label: 'label-inclination' },
                'slider-rotation': { param: 'rotation', label: 'label-rotation' },
                'slider-offset': { param: 'offset', label: 'label-offset' }
            };

            Object.keys(sliders).forEach(id => {
                const slider = document.getElementById(id);
                const config = sliders[id];
                slider.addEventListener('input', (e) => {
                    params[config.param] = parseFloat(e.target.value);
                    document.getElementById(config.label).textContent = parseFloat(e.target.value).toFixed(2);
                    updateScene();
                });
            });

            const checkbox = document.getElementById('checkbox-boundary-only');
            checkbox.addEventListener('change', (e) => {
                params.boundaryOnly = e.target.checked;
                updateScene();
            });

            // Initialize labels
            Object.values(sliders).forEach(config => {
                document.getElementById(config.label).textContent = params[config.param].toFixed(2);
            });
        }

        function onWindowResize() {
            const canvas = document.getElementById('main-canvas');
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Constants
        const SUBTRACTION = 0;

        init();
        animate();
    </script>
</body>
</html>
