    const span   = document.getElementById('value-' + id);
    const arrow  = document.getElementById('arrow-' + id);
    canvasWidth  = 320;   // defined below per sketch, same for all

    slider.addEventListener('input', () => {
      span.textContent = slider.value;
      arrow.style.left = (slider.value * canvasWidth) + 'px';
      updateAllBars();
    });
  });

  // Initial arrow positions
  ['hue','saturation','brightness'].forEach(id => {
    const slider = document.getElementById('slider-' + id);
    const arrow  = document.getElementById('arrow-' + id);
    arrow.style.left = (slider.value * 320) + 'px';
  });

  // Reset button handling
  document.getElementById('btn-reset').addEventListener('click', () => {
    document.getElementById('slider-hue').value       = 0;
    document.getElementById('value-hue').textContent  = 0;
    document.getElementById('slider-saturation').value = 0.5;
    document.getElementById('value-saturation').textContent = 0.5;
    document.getElementById('slider-brightness').value = 1;
    document.getElementById('value-brightness').textContent = 1;

    ['hue','saturation','brightness'].forEach(id => {
      const slider = document.getElementById('slider-' + id);
      const arrow  = document.getElementById('arrow-' + id);
      arrow.style.left = (slider.value * 320) + 'px';
    });

    updateAllBars();
  });

  // Initial draw
  updateAllBars();
}

function updateAllBars() {
  const h = parseFloat(document.getElementById('slider-hue').value);
  const s = parseFloat(document.getElementById('slider-saturation').value);
  const b = parseFloat(document.getElementById('slider-brightness').value);

  // Refresh dynamic bars (sat, bright, result)
  sats.redraw();
  brts.redraw();
  res.redraw();
}

// === p5 sketches ===
function baseSketch(p) {
  p.canvasWidth = 320;
  p.canvasHeight = 40;
  p.setup = () => {
    p.createCanvas(p.canvasWidth, p.canvasHeight);
    p.colorMode(p.HSB, 1, 1, 1);
    p.noStroke();
  };
}

function hueSketch(p) {
  baseSketch.call(p, p);
  p.draw = () => {
    p.clear();
    p.rect(0, 0, p.width, p.height, 20);
    for (let x = 0; x < p.width; x += 1) {
      p.stroke(x / p.width, 1, 1);
      p.line(x, 0, x, p.height);
    }
    // prevent further looping
    p.noLoop();
  };
}

function satSketch(p) {
  baseSketch.call(p, p);
  p.draw = () => {
    const h = parseFloat(document.getElementById('slider-hue').value);
    const b = parseFloat(document.getElementById('slider-brightness').value);
    p.clear();
    p.rect(0, 0, p.width, p.height, 20);
    for (let x = 0; x < p.width; x += 1) {
      p.stroke(h, x / p.width, b);
      p.line(x, 0, x, p.height);
    }
    p.noLoop();
  };
}

function brightSketch(p) {
  baseSketch.call(p, p);
  p.draw = () => {
    const h = parseFloat(document.getElementById('slider-hue').value);
    const s = parseFloat(document.getElementById('slider-saturation').value);
    p.clear();
    p.rect(0, 0, p.width, p.height, 20);
    for (let x = 0; x < p.width; x += 1) {
      p.stroke(h, s, x / p.width);
      p.line(x, 0, x, p.height);
    }
    p.noLoop();
  };
}

function resultSketch(p) {
  baseSketch.call(p, p);
  p.draw = () => {
    const h = parseFloat(document.getElementById('slider-hue').value);
    const s = parseFloat(document.getElementById('slider-saturation').value);
    const b = parseFloat(document.getElementById('slider-brightness').value);
    p.clear();
    p.fill(h, s, b);
    p.rect(0, 0, p.width, p.height, 20);
    p.noLoop();
  };
}
</script>
</body>
</html>
