            width: 60px;
            text-align: right;
            font-family: monospace;
        }

        #plot-container {
            height: 400px;
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-alpha">α</label>
                <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
                <span class="value-display" id="value-alpha">0.05</span>
            </div>
            <div class="control-row">
                <label for="slider-beta">β</label>
                <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
                <span class="value-display" id="value-beta">5.00</span>
            </div>
            <div class="control-row">
                <label for="slider-gamma">γ</label>
                <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
                <span class="value-display" id="value-gamma">500</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // ODE system solver
        function solveODE(alpha, beta, gamma) {
            const h = 0.05;
            const tEnd = 20;
            const n = Math.floor(tEnd / h);

            const t = new Array(n + 1);
            const dT = new Array(n + 1);
            const cX = new Array(n + 1);

            // Initial conditions
            t[0] = 0;
            dT[0] = 0;
            cX[0] = 0;

            // RK4 method
            function f1(y0, y1) {
                return gamma * y1 * Math.exp(y0 / (1 + y0 / alpha)) - beta * y0;
            }

            function f2(y0, y1) {
                return 1 - y1 * Math.exp(y0 / (1 + y0 / alpha));
            }

            for (let i = 0; i < n; i++) {
                const t0 = t[i];
                const y0 = dT[i];
                const y1 = cX[i];

                const k1_0 = h * f1(y0, y1);
                const k1_1 = h * f2(y0, y1);

                const k2_0 = h * f1(y0 + k1_0/2, y1 + k1_1/2);
                const k2_1 = h * f2(y0 + k1_0/2, y1 + k1_1/2);

                const k3_0 = h * f1(y0 + k2_0/2, y1 + k2_1/2);
                const k3_1 = h * f2(y0 + k2_0/2, y1 + k2_1/2);

                const k4_0 = h * f1(y0 + k3_0, y1 + k3_1);
                const k4_1 = h * f2(y0 + k3_0, y1 + k3_1);

                dT[i+1] = y0 + (k1_0 + 2*k2_0 + 2*k3_0 + k4_0) / 6;
                cX[i+1] = y1 + (k1_1 + 2*k2_1 + 2*k3_1 + k4_1) / 6;
                t[i+1] = t0 + h;
            }

            return { t, dT, cX };
        }

        // Update plot
        function updatePlot() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            const gamma = parseFloat(document.getElementById('slider-gamma').value);

            const data = solveODE(alpha, beta, gamma);

            const trace1 = {
                x: data.t,
                y: data.dT,
                mode: 'lines',
                line: { color: 'orange', width: 2 }
            };

            const trace2 = {
                x: data.t,
                y: data.cX.map(val => val * 1000),
                mode: 'lines',
                line: { color: 'steelblue', width: 2 }
            };

            const layout = {
                xaxis: { title: '', range: [0, 20], zeroline: true },
                yaxis: { title: '', range: [0, 100], zeroline: true },
                showlegend: false,
                margin: { l: 50, r: 20, b: 30, t: 20 },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: 'white',
                annotations: [
                    {
                        text: 'ΔT',
                        x: -0.05,
                        y: 100,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple' }
                    },
                    {
                        text: '1000 C<sub>X</sub>',
                        x: -0.05,
                        y: 95,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple' }
                    }
                ]
            };

            Plotly.newPlot('plot-container', [trace1, trace2], layout);
        }

        // Update value displays
        function updateValueDisplay(sliderId, valueId, decimals) {
            const slider = document.getElementById(sliderId);
            const display = document.getElementById(valueId);
            display.textContent = parseFloat(slider.value).toFixed(decimals);
        }

        // Event listeners
        document.getElementById('slider-alpha').addEventListener('input', (e) => {
            updateValueDisplay('slider-alpha', 'value-alpha', 4);
            updatePlot();
        });

        document.getElementById('slider-beta').addEventListener('input', (e) => {
            updateValueDisplay('slider-beta', 'value-beta', 2);
            updatePlot();
        });

        document.getElementById('slider-gamma').addEventListener('input', (e) => {
            updateValueDisplay('slider-gamma', 'value-gamma', 0);
            updatePlot();
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
