  </style>
</head>
<body>
  <div id="main-container">
    <h1>Rayleigh Wave Simulator</h1>

    <!-- Control Panel -->
    <div id="controls-container">
      <!-- Time Slider -->
      <div class="slider-group">
        <label for="slider-time">time</label>
        <input type="range" id="slider-time" min="0" max="25" value="7.0" step="0.01">
        <span id="value-time">7.00</span>
      </div>

      <!-- Wavelength Slider -->
      <div class="slider-group">
        <label for="slider-wavelength">wavelength</label>
        <input type="range" id="slider-wavelength" min="1" max="8" value="4.0" step="0.01">
        <span id="value-wavelength">4.00</span>
      </div>

      <!-- Longitudinal Amplitude Slider -->
      <div class="slider-group">
        <label for="slider-longitudinal-amplitude">longitudinal amplitude</label>
        <input type="range" id="slider-long-longitudinal-amplitude" min="0" max="3" value="2.0" step="0.01">
        <span id="value-longitudinal-amplitude">2.00</span>
      </div>

      <!-- Latitudinal Amplitude Slider -->
      <div class="slider-group">
        <label for="slider-latitudinal-amplitude">latitudinal amplitude</label>
        <input type="range" id="slider-latitudinal-amplitude" min="0" max="3" value="1.0" step="0.01">
        <span id="value-latitudinal-amplitude">1.00</span>
      </div>
    </div>

    <!-- Visualization Canvas -->
    <div id="canvas-container">
      <!-- p5.js canvas will be injected here -->
    </div>
  </div>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.0/p5.min.js"></script>
  <script>
    let time = 7.0;
    let isTweeningTime = false;
    let targetTime = 7.0;

    let collectedParams = {
        time: 7.0,
        wavelength: 4.0,
        longitudinalAmplitude: 2.0,
        latitudinalAmplitude: 1.0
    };

    // Grid definition
    const NUM_COLS = 10;
    const NUM_ROWS = 6;
    let particles = [];
    let canvasContainer;
    let offscreenGraphics; // For smooth rendering

    function setup() {
      canvasContainer = select('#canvas-container');
      let canvas = createCanvas(700, 450);
      canvas.parent(canvasContainer);

      // Initialize equilibrium positions
      const paddingX = 40;
      const paddingY = 40;
      const spacingX = (width - 2 * paddingX) / (NUM_COLS - 1);
      const spacingY = (height - 2 * paddingY) / (NUM_ROWS - 1);

      for (let r = 0; r < NUM_ROWS; r++) {
        for (let c = 0; c < NUM_COLS; c++) {
          const x0 = paddingX + c * spacingX;
          const y0 = paddingY + r * spacingY;
          particles.push({ x0, y0 });
        }
      }

      // Attach event listeners to sliders (correct IDs)
      // Time slider: handles its own value changes
      select('#slider-time').input(handleTimeSliderChange);

      // Other sliders update the shared collectedParams
      const wavelengthSlider = createSliderFromSelect('#slider-wavelength');
      wavelengthSlider.input(() => {
        collectedParams.wavelength = parseFloat(select('#slider-wavelength').value());
        updateSlideValue('wavelength');
      });

      const longAmpSlider = createSliderFromSelect('#slider-long-longitudinal-amplitude');
      longAmpSlider.input(() => {
        collectedParams.longitudinalAmplitude = parseFloat(select('#slider-long-longitudinal-amplitude').value());
        updateSlideValue('longitudinal-amplitude');
      });

      const latAmpSlider = createSliderFromSelect('#slider-latitudinal-amplitude');
      latAmpSlider.input(() => {
        collectedParams.latitudinalAmplitude = parseFloat(select('#slider-latitudinal-amplitude').value());
        updateSlideValue('latitudinal-amplitude');
      });
    }

    // Helper to wrap p5.select HTML element for consistent event handling
    function createSliderFromSelect(selector) {
        return select(selector);
    }

    function handleTimeSliderChange() {
        let currentSliderVal = parseFloat(select('#slider-time').value());
        isTweeningTime = true;
        targetTime = currentSliderVal;
        collectedParams.time = targetTime;
    }

    function updateSlideValue(sliderBaseId) {
      const slider = select(`#slider-${sliderBaseId}`);
      const valDisp = select(`#value-${sliderBaseId}`);
      valDisp.html(parseFloat(slider.value()).toFixed(2));
    }

    function mouseReleased() {
        const timeSlider = select('#slider-time');
        if (isTweeningTime && select('#slider-time').elt === document.activeElement) {
            targetTime = parseFloat(timeSlider.value());
            collectedParams.time = targetTime;
        }
        return false; // Prevent noUISlider behaviors or others
    }

    function draw() {
        // Update state
        if (!isTweeningTime) {
            time += 0.03;
            targetTime = time;
            select('#slider-time').value(time);
            select('#value-time').html(time.toFixed(2));
            collectedParams.time = time;
        } else {
            let sliderVal = parseFloat(select('#slider-time').value());
            if (abs(sliderVal - targetTime) < 0.05) {
                isTweeningTime = false;
            }
        }

        // Read current values
        const T = collectedParams.time;
        const wavelength = collectedParams.wavelength;
        const longAmpBase = collectedParams.longitudinalAmplitude;
        const latAmpBase = collectedParams.latitudinalAmplitude;

        // Clear background
        background('#E0F0FF');

        // Loop through particles
        for (let r = 0; r < NUM_ROWS; r++) {
            for (let c = 0; c < NUM_COLS; c++) {
                const idx = r * NUM_COLS + c;
                const { x0, y0 } = particles[idx];

                // Calculate wave properties
                const k = TWO_PI / wavelength;
                const ampDecay = exp(-r * (2.5 / (NUM_ROWS - 1))); // Exponential decay with depth
                const ampX = longAmpBase * ampDecay * 20; // 20 as global scale
                const ampY = latAmpBase * ampDecay * 20;
                const phase = k * (x0 - 350) - T; // Shift x0 to center for better periodicity

                // --- Draw Elliptical Paths ---
                noFill();
                stroke('#8F998F');
                strokeWeight(1);
                push();
                translate(x0, y0);
                rotate(0); // For true ellipses, no rotation needed here
                ellipse(0, 0, abs(ampX * 2), abs(ampY * 2));
                pop();

                // Calculate particle displacement (retrograde motion)
                const dx = ampX * cos(phase);
                const dy = ampY * sin(phase);
                const currentX = x0 + dx;
                const currentY = y0 + dy;

                // Store current position temporarily for next step
                particles[idx].currentX = currentX;
                particles[idx].currentY = currentY;
            }
        }

        // Draw connecting dashed lines across columns in each row
        drawingContext.setLineDash([5, 5]);
        stroke('#0000FF');
        strokeWeight(2);
        for (let r = 0; r < NUM_ROWS; r++) {
            for (let c = 0; c < NUM_COLS - 1; c++) {
                const idx1 = r * NUM_COLS + c;
                const idx2 = r * NUM_COLS + c + 1;
                line(particles[idx1].currentX, particles[idx1].currentY,
                     particles[idx2].currentX, particles[idx2].currentY);
            }
        }
        drawingContext.setLineDash([]); // Reset dashed line

        // Draw particles (red circles)
        fill('#FF0000');
        noStroke();
        for (let r = 0; r < NUM_ROWS; r++) {
            for (let c = 0; c < NUM_COLS; c++) {
                const idx = r * NUM_COLS + c;
                ellipse(particles[idx].currentX, particles[idx].currentY, 10);
            }
        }
    }
  </script>
</body>
</html>
