                <div class="row">
                    <label for="slider-c">c (°C<sup>-1</sup>)</label>
                    <input type="range" id="slider-c" min="0.01" max="0.1" step="0.001" value="0.025">
                    <span id="value-c">0.025</span>
                </div>
                <div class="row">
                    <label for="slider-b">b (°C)</label>
                    <input type="range" id="slider-b" min="200" max="350" step="0.01" value="273.16">
                    <span id="value-b">273.16</span>
                </div>
                <div class="row">
                    <label for="slider-a">a (°C)</label>
                    <input type="range" id="slider-a" min="1000" max="10000" step="1" value="4000">
                    <span id="value-a">4000</span>
                </div>
            </div>
        </div>
        <div class="right-col">
            <div id="plot-top" class="plot"></div>
            <div id="plot-bottom" class="plot"></div>
        </div>
    </div>

    <script>
        const btnArr = document.getElementById('btn-arrhenius');
        const btnExp = document.getElementById('btn-exponential');
        const sliders = {
            n: document.getElementById('slider-n'),
            tmin: document.getElementById('slider-tmin'),
            tmax: document.getElementById('slider-tmax'),
            tref: document.getElementById('slider-tref'),
            c: document.getElementById('slider-c'),
            b: document.getElementById('slider-b'),
            a: document.getElementById('slider-a')
        };
        const values = {
            n: document.getElementById('value-n'),
            tmin: document.getElementById('value-tmin'),
            tmax: document.getElementById('value-tmax'),
            tref: document.getElementById('value-tref'),
            c: document.getElementById('value-c'),
            b: document.getElementById('value-b'),
            a: document.getElementById('value-a')
        };
        const plotTop = document.getElementById('plot-top');
        const plotBottom = document.getElementById('plot-bottom');

        function getModel() {
            return btnArr.classList.contains('active-button') ? 'arrhenius' : 'exponential';
        }

        function linreg(xs, ys) {
            const n = xs.length;
            const sumX = xs.reduce((a, b) => a + b, 0);
            const sumY = ys.reduce((a, b) => a + b, 0);
            const sumXX = xs.reduce((s, x, i) => s + x * x, 0);
            const sumXY = xs.reduce((s, x, i) => s + x * ys[i], 0);
            const slope = (n * sumXY - sumX * sumY) / (n * sumXX - sumX * sumX);
            const intercept = (sumY - slope * sumX) / n;
            const ssRes = xs.reduce((s, x, i) => {
                const r = ys[i] - (slope * x + intercept);
                return s + r * r;
            }, 0);
            const meanY = sumY / n;
            const ssTot = ys.reduce((s, y) => s + (y - meanY) * (y - meanY), 0);
            const r2 = 1 - ssRes / ssTot;
            return { slope, intercept, r2 };
        }

        function updatePlots() {
            const n = +sliders.n.value;
            const tmin = +sliders.tmin.value;
            const tmax = +sliders.tmax.value;
            const tref = +sliders.tref.value;
            const c = +sliders.c.value;
            const b = +sliders.b.value;
            const a = +sliders.a.value;
            const model = getModel();
            const Ts = Array.from({ length: n }, (_, i) => tmin + i * (tmax - tmin) / (n - 1));
            let yArr = [], yExp = [];
            if (model === 'arrhenius') {
                yArr = Ts.map(T => Math.exp(-a / (T + b)) / Math.exp(-a / (tref + b)));
                yExp = Ts.map(T => Math.exp(c * (T - tref)));
            } else {
                yExp = Ts.map(T => Math.exp(c * (T - tref)));
                yArr = Ts.map(T => Math.exp(-a / (T + b)) / Math.exp(-a / (tref + b)));
            }
            let label, trace, traceFit, traceLin, tracesScatter, linXr, linYr, r2, aFit, cFit;
            if (model === 'arrhenius') {
                const xs = Ts.map(T => T - tref);
                const ys = yArr.map(y => Math.log(y));
                const { slope, r2: r } = linreg(xs, ys);
                cFit = slope; r2 = r;
                // fitted exponential curve for top plot
                const Tfine = linspace(tmin, tmax, 200);
                const yfit = Tfine.map(T => Math.exp(cFit * (T - tref)));
                // linearized plot
                linXr = xs; linYr = ys;
                // traces
                trace = { x: Ts, y: yArr, mode: 'markers', name: 'data', marker: { color: '#d62728', size: 8 } };
                traceFit = { x: Tfine, y: yfit, mode: 'lines', name: 'fit', line: { color: '#d62728' } };
                traceLin = { x: linXr, y: linYr, mode: 'markers', name: 'data', marker: { color: '#2ca02c', size: 8 } };
                tracesScatter = [
                    { x: [-50, 50], y: [0, 0], mode: 'lines', line: { color: '#000', width: 1 }, hoverinfo: 'skip', showlegend: false },
                    { x: [0, 0], y: [-6, 2], mode: 'lines', line: { color: '#000', width: 1 }, hoverinfo: 'skip', showlegend: false }
                ];
            } else {
                const xs = Ts.map(T => 1 / (T + b));
                const ys = yExp.map(y => Math.log(y));
                const { slope, r2: r } = linreg(xs, ys);
                aFit = -slope; r2 = r;
                // fitted Arrhenius curve for top plot
                const Tfine = linspace(tmin, tmax, 200);
                const yfit = Tfine.map(T => Math.exp(-aFit / (T + b)) / Math.exp(-aFit / (tref + b)));
                // linearized plot
                linXr = xs; linYr = ys;
                // traces
                trace = { x: Ts, y: yExp, mode: 'markers', name: 'data', marker: { color: '#d62728', size: 8 } };
                traceFit = { x: Tfine, y: yfit, mode: 'lines', name: 'fit', line: { color: '#d62728' } };
                traceLin = { x: linXr, y: linYr, mode: 'markers', name: 'data', marker: { color: '#1f77b4', size: 8 } };
                tracesScatter = [
                    { x: [Math.min(...linXr), Math.max(...linXr)], y: [0, 0], mode: 'lines', line: { color: '#000', width: 1 }, hoverinfo: 'skip', showlegend: false },
                    { x: [xs.reduce((s, t) => s + t, 0) / xs.length, xs.reduce((s, t) => s + t, 0) / xs.length], y: [Math.min(...linYr), Math.max(...linYr)], mode: 'lines', line: { color: '#000', width: 1 }, hoverinfo: 'skip', showlegend: false }
                ];
            }

            Plotly.newPlot(plotTop, [trace, traceFit], {
                uirevision: true,
                title: model === 'arrhenius' ? `exponential model fit to Arrhenius data<br>c = ${cFit.toFixed(4)} R² = ${r2.toFixed(3)}` : `Arrhenius model fit to exponential data<br>a = ${aFit.toFixed(0)} R² = ${r2.toFixed(3)}`,
                xaxis: { title: 'T (°C)' },
                yaxis: { title: 'k(T)/k(T_ref)' },
                margin: { l: 55, r: 20, t: 50, b: 45 }
            }, { displayModeBar: false });

            Plotly.newPlot(plotBottom, [traceLin], {
                uirevision: true,
                title: model === 'arrhenius' ? `linear fit of the exponential model<br>R² = ${r2.toFixed(3)}` : `linear fit of the Arrhenius model<br>R² = ${r2.toFixed(3)}`,
                xaxis: { title: model === 'arrhenius' ? 'T - T_ref' : '1/(T + b)' },
                yaxis: { title: 'log(k(T)/k(T_ref))' },
                margin: { l: 55, r: 20, t: 50, b: 45 },
                shapes: model === 'arrhenius' ? [
                    { type: 'line', x0: 0, x1: 0, yref: 'paper', y0: 0, y1: 1, line: { color: '#000', width: 1 } },
                    { type: 'line', y0: 0, y1: 0, xref: 'paper', x0: 0, x1: 1, line: { color: '#000', width: 1 } }
                ] : [
                    { type: 'line', x0: linXr.reduce((s, t) => s + t, 0) / linXr.length, x1: linXr.reduce((s, t) => s + t, 0) / linXr.length, yref: 'paper', y0: 0, y1: 1, line: { color: '#000', width: 1 } },
                    { type: 'line', y0: 0, y1: 0, xref: 'paper', x0: 0, x1: 1, line: { color: '#000', width: 1 } }
                ]
            }, { displayModeBar: false });
        }

        function linspace(min, max, n) {
            const arr = new Array(n);
            const step = (max - min) / (n - 1);
            for (let i = 0; i < n; i++) arr[i] = min + i * step;
            return arr;
        }

        [btnArr, btnExp].forEach(btn => {
            btn.addEventListener('click', () => {
                btnArr.classList.toggle('active-button', btn === btnArr);
                btnExp.classList.toggle('active-button', btn === btnExp);
                updatePlots();
            });
        });

        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', () => {
                const id = slider.id.replace('slider-', '');
                values[id].textContent = slider.value;
                // Constraint: tmax > tmin
                if (slider === sliders.tmin) {
                    const currTmin = +slider.value;
                    const currTmax = +sliders.tmax.value;
                    sliders.tmax.setAttribute('min', currTmin + 1);
                    if (currTmax <= currTmin) {
                        sliders.tmax.value = currTmin + 1;
                        values.tmax.textContent = sliders.tmax.value;
                    }
                }
                updatePlots();
            });
        });

        updatePlots();
    </script>
</body>
</html>
