                <label for="select-cipher">cipher:</label>
                <select id="select-cipher">
                    <option value="Caesar">Caesar</option>
                    <option value="De Vigenere">De Vigenere</option>
                    <option value="Autokey">Autokey</option>
                </select>
            </div>
