        }
        button:not(.active) {
            background: #e0e0e0;
            color: #333;
        }
        button.active {
            background: #1976d2;
            color: white;
        }
        #plot-container {
            flex: 1;
            border: 1px solid #ccc;
            border-radius: 8px;
            background: white;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-lambda">permeability ratio, λ</label>
                <input type="range" id="slider-lambda" min="-8" max="-4" step="0.01" value="-5">
                <span id="value-lambda" class="value-display">1e-5</span>
            </div>
            <div class="control-group">
                <label for="slider-omega">storativity ratio, ω</label>
                <input type="range" id="slider-omega" min="-3" max="-1" step="0.01" value="-2">
                <span id="value-omega" class="value-display">1e-2</span>
            </div>
            <div class="control-group">
                <label>option</label>
                <div class="toggle-group">
                    <button id="btn-pressure-drop" class="active">pressure drop</button>
                    <button id="btn-difference">difference</button>
                </div>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Bessel function approximations
        function besselK0(x) {
            if (x <= 2) {
                const y = x/2;
                const y2 = y*y;
                return -Math.log(y)*besselI0(x) + (-0.57721566 + 0.42278420*y2 + 0.23069756*y2*y2 + 0.03488590*y2*y2*y2 + 0.00262698*y2*y2*y2*y2 + 0.00010750*y2*y2*y2*y2*y2 + 0.00000740*y2*y2*y2*y2*y2*y2);
            } else {
                const y = 2/x;
                return Math.exp(-x)/Math.sqrt(x)*(1.25331414 - 0.07832358*y + 0.02189568*y*y - 0.01062446*y*y*y + 0.00587872*y*y*y*y - 0.00251540*y*y*y*y*y + 0.00053208*y*y*y*y*y*y);
            }
        }

        function besselI0(x) {
            const y = x/2;
            const y2 = y*y;
            return 1 + y2*(3.5156229 + y2*(3.0899424 + y2*(1.2067492 + y2*(0.2659732 + y2*(0.0360768 + y2*0.0045813)))));
        }

        function besselK1(x) {
            if (x <= 2) {
                const y = x/2;
                const y2 = y*y;
                return Math.log(y)*besselI1(x) + (1 + 0.15443144*y2 - 0.67278579*y2*y2 - 0.18156897*y2*y2*y2 - 0.01919402*y2*y2*y2*y2 - 0.00110404*y2*y2*y2*y2*y2 - 0.00004686*y2*y2*y2*y2*y2*y2)/x;
            } else {
                const y = 2/x;
                return Math.exp(-x)/Math.sqrt(x)*(1.25331414 + 0.23498619*y - 0.03655620*y*y + 0.01504268*y*y*y - 0.00780353*y*y*y*y + 0.00325614*y*y*y*y*y - 0.00068245*y*y*y*y*y*y);
            }
        }

        function besselI1(x) {
            const y = x/2;
            const y2 = y*y;
            return y*(0.5 + y2*(0.87890594 + y2*(0.51498869 + y2*(0.15084934 + y2*(0.02658733 + y2*(0.00301532 + y2*0.00032411))))));
        }

        // Stehfest algorithm for inverse Laplace transform
        function stehfest(laplaceFunc, t, n = 10) {
            const ln2 = Math.log(2);
            const coeffs = [0, 0.0833333333, -32.0833333333, 1279, -15623.6666666667, 84244.1666666667, -236957.5, 375911.6666666667, -340071.6666666667, 164062.5, -32812.5];

            let sum = 0;
            for (let k = 1; k <= n; k++) {
                const s = k * ln2 / t;
                sum += coeffs[k] * laplaceFunc(s);
            }
            return ln2 / t * sum;
        }

        // Core calculation
        function calculatePressureDrop(tau, lambda, omega) {
            const laplaceFunc = (s) => {
                const f_s = (omega * (1 - omega) * s + lambda) / ((1 - omega) * s + lambda);
                const sqrt_sf = Math.sqrt(s * f_s);
                if (sqrt_sf === 0) return 0;
                const K0 = besselK0(sqrt_sf);
                const K1 = besselK1(sqrt_sf);
                if (K1 === 0) return 0;
                return (K0 / (sqrt_sf * K1)) / s;
            };

            return stehfest(laplaceFunc, tau);
        }

        function generateTauValues() {
            const tau = [];
            for (let i = 0; i < 200; i++) {
                tau.push(Math.pow(10, i * (10 - 0) / 199));
            }
            return tau;
        }

        function updatePlot() {
            const lambdaExp = parseFloat(document.getElementById('slider-lambda').value);
            const omegaExp = parseFloat(document.getElementById('slider-omega').value);
            const lambda = Math.pow(10, lambdaExp);
            const omega = Math.pow(10, omegaExp);

            const isPressureDrop = document.getElementById('btn-pressure-drop').classList.contains('active');
            const tau = generateTauValues();

            const y = tau.map(t => {
                const dp = calculatePressureDrop(t, lambda, omega);
                return isPressureDrop ? dp : dp - 0.5 * (Math.log(t) + 0.8091);
            });

            const trace = {
                x: tau,
                y: y,
                mode: 'lines',
                line: { color: 'red', width: 2 }
            };

            const layout = {
                title: isPressureDrop ? "pressure drawdown at the wellbore" : "deviation from the asymptote",
                xaxis: {
                    title: 'dimensionless time, τ',
                    type: 'log',
                    range: [0, 10],
                    dtick: 2,
                    gridcolor: '#ddd'
                },
                yaxis: {
                    title: isPressureDrop ? 'dimensionless pressure drop, ΔP' : 'dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)',
                    range: isPressureDrop ? [0, 12] : [0, 3.5],
                    gridcolor: '#ddd'
                },
                margin: { l: 80, r: 50, b: 60, t: 60 },
                paper_bgcolor: '#fff',
                plot_bgcolor: '#fff'
            };

            Plotly.react('plot-container', [trace], layout);
        }

        // Initialize
        document.addEventListener('DOMContentLoaded', () => {
            const lambdaSlider = document.getElementById('slider-lambda');
            const omegaSlider = document.getElementById('slider-omega');
            const lambdaDisplay = document.getElementById('value-lambda');
            const omegaDisplay = document.getElementById('value-omega');
            const btnPressureDrop = document.getElementById('btn-pressure-drop');
            const btnDifference = document.getElementById('btn-difference');

            function updateDisplays() {
                const lambda = Math.pow(10, parseFloat(lambdaSlider.value));
                const omega = Math.pow(10, parseFloat(omegaSlider.value));
                lambdaDisplay.textContent = lambda < 1e-4 ? lambda.toExponential(0) : lambda.toString();
                omegaDisplay.textContent = omega < 1e-4 ? omega.toExponential(0) : omega.toString();
            }

            lambdaSlider.addEventListener('input', () => {
                updateDisplays();
                updatePlot();
            });

            omegaSlider.addEventListener('input', () => {
                updateDisplays();
                updatePlot();
            });

            btnPressureDrop.addEventListener('click', () => {
                if (!btnPressureDrop.classList.contains('active')) {
                    btnPressureDrop.classList.add('active');
                    btnDifference.classList.remove('active');
                    updatePlot();
                }
            });

            btnDifference.addEventListener('click', () => {
                if (!btnDifference.classList.contains('active')) {
                    btnDifference.classList.add('active');
                    btnPressureDrop.classList.remove('active');
                    updatePlot();
                }
            });

            updateDisplays();
            updatePlot();
        });
    </script>
</body>
</html>
