                    <th>volume</th>
                </tr>
            </thead>
            <tbody id="data-table-body"></tbody>
        </table>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let dataPoints = [
            { age: 30, volume: 289 },
            { age: 35, volume: 445 },
            { age: 40, volume: 565 },
            { age: 45, volume: 704 },
            { age: 50, volume: 833 },
            { age: 55, volume: 924 }
        ];

        let p = 58.2729;
        let u = 22.7319;
        let dragging = null;
        let canvas;

        const canvasWidth = 600;
        const canvasHeight = 500;

        function setup() {
            canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
            recalculate();
        }

        // Convert data coordinates to canvas coordinates
        function dataToCanvas(age, volume) {
            const px = map(age, 20, 80, 60, canvasWidth - 40);
            const py = map(volume, 0, 1500, canvasHeight - 60, 40);
            return { x: px, y: py };
        }
        // Convert canvas coordinates to data coordinates
        function canvasToData(x, y) {
            const age = constrain(map(x, 60, canvasWidth - 40, 20, 80), 20, 80);
            const volume = constrain(map(y, canvasHeight - 60, 40, 0, 1500), 0, 1500);
            return { age, volume };
        }

        // Draw dashed line helper
        function dashedLine(x1, y1, x2, y2, spacing) {
            stroke(180, 180, 180);
            let len = dist(x1, y1, x2, y2);
            let div = round(len / spacing);
            for (let i = 0; i < div; i++) {
                let t1 = i / div;
                let t2 = (i + 0.5) / div;
                line(lerp(x1, x2, t1), lerp(y1, y2, t1), lerp(x1, x2, t2), lerp(y1, y2, t2));
            }
        }

        function drawGrid() {
            for (let x = 30; x <= 80; x += 10) {
                let { x: px } = dataToCanvas(x, 0);
                dashedLine(px, 40, px, canvasHeight - 60, 3);
            }
            for (let y = 200; y <= 1400; y += 200) {
                let { y: py } = dataToCanvas(20, y);
                dashedLine(60, py, canvasWidth - 40, py, 3);
            }
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(1);
            line(60, canvasHeight - 60, canvasWidth - 40, canvasHeight - 60); // x axis
            line(60, 40, 60, canvasHeight - 60); // y axis

            // Ticks and labels
            fill(0);
            textSize(10);
            textAlign(CENTER, TOP);
            for (let x = 20; x <= 80; x += 10) {
                let { x: px } = dataToCanvas(x, 0);
                line(px, canvasHeight - 60, px, canvasHeight - 55);
                text(x, px, canvasHeight - 45);
            }
            textAlign(RIGHT, CENTER);
            for (let y = 0; y <= 1400; y += 200) {
                let { y: py } = dataToCanvas(20, y);
                line(55, py, 60, py);
                text(y, 50, py);
            }

            // Axis labels
            textAlign(CENTER, BOTTOM);
            text("Stand age, yr", canvasWidth / 2, canvasHeight - 10);
            push();
            translate(20, canvasHeight / 2);
            rotate(-HALF_PI);
            textAlign(CENTER, BOTTOM);
            text("Wood volume, m³ ha⁻¹", 0, 0);
            pop();

            // 4/5 law label
            push();
            translate(canvasWidth - 25, canvasHeight / 2);
            rotate(-HALF_PI);
            textAlign(CENTER, CENTER);
            textSize(14);
            fill(0, 0, 200);
            text("4/5 law", 0, 0);
            pop();
        }

        function drawCurve() {
            stroke(0, 0, 205);
            strokeWeight(2);
            noFill();
            beginShape();
            let minAge = max(u + 0.1, 20);
            for (let x = minAge; x <= 80; x += 0.5) {
                let y = p * Math.pow(x - u, 4/5);
                if (y < 1500) {
                    let pos = dataToCanvas(x, y);
                    vertex(pos.x, pos.y);
                }
            }
            endShape();
        }

        function drawPoints() {
            dataPoints.forEach(pt => {
                let { x, y } = dataToCanvas(pt.age, pt.volume);
                fill(0, 0, 205);
                stroke(0);
                ellipse(x, y, 8, 8);
            });
        }

        function findPointAt(px, py) {
            return dataPoints.find(pt => {
                let pos = dataToCanvas(pt.age, pt.volume);
                return dist(pos.x, pos.y, px, py) < 10;
            });
        }

        function mousePressed() {
            let pt = findPointAt(mouseX, mouseY);
            if (pt) dragging = pt;
        }

        function mouseDragged() {
            if (dragging) {
                let { age, volume } = canvasToData(mouseX, mouseY);
                dragging.age = age;
                dragging.volume = volume;
                recalculate();
            }
        }

        function mouseReleased() {
            dragging = null;
        }

        function mouseClicked() {
            if (!findPointAt(mouseX, mouseY)) {
                let d = canvasToData(mouseX, mouseY);
                dataPoints.push(d);
                recalculate();
            }
        }

        function doubleClicked() {
            let pt = findPointAt(mouseX, mouseY);
            if (pt) {
                dataPoints = dataPoints.filter(p => p !== pt);
                recalculate();
            }
        }

        function recalculate() {
            // Fit nonlinear model: volume = p*(age - u)^(4/5)
            // Gradient descent for p and u
            const learningRate = 0.0001;
            const iterations = 1000;
            // Initial guess
            p = 50;
            u = 20;
            const epsilon = 1e-3;
            const minAge = Math.min(...dataPoints.map(pt => pt.age)) - 0.1;

            for (let i = 0; i < iterations; i++) {
                let sum_dp = 0, sum_du = 0, E = 0;
                for (let {age, volume} of dataPoints) {
                    const x = age - u;
                    if (x <= 0) continue;
                    const x43 = Math.pow(x, 4/5);
                    const yhat = p * x43;
                    const error = volume - yhat;
                    sum_dp += -2 * error * x43;
                    sum_du += 2 * error * p * 0.8 * Math.pow(x, -1/5);
                }
                const prevE = E;
                p -= learningRate * sum_dp;
                u -= learningRate * sum_du;
                u = constrain(u, -Infinity, minAge - epsilon);
            }

            // Update UI
            document.getElementById('results-u').textContent = u.toFixed(4);
            document.getElementById('results-p').textContent = p.toFixed(4);

            const tbody = document.getElementById('data-table-body');
            tbody.innerHTML = '';
            dataPoints.forEach(pt => {
                const tr = document.createElement('tr');
                tr.innerHTML = `<td>${pt.age.toFixed(1)}</td><td>${pt.volume.toFixed(1)}</td>`;
                tbody.appendChild(tr);
            });
        }

        function draw() {
            background(255, 255, 224);
            drawGrid();
            drawAxes();
            drawCurve();
            drawPoints();
        }
    </script>
</body>
</html>
