<body>
    <div id="demo-container">
        <div id="controls">
            <label for="angle-slider">vertices sorted by angle</label>
            <input type="range" id="angle-slider" min="0" max="38" step="1" value="3">
            <span id="angle-value">3</span>
            <button id="reset-button">+</button>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let canvas;
        let sourcePoint = { x: 275, y: 275 };
        let isDragging = false;

        // Polygon data
        const outerPolygon = [[50, 50], [550, 50], [550, 550], [50, 550]];
        const holes = [
            [[100, 100], [250, 100], [250, 150], [200, 150], [200, 200], [100, 200]],
            [[300, 100], [450, 100], [450, 250], [400, 250], [400, 200], [300, 200]],
            [[100, 350], [250, 350], [250, 500], [200, 500], [200, 450], [100, 450]],
            [[300, 400], [450, 400], [450, 500], [300, 500]],
            [[300, 300], [350, 300], [350, 350], [300, 350]]
        ];

        let allVertices = [];
        let allEdges = [];
        let sortedVertices = [];

        function setup() {
            canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');

            // Prepare geometry
            prepareGeometry();

            // Event listeners
            const slider = document.getElementById('angle-slider');
            const angleValue = document.getElementById('angle-value');

            slider.addEventListener('input', function() {
                angleValue.textContent = this.value;
                updateVisibility();
            });

            document.getElementById('reset-button').addEventListener('click', resetDemo);
        }

        function prepareGeometry() {
            // Collect all vertices
            allVertices = [...outerPolygon];
            holes.forEach(hole => allVertices.push(...hole));

            // Collect all edges as [x1,y1,x2,y2] pairs
            allEdges = [];

            // Outer polygon edges
            for (let i = 0; i < outerPolygon.length; i++) {
                const j = (i + 1) % outerPolygon.length;
                allEdges.push([...outerPolygon[i], ...outerPolygon[j]]);
            }

            // Hole edges
            holes.forEach(hole => {
                for (let i = 0; i < hole.length; i++) {
                    const j = (i + 1) % hole.length;
                    allEdges.push([...hole[i], ...hole[j]]);
                }
            });
        }

        function sortVertices() {
            sortedVertices = [];
            for (let i = 0; i < allVertices.length; i++) {
                const v = allVertices[i];
                const angle = atan2(v[1] - sourcePoint.y, v[0] - sourcePoint.x);
                sortedVertices.push({ v, angle, idx: i });
            }
            sortedVertices.sort((a, b) => a.angle - b.angle);
        }

        function lineIntersection(x1, y1, x2, y2, x3, y3, x4, y4) {
            const den = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4);
            if (Math.abs(den) < 1e-10) return null;

            const t = ((x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)) / den;
            const u = -((x1 - x2) * (y1 - y3) - (y1 - y2) * (x1 - x3)) / den;

            if (t >= 0 && t <= 1 && u >= 0 && u <= 1) {
                return {
                    x: x1 + t * (x2 - x1),
                    y: y1 + t * (y2 - y1)
                };
            }
            return null;
        }

        function raySegmentIntersection(ray, seg) {
            const x1 = sourcePoint.x;
            const y1 = sourcePoint.y;
            const x2 = ray.x;
            const y2 = ray.y;
            const x3 = seg.x1;
            const y3 = seg.y1;
            const x4 = seg.x2;
            const y4 = seg.y2;

            const den = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4);
            if (Math.abs(den) < 1e-10) return null;

            const t = ((x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)) / den;
            const u = ((x1 - x3) * (y1 - y2) - (y1 - y3) * (x1 - x2)) / den;

            if (t >= 0 && u > 0 && u < 1) {
                return {
                    x: x1 + t * (x2 - x1),
                    y: y1 + t * (y2 - y1)
                };
            }
            return null;
        }

        function calculateVisibility(upTo = 38) {
            const visibility = [];

            for (let k = 0; k < Math.min(upTo, sortedVertices.length); k++) {
                const { v: vertex } = sortedVertices[k];

                // Find closest intersection
                let closestDist = Infinity;
                let closestPoint = null;

                for (const edge of allEdges) {
                    const seg = {
                        x1: edge[0], y1: edge[1],
                        x2: edge[2], y2: edge[3]
                    };

                    // Skip if ray is collinear with edge
                    const edgeVector = createVector(seg.x2 - seg.x1, seg.y2 - seg.y1);
                    const rayVector = createVector(vertex[0] - sourcePoint.x, vertex[1] - sourcePoint.y);

                    if (abs(edgeVector.angleBetween(rayVector)) < 0.001 || 
                        abs(edgeVector.angleBetween(rayVector) - PI) < 0.001) continue;

                    const info = raySegmentIntersection(
                        { x: vertex[0], y: vertex[1] },
                        seg
                    );

                    if (info) {
                        const dist = dist(sourcePoint.x, sourcePoint.y, info.x, info.y);
                        if (dist < closestDist) {
                            closestDist = dist;
                            closestPoint = info;
                        }
                    }
                }

                if (closestPoint) {
                    visibility.push(closestPoint);
                }
            }

            return visibility;
        }

        function draw() {
            background(255);

            // Draw polygon
            noStroke();
            fill(255);
            beginShape();
            for (let i = 0; i < outerPolygon.length; i++) {
                vertex(outerPolygon[i][0], outerPolygon[i][1]);
            }
            beginContour();
            for (let i = holes[0].length - 1; i >= 0; i--) {
                vertex(holes[0][i][0], holes[0][i][1]);
            }
            endContour();
            beginContour();
            for (let i = holes[1].length - 1; i >= 0; i--) {
                vertex(holes[1][i][0], holes[1][i][1]);
            }
            endContour();
            beginContour();
            for (let i = holes[2].length - 1; i >= 0; i--) {
                vertex(holes[2][i][0], holes[2][i][1]);
            }
            endContour();
            beginContour();
            for (let i = holes[3].length - 1; i >= 0; i--) {
                vertex(holes[3][i][0], holes[3][i][1]);
            }
            endContour();
            beginContour();
            for (let i = holes[4].length - 1; i >= 0; i--) {
                vertex(holes[4][i][0], holes[4][i][1]);
            }
            endContour();
            endShape(CLOSE);
            stroke('#AAAAAA');
            strokeWeight(1);
            noFill();
            beginShape();
            for (let i = 0; i < outerPolygon.length; i++) {
                vertex(outerPolygon[i][0], outerPolygon[i][1]);
            }
            endShape(CLOSE);
            holes.forEach(hole => {
                beginShape();
                for (let i = 0; i < hole.length; i++) {
                    vertex(hole[i][0], hole[i][1]);
                }
                endShape(CLOSE);
            });

            // Draw vertices
            noStroke();
            fill('#FF0000');
            allVertices.forEach(v => ellipse(v[0], v[1], 4));

            // Draw source point
            fill('#0000FF');
            ellipse(sourcePoint.x, sourcePoint.y, 8);
            noFill();
            stroke('#0000FF');
            ellipse(sourcePoint.x, sourcePoint.y, 12);

            sortVertices();
            const sliderValue = parseInt(document.getElementById('angle-slider').value);

            if (sliderValue < 38) {
                // Draw committed visibility
                const committed = calculateVisibility(sliderValue - 1);
                if (committed.length > 2) {
                    noStroke();
                    fill(255, 255, 153, 200);
                    beginShape();
                    committed.forEach(p => vertex(p.x, p.y));
                    endShape(CLOSE);
                }

                // Draw active triangle
                if (sliderValue > 0 && sortedVertices[sliderValue - 1]) {
                    const { v: activeVertex } = sortedVertices[sliderValue - 1];

                    // Find intersections for active rays
                    let points = [];

                    // Ray to current vertex
                    let closestPoint = null;
                    let closestDist = Infinity;
                    for (const edge of allEdges) {
                        const seg = {
                            x1: edge[0], y1: edge[1],
                            x2: edge[2], y2: edge[3]
                        };

                        const info = raySegmentIntersection(
                            { x: activeVertex[0], y: activeVertex[1] },
                            seg
                        );

                        if (info) {
                            const dist = dist(sourcePoint.x, sourcePoint.y, info.x, info.y);
                            if (dist < closestDist) {
                                closestDist = dist;
                                closestPoint = info;
                            }
                        }
                    }

                    if (closestPoint) {
                        points.push(closestPoint);
                    }

                    // Draw active triangle
                    noFill();
                    stroke('#0000FF');
                    strokeWeight(1);
                    line(sourcePoint.x, sourcePoint.y, activeVertex[0], activeVertex[1]);

                    noStroke();
                    fill(255, 204, 102, 150);
                    beginShape();
                    vertex(sourcePoint.x, sourcePoint.y);
                    points.forEach(p => vertex(p.x, p.y));
                    endShape(CLOSE);

                    // Draw sweep arc
                    noFill();
                    stroke('#0000FF');
                    strokeWeight(2);
                    if (sliderValue > 1) {
                        const startAngle = sortedVertices[0].angle;
                        const endAngle = sortedVertices[sliderValue - 1].angle;
                        arc(sourcePoint.x, sourcePoint.y, 50, 50, startAngle, endAngle);
                    }

                    // Highlight active edge
                    stroke('#00CC00');
                    strokeWeight(3);
                    // Complexity simplified - just draw the edge being processed
                }
            } else {
                // Draw final visibility polygon
                const final = calculateVisibility();
                if (final.length > 2) {
                    noStroke();
                    fill(255, 204, 102, 200);
                    beginShape();
                    final.forEach(p => vertex(p.x, p.y));
                    endShape(CLOSE);
                }
            }
        }

        function mousePressed() {
            const d = dist(mouseX, mouseY, sourcePoint.x, sourcePoint.y);
            if (d < 15 && mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging && mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height) {
                sourcePoint.x = mouseX;
                sourcePoint.y = mouseY;
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function resetDemo() {
            sourcePoint = { x: 275, y: 275 };
            document.getElementById('angle-slider').value = 3;
            document.getElementById('angle-value').textContent = '3';
            updateVisibility();
        }

        function updateVisibility() {
            redraw();
        }
    </script>
</body>
</html>
