<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Power-Series vs Numerical ODE Solution</title>
    <script src="https://cdn.plot.ly/plotly-2.26.0.min.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            background: #f9f9f9;
        }

        #main-container {
            display: flex;
            height: 100vh;
        }

        #control-panel {
            width: 300px;
            padding: 20px;
            background: #fff;
            box-shadow: 2px 0 4px rgba(0,0,0,0.1);
            display: flex;
            flex-direction: column;
            gap: 20px;
        }

        #control-panel label {
            font-weight: bold;
            margin-bottom: 5px;
            display: block;
        }

        #control-panel input[type=range] {
            width: 100%;
            margin: 5px 0;
        }

        #visualization-area {
            flex-grow: 1;
            padding: 20px;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
        }

        #plot-container {
            width: 100%;
            height: 100%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-order">expansion order</label>
                <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
                <span id="value-order">5</span>
            </div>
            <div>
                <label for="slider-a">f(0)</label>
                <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
                <span id="value-a">2.0</span>
            </div>
            <div>
                <label for="slider-b">f'(0)</label>
                <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
                <span id="value-b">0.0</span>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Slider elements
        const sliderOrder = document.getElementById('slider-order');
        const sliderA = document.getElementById('slider-a');
        const sliderB = document.getElementById('slider-b');

        const valueOrder = document.getElementById('value-order');
        const valueA = document.getElementById('value-a');
        const valueB = document.getElementById('value-b');

        // Global variables for data
        let traceRed = {
            x: [],
            y: [],
            type: 'scatter',
            mode: 'lines',
            line: { color: 'red', width: 2 },
            name: 'Power Series'
        };

        let traceGreen = {
            x: [],
            y: [],
            type: 'scatter',
            mode: 'lines',
            line: { color: 'green', width: 3 },
            name: 'Numerical RK4'
        };

        let layout = {
            title: '',
            xaxis: {
                title: 'x',
                range: [0, 1.5],
                zeroline: false
            },
            yaxis: {
                title: 'f(x)',
                range: [0, 4],
                zeroline: false
            },
            margin: { l: 50, r: 20, t: 50, b: 40 },
            shapes: []
        };

        Plotly.newPlot('plot-container', [traceRed, traceGreen], layout);

        // Helper for factorial
        function factorial(n) {
            if (n === 0 || n === 1) return 1;
            let res = 1;
            for (let i = 2; i <= n; i++) res *= i;
            return res;
        }

        // Runge-Kutta 4th order solver
        function rk4Step(f, y, x, h) {
            const k1 = f(y, x);
            const k2 = f(y.map((val, i) => val + k1[i] * h / 2), x + h / 2);
            const k3 = f(y.map((val, i) => val + k2[i] * h / 2), x + h / 2);
            const k4 = f(y.map((val, i) => val + k3[i] * h), x + h);
            return y.map((val, i) => val + (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]) * h / 6);
        }

        // Generate polynomial title
        function getPolynomialTitle(cs, order) {
            let terms = [];
            for (let k = 0; k <= order; k++) {
                if (Math.abs(cs[k]) < 1e-8) continue;
                let coeff = cs[k].toFixed(5);
                if (k === 0) {
                    terms.push(coeff);
                } else {
                    let term =
                        (cs[k] < 0 ? '- ' : terms.length ? '+ ') +
                        Math.abs(parseFloat(coeff)).toString();
                    if (k === 1) term += ' x';
                    else term += ` x^${k}`;
                    terms.push(term);
                }
            }
            let res = terms.join(' ');
            res = res.replace(/- /g, ' - ');
            res = res.replace(/\+ /g, ' + ');
            res = res.replace(/([+-]?\d+\.?\d*)\s*x\^0/g, '$1');
            res = res.replace(/\+\s*-\s*/g, '- ');
            res = res.replace(/^([+-])\s*/, '$1');
            if (!res.trim()) res = '0';
            return 'f(x) = ' + res.trim();
        }

        // Update plot and data
        function updatePlot() {
            const order = parseInt(sliderOrder.value);
            const a = parseFloat(sliderA.value);
            const b = parseFloat(sliderB.value);

            // Update value displays
            valueOrder.textContent = order;
            valueA.textContent = a.toFixed(1);
            valueB.textContent = b.toFixed(1);

            // Calculate derivatives and coefficients
            const f_derivs = new Array(6);
            f_derivs[0] = a;
            f_derivs[1] = b;
            f_derivs[2] = -(f_derivs[1] + 1.5 * f_derivs[0]) / a;
            f_derivs[3] = -((f_derivs[2] * f_derivs[1]) + f_derivs[2] + 1.5 * f_derivs[1]) / a;
            f_derivs[4] = -((2 * f_derivs[3] * f_derivs[1]) + (f_derivs[2] ** 2) + f_derivs[3] + 1.5 * f_derivs[2]) / a;
            f_derivs[5] = -((3 * f_derivs[4] * f_derivs[1]) + (4 * f_derivs[3] * f_derivs[2]) + f_derivs[4] + 1.5 * f_derivs[3]) / a;

            const cs = [];
            for (let k = 0; k <= 5; k++) {
                cs.push(f_derivs[k] / factorial(k));
            }

            // Power series data
            const xs = [];
            const ysPowerSeries = [];
            for (let x = 0; x <= 1.5; x += 0.0075) {
                xs.push(x);
                let sum = 0;
                for (let k = 0; k <= order; k++) {
                    sum += cs[k] * (x ** k);
                }
                ysPowerSeries.push(sum);
            }

            // Numerical solution
            const ysNumerical = [];
            const h = 0.0075;
            const y0 = [a, b];
            let x = 0;
            let y = [...y0];

            function fode(yAy, xAy) {
                const [y1, y2] = yAy;
                return [y2, -(y2 + 1.5 * y1) / y1];
            }

            for (let i = 0; i <= xs.length - 1; i++) {
                ysNumerical.push(Math.max(y[0], 0.001));
                if (i < xs.length - 1) {
                    y = rk4Step(fode, y, x, h);
                    x += h;
                }
            }

            // Find agreement region
            let x_limit = 1.5;
            for (let i = 0; i < xs.length; i++) {
                if (Math.abs(ysPowerSeries[i] - ysNumerical[i]) >= 0.01) {
                    x_limit = xs[i];
                    break;
                }
            }

            // New plot data and layout
            traceRed.x = xs;
            traceRed.y = ysPowerSeries;
            traceGreen.x = xs;
            traceGreen.y = ysNumerical;

            layout.title = getPolynomialTitle(cs, order);
            layout.shapes = [{
                type: 'rect',
                xref: 'x',
                yref: 'paper',
                x0: 0,
                y0: 0,
                x1: x_limit,
                y1: 1,
                fillcolor: 'lightblue',
                opacity: 0.4,
                layer: 'below',
                line: { width: 0 }
            }];

            Plotly.react('plot-container', [traceRed, traceGreen], layout);
        }

        // Initial plot
        updatePlot();

        // Event listeners
        sliderOrder.oninput = updatePlot;
        sliderA.oninput = updatePlot;
        sliderB.oninput = updatePlot;
    </script>
</body>
</html>
