        }
        #controls button {
            margin: 0 4px;
            padding: 6px 10px;
            border: 1px solid #888;
            background: #f0f0f0;
            cursor: pointer;
            border-radius: 2px;
        }
        #controls button.active {
            background: #4CAF50;
            color: #fff;
        }
        #view-controls {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        #canvas-container {
            flex: 1;
            position: relative;
            background: #fff;
        }
        #visualization-canvas {
            display: block;
            width: 100%;
            height: 100%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls">
            <div id="text-control">
                <label for="select-text">text</label>
                <select id="select-text">
                    <option>To Be Or Not To Be</option>
                    <option>The quick brown fox jumps over the lazy dog</option>
                    <option>It was the best of times, it was the worst of times</option>
                </select>
            </div>
            <div id="view-controls">
                <label>plot</label>
                <button id="btn-plot-2d" class="active">distribution 2D</button>
                <button id="btn-plot-3d">keyboard 3D</button>
                <label>keyboard</label>
                <button id="btn-keyboard-qwerty" class="active">QWERTY</button>
                <button id="btn-keyboard-dvorak">Dvorak</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="visualization-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        // Texts
        const texts = {
            "To Be Or Not To Be": "To Be Or Not To Be",
            "The quick brown fox jumps over the lazy dog": "The quick brown fox jumps over the lazy dog",
            "It was the best of times, it was the worst of times": "It was the best of times, it was the worst of times"
        };

        // Keyboard layouts
        const qwerty = {
            top: "QWERTYUIOP",
            home: "ASDFGHJKL",
            bottom: "ZXCVBNM"
        };
        const dvorak = {
            top: "PYFGCRL",
            home: "AOEUIDHTNS",
            bottom: "QJKXBMWVZ"
        };

        // State
        let freq = {};
        let maxFreq = 0;
        let scene, camera, renderer;
        let currentView = "2d"; // "2d" or "3d"
        let currentKeyboard = "qwerty"; // "qwerty" or "dvorak"
        let bars = [];
        let keys = [];
        let labels = [];

        // Three.js setup
        function initScene() {
            const canvas = document.getElementById('visualization-canvas');
            renderer = new THREE.WebGLRenderer({canvas: canvas, antialias: true});
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
            renderer.setClearColor(0xffffff);

            scene = new THREE.Scene();
            scene.add(new THREE.AmbientLight(0xffffff, 0.6));
            const dirLight = new THREE.DirectionalLight(0xffffff, 0.5);
            dirLight.position.set(10, 20, 10);
            scene.add(dirLight);

            // Initial draw
            updateFrequency();
            drawScene();

            // Resize
            window.addEventListener('resize', () => {
                renderer.setSize(canvas.clientWidth, canvas.clientHeight);
                updateCameraAspect();
                renderer.render(scene, camera);
            });
        }

        function updateCameraAspect() {
            const canvas = document.getElementById('visualization-canvas');
            if (currentView === "2d") {
                const w = canvas.clientWidth;
                const h = canvas.clientHeight;
                camera.left = -w/2;
                camera.right = w/2;
                camera.top = h/2;
                camera.bottom = -h/2;
                camera.updateProjectionMatrix();
            } else {
                camera.aspect = canvas.clientWidth / canvas.clientHeight;
                camera.updateProjectionMatrix();
            }
        }

        function updateFrequency() {
            const text = texts[document.getElementById('select-text').value].toUpperCase();
            freq = {};
            for (let ch of text) {
                if (ch >= 'A' && ch <= 'Z') {
                    freq[ch] = (freq[ch] || 0) + 1;
                }
            }
            // Fill missing letters with 0
            for (let i = 65; i <= 90; i++) {
                const letter = String.fromCharCode(i);
                if (!freq.hasOwnProperty(letter)) freq[letter] = 0;
            }
            maxFreq = Math.max(...Object.values(freq));
            // Round up max for axis
            maxFreq = Math.ceil(maxFreq / 50) * 50 || 50;
        }

        function drawScene() {
            // Clear previous objects
            bars.forEach(obj => scene.remove(obj));
            keys.forEach(obj => scene.remove(obj));
            labels.forEach(obj => scene.remove(obj));
            bars = [];
            keys = [];
            labels = [];

            const canvas = document.getElementById('visualization-canvas');
            if (currentView === "2d") {
                camera = new THREE.OrthographicCamera(
                    -canvas.clientWidth / 2,
                    canvas.clientWidth / 2,
                    canvas.clientHeight / 2,
                    -canvas.clientHeight / 2,
                    0.1, 1000
                );
                camera.position.set(0, 0, 100);
                camera.lookAt(0, 0, 0);
                draw2D();
            } else {
                camera = new THREE.PerspectiveCamera(
                    45,
                    canvas.clientWidth / canvas.clientHeight,
                    0.1, 10000
                );
                camera.position.set(0, 300, 400);
                camera.lookAt(0, 0, 0);
                draw3D();
            }
            scene.add(camera);
            renderer.render(scene, camera);
        }

        function draw2D() {
            const barWidth = 24;
            const spacing = 4;
            const startX = -(26 * (barWidth + spacing)) / 2;
            const letters = Object.keys(freq).sort();

            // Draw axes and grid
            const axisY = maxFreq;
            const ySeg = maxFreq / 4;
            for (let i = 0; i <= 4; i++) {
                const y = -i * ySeg;
                const points = [
                    new THREE.Vector3(startX - 20, y, 0),
                    new THREE.Vector3(startX + 26 * (barWidth + spacing), y, 0)
                ];
                const geom = new THREE.BufferGeometry().setFromPoints(points);
                const mat = new THREE.LineBasicMaterial({color: 0xcccccc});
                const line = new THREE.Line(geom, mat);
                scene.add(line);
                bars.push(line);

                // Label
                const label = createTextSprite(Math.round(i * ySeg).toString(), {fontSize: 14});
                label.position.set(startX - 40, y, 0);
                scene.add(label);
                labels.push(label);
            }

            // Draw bars
            letters.forEach((letter, idx) => {
                const h = freq[letter];
                const barHeight = (h / maxFreq) * (4 * ySeg);
                const geom = new THREE.BoxGeometry(barWidth, barHeight, 10);
                const color = getColorByFrequency(h / maxFreq);
                const mat = new THREE.MeshStandardMaterial({color: color});
                const bar = new THREE.Mesh(geom, mat);
                bar.position.set(startX + idx * (barWidth + spacing), barHeight / 2 - maxFreq, 0);
                scene.add(bar);
                bars.push(bar);

                // Label
                const label = createTextSprite(letter, {fontSize: 18});
                label.position.set(bar.position.x, -maxFreq - 20, 0);
                scene.add(label);
                labels.push(label);
            });
        }

        function draw3D() {
            const layout = currentKeyboard === "qwerty" ? qwerty : dvorak;
            const keyRadius = 16;
            const keySpacing = 50;
            const baseY = 0;

            // Helper to get row color
            function getRowColor(row) {
                if (row === 'top') return 0x9ACD32; // olive green
                if (row === 'home') return 0x4682B4; // steel blue
                return 0x9370DB; // medium purple
            }

            // Draw keys
            function drawRow(rowKey, rowName, startX, z) {
                for (let i = 0; i < rowKey.length; i++) {
                    const letter = rowKey[i];
                    const h = freq[letter];
                    const keyHeight = Math.max(2, (h / maxFreq) * 200);
                    const geom = new THREE.CylinderGeometry(keyRadius, keyRadius, keyHeight, 32);
                    const mat = new THREE.MeshStandardMaterial({color: getRowColor(rowName)});
                    const key = new THREE.Mesh(geom, mat);
                    key.position.set(startX + i * keySpacing, keyHeight / 2 + baseY, z);
                    scene.add(key);
                    keys.push(key);

                    // Label
                    const label = createTextSprite(letter, {fontSize: 20});
                    label.position.set(key.position.x, key.position.y + keyHeight / 2 + 8, key.position.z);
                    scene.add(label);
                    labels.push(label);
                }
            }

            const rows = ['top', 'home', 'bottom'];
            const offsets = {
                top: {x: -((layout.top.length - 1) * keySpacing) / 2, z: -keySpacing},
                home: {x: -((layout.home.length - 1) * keySpacing) / 2, z: 0},
                bottom: {x: -((layout.bottom.length - 1) * keySpacing) / 2, z: keySpacing}
            };

            drawRow(layout.top, 'top', offsets.top.x, offsets.top.z);
            drawRow(layout.home, 'home', offsets.home.x, offsets.home.z);
            drawRow(layout.bottom, 'bottom', offsets.bottom.x, offsets.bottom.z);
        }

        function getColorByFrequency(t) {
            // Gradient: blue -> purple -> orange -> red
            if (t < 0.33) {
                // blue to purple
                const s = t / 0.33;
                return new THREE.Color().lerpColors(new THREE.Color(0x0000ff), new THREE.Color(0x800080), s);
            } else if (t < 0.66) {
                // purple to orange
                const s = (t - 0.33) / 0.33;
                return new THREE.Color().lerpColors(new THREE.Color(0x800080), new THREE.Color(0xffa500), s);
            } else {
                // orange to red
                const s = (t - 0.66) / 0.34;
                return new THREE.Color().lerpColors(new THREE.Color(0xffa500), new THREE.Color(0xff0000), s);
            }
        }

        function createTextSprite(text, opts) {
            opts = opts || {};
            const fontSize = opts.fontSize || 20;
            const canvas = document.createElement('canvas');
            const ctx = canvas.getContext('2d');
            ctx.font = fontSize + "px Arial";
            const metrics = ctx.measureText(text);
            const textWidth = metrics.width;
            canvas.width = textWidth + 10;
            canvas.height = fontSize + 10;
            ctx.font = fontSize + "px Arial";
            ctx.fillStyle = "#000";
            ctx.fillText(text, 5, fontSize);
            const texture = new THREE.CanvasTexture(canvas);
            const spriteMat = new THREE.SpriteMaterial({map: texture});
            const sprite = new THREE.Sprite(spriteMat);
            sprite.scale.set(canvas.width, canvas.height, 1);
            return sprite;
        }

        // Controls
        document.getElementById('select-text').addEventListener('change', () => {
            updateFrequency();
            drawScene();
        });

        document.getElementById('btn-plot-2d').addEventListener('click', () => {
            if (currentView === "2d") return;
            setActiveButton('btn-plot-2d', 'btn-plot-3d');
            currentView = "2d";
            drawScene();
        });

        document.getElementById('btn-plot-3d').addEventListener('click', () => {
            if (currentView === "3d") return;
            setActiveButton('btn-plot-3d', 'btn-plot-2d');
            currentView = "3d";
            drawScene();
        });

        document.getElementById('btn-keyboard-qwerty').addEventListener('click', () => {
            setActiveButton('btn-keyboard-qwerty', 'btn-keyboard-dvorak');
            currentKeyboard = "qwerty";
            if (currentView === "3d") {
                drawScene();
            }
        });

        document.getElementById('btn-keyboard-dvorak').addEventListener('click', () => {
            setActiveButton('btn-keyboard-dvorak', 'btn-keyboard-qwerty');
            currentKeyboard = "dvorak";
            if (currentView === "3d") {
                drawScene();
            }
        });

        function setActiveButton(activeId, inactiveId) {
            document.getElementById(activeId).classList.add('active');
            document.getElementById(inactiveId).classList.remove('active');
        }

        // Init
        initScene();
    </script>
</body>
</html>
