    <script>
        let particles = [];
        let isPlaying = false;
        let canvas;

        // Default values
        const defaults = {
            time: 0,
            trail: 5,
            points: 50,
            p_amp: 0.2,
            p_wl: 3,
            s_amp: 0.1,
            s_wl: 1
        };

        function setup() {
            canvas = createCanvas(800, 400);
            canvas.parent('canvas-container');
            initializeParticles();
            setupControls();
        }

        function draw() {
            background(255);

            if (isPlaying) {
                let timeSlider = document.getElementById('slider-time');
                let currentTime = parseFloat(timeSlider.value);
                currentTime += 0.01;
                if (currentTime > 5) currentTime = 0;
                timeSlider.value = currentTime;
                document.getElementById('label-time').textContent = currentTime.toFixed(2);
            }

            // Get current values
            const time = parseFloat(document.getElementById('slider-time').value);
            const p_amp = parseFloat(document.getElementById('slider-p-amp').value);
            const p_wl = parseFloat(document.getElementById('slider-p-wl').value);
            const s_amp = parseFloat(document.getElementById('slider-s-amp').value);
            const s_wl = parseFloat(document.getElementById('slider-s-wl').value);
            const trailLength = parseInt(document.getElementById('slider-trail').value);

            // Draw axes
            stroke(200);
            strokeWeight(1);
            line(50, height/2, width - 50, height/2);
            line(50, 50, 50, height - 50);

            // Draw ticks
            for (let i = 0; i <= 5; i++) {
                let x = map(i, 0, 5, 50, width - 50);
                line(x, height/2 - 5, x, height/2 + 5);
                fill(0);
                noStroke();
                text(i, x - 5, height/2 + 20);
            }

            for (let i = -0.4; i <= 0.4; i += 0.2) {
                let y = map(i, -0.5, 0.5, height - 50, 50);
                line(45, y, 55, y);
                fill(0);
                noStroke();
                text(i.toFixed(1), 20, y + 5);
            }

            // Update and draw particles
            particles.forEach(particle => {
                // Calculate displacements
                const dx = p_amp * sin(TWO_PI * (particle.x0 / p_wl - time));
                const dy = s_amp * sin(TWO_PI * (particle.x0 / s_wl - time));

                const x_final = map(particle.x0 + dx, 0, 5, 50, width - 50);
                const y_final = map(dy, -0.5, 0.5, height - 50, 50);

                // Add to history
                particle.history.push({x: x_final, y: y_final});
                if (particle.history.length > trailLength) {
                    particle.history.shift();
                }

                // Draw trail
                if (trailLength > 0) {
                    noStroke();
                    for (let i = 0; i < particle.history.length; i++) {
                        const alpha = map(i, 0, particle.history.length - 1, 0, 255);
                        fill(106, 90, 205, alpha * 0.3);
                        ellipse(particle.history[i].x, particle.history[i].y, 3, 3);
                    }
                }

                // Draw particle
                fill(106, 90, 205);
                noStroke();
                ellipse(x_final, y_final, 8, 8);

                // Draw vertical line
                stroke(211, 211, 211);
                strokeWeight(1);
                line(x_final, 50, x_final, height - 50);
            });
        }

        function initializeParticles() {
            particles = [];
            const numPoints = parseInt(document.getElementById('slider-points').value);
            for (let i = 0; i < numPoints; i++) {
                particles.push({
                    x0: map(i, 0, numPoints - 1, 0, 5),
                    history: []
                });
            }
        }

        function setupControls() {
            // Update labels on slider change
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', (e) => {
                    const label = document.getElementById('label-' + e.target.id.split('-')[1]);
                    if (label) {
                        label.textContent = e.target.value;
                    }
                    if (e.target.id === 'slider-points') {
                        initializeParticles();
                    }
                });
            });

            // Play/Pause button
            document.getElementById('btn-play-pause').addEventListener('click', (e) => {
                isPlaying = !isPlaying;
                e.target.textContent = isPlaying ? 'Pause' : 'Play';
            });

            // Reset button
            document.getElementById('btn-reset').addEventListener('click', () => {
                // Reset all sliders
                document.getElementById('slider-time').value = defaults.time;
                document.getElementById('slider-trail').value = defaults.trail;
                document.getElementById('slider-points').value = defaults.points;
                document.getElementById('slider-p-amp').value = defaults.p_amp;
                document.getElementById('slider-p-wl').value = defaults.p_wl;
                document.getElementById('slider-s-amp').value = defaults.s_amp;
                document.getElementById('slider-s-wl').value = defaults.s_wl;

                // Update labels
                document.getElementById('label-time').textContent = defaults.time;
                document.getElementById('label-trail').textContent = defaults.trail;
                document.getElementById('label-points').textContent = defaults.points;
                document.getElementById('label-p-amp').textContent = defaults.p_amp;
                document.getElementById('label-p-wl').textContent = defaults.p_wl;
                document.getElementById('label-s-amp').textContent = defaults.s_amp;
                document.getElementById('label-s-wl').textContent = defaults.s_wl;

                // Reset state
                isPlaying = false;
                document.getElementById('btn-play-pause').textContent = 'Play';

                initializeParticles();
            });
        }

        function windowResized() {
            resizeCanvas(canvas.width, canvas.height);
        }
    </script>
</body>
</html>
