<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label>fault dip</label>
                <div class="slider-container">
                    <input type="range" id="slider-dip" min="0" max="90" step="1" value="0">
                    <span id="value-dip" class="value-display">0</span>
                </div>
            </div>

            <div class="control-group">
                <label>fault depth</label>
                <div class="slider-container">
                    <input type="range" id="slider-depth" min="100" max="1000" step="10" value="500">
                    <span id="value-depth" class="value-display">500</span>
                </div>
            </div>

            <div class="control-group">
                <label>x and y plot limit</label>
                <div class="slider-container">
                    <input type="range" id="slider-limit" min="500" max="2000" step="50" value="1000">
                    <span id="value-limit" class="value-display">1000</span>
                </div>
            </div>

            <div class="control-group">
                <label>displacement component</label>
                <div id="btn-group-component" class="button-group">
                    <button id="btn-component-x">X</button>
                    <button id="btn-component-y">Y</button>
                    <button id="btn-component-z" class="active">Z</button>
                </div>
            </div>

            <div class="control-group">
                <label>fault type</label>
                <div id="btn-group-fault-type" class="button-group">
                    <button id="btn-fault-tensile" class="active">tensile</button>
                    <button id="btn-fault-strike-slip">strike-slip</button>
                    <button id="btn-fault-normal">normal</button>
                </div>
            </div>
        </div>

        <div id="plot-container">
            <div id="plot"></div>
        </div>
    </div>

    <script>
        // Global state
        const state = {
            dip: 0,
            depth: 500,
            limit: 1000,
            component: 'Z',
            faultType: 'tensile'
        };

        // Constants
        const nu = 0.25;
        const gridSize = 50;

        // Utility functions
        function toRadians(degrees) {
            return degrees * Math.PI / 180;
        }

        function calculateDisplacements(x, y, dip, depth, faultType) {
            const d = toRadians(dip);
            const b = 1; // dislocation magnitude in meters
            const q = y * Math.cos(d) - depth * Math.sin(d);
            const R = Math.sqrt(x**2 + y**2 + depth**2);

            let ux = 0, uy = 0, uz = 0;

            if (faultType === 'tensile') {
                // Tensile fault (opening mode)
                ux = -b * x * q / (R**3);
                uy = -b * (q * Math.sin(d) + depth * Math.cos(d)) / (R**3);
                uz = b * (y * Math.sin(d) + q * Math.cos(d)) / (R**3);
            } else if (faultType === 'strike-slip') {
                // Strike-slip fault (horizontal displacement)
                ux = b * (1/2 + (1 - nu) * (y * Math.cos(d) + depth * Math.sin(d)) / (R + depth * Math.cos(d) + y * Math.sin(d)));
                uy = -b * (1 - nu) * x * Math.cos(d) / (R + depth * Math.cos(d) + y * Math.sin(d));
                uz = -b * (1 - nu) * x * Math.sin(d) / (R + depth * Math.cos(d) + y * Math.sin(d));
            } else if (faultType === 'normal') {
                // Normal fault (vertical displacement)
                ux = -b * (1 - nu) * x * (Math.sin(d) + (y * Math.cos(d) - depth * Math.sin(d)) * Math.sin(d) / (R + y * Math.sin(d) + depth * Math.cos(d))) / (R * (R + y * Math.sin(d) + depth * Math.cos(d)));
                uy = b * (1 - nu) / R + b * (1 - nu) * y / (R * (R + y * Math.sin(d) + depth * Math.cos(d))) * (Math.sin(d) + (y * Math.cos(d) - depth * Math.sin(d)) / (R + y * Math.sin(d) + depth * Math.cos(d)));
                uz = b * (1 - nu) / R * (Math.cos(d) + (y * Math.sin(d) + depth * Math.cos(d)) / (R + y * Math.sin(d) + depth * Math.cos(d))) - b * (1 - nu) * (3 - 4 * nu) * Math.cos(d) / (R + y * Math.sin(d) + depth * Math.cos(d));
            }

            return { ux, uy, uz };
        }

        function updatePlot() {
            const x = [];
            const y = [];
            const z = [];

            const x_step = 2 * state.limit / (gridSize - 1);
            const y_step = 2 * state.limit / (gridSize - 1);

            for (let i = 0; i < gridSize; i++) {
                const x_val = -state.limit + i * x_step;
                x.push(x_val);

                const y_row = [];
                const z_row = [];

                for (let j = 0; j < gridSize; j++) {
                    const y_val = -state.limit + j * y_step;
                    if (i === 0) y.push(y_val);

                    const { ux, uy, uz } = calculateDisplacements(
                        x_val,
                        y_val,
                        toRadians(state.dip),
                        state.depth,
                        state.faultType
                    );

                    let displacement = 0;
                    switch (state.component) {
                        case 'X': displacement = ux * 1000; break;
                        case 'Y': displacement = uy * 1000; break;
                        case 'Z': displacement = uz * 1000; break;
                    }

                    z_row.push(displacement);
                }

                z.push(z_row);
            }

            const data = [{
                type: 'surface',
                x: x,
                y: y,
                z: z,
                colorscale: [['0', '#FFA500'], ['0.5', '#FFD700'], ['1', '#FFDD44']],
                showscale: false,
                contours: {
                    x: { show: true, color: 'black', width: 0.5 },
                    y: { show: true, color: 'black', width: 0.5 },
                    z: { show: false }
                },
                lighting: {
                    ambient: 0.8,
                    diffuse: 0.8,
                    specular: 0.2
                }
            }];

            const layout = {
                title: '',
                scene: {
                    xaxis: { title: 'x (m)', range: [-state.limit, state.limit] },
                    yaxis: { title: 'y (m)', range: [-state.limit, state.limit] },
                    zaxis: { title: 'z (mm)', autorange: true },
                    camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
                },
                margin: { l: 20, r: 20, b: 20, t: 20 }
            };

            Plotly.react('plot', data, layout);
        }

        // Event listeners
        document.getElementById('slider-dip').addEventListener('input', (e) => {
            state.dip = parseInt(e.target.value);
            document.getElementById('value-dip').textContent = state.dip;
            updatePlot();
        });

        document.getElementById('slider-depth').addEventListener('input', (e) => {
            state.depth = parseInt(e.target.value);
            document.getElementById('value-depth').textContent = state.depth;
            updatePlot();
        });

        document.getElementById('slider-limit').addEventListener('input', (e) => {
            state.limit = parseInt(e.target.value);
            document.getElementById('value-limit').textContent = state.limit;
            updatePlot();
        });

        // Component buttons
        document.querySelectorAll('#btn-group-component button').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('#btn-group-component button').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                state.component = btn.textContent;
                updatePlot();
            });
        });

        // Fault type buttons
        document.querySelectorAll('#btn-group-fault-type button').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('#btn-group-fault-type button').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                state.faultType = btn.textContent.includes('tensile') ? 'tensile' : 
                                 btn.textContent.includes('strike') ? 'strike-slip' : 'normal';
                updatePlot();
            });
        });

        // Initialize plot
        updatePlot();
    </script>
</body>
</html>
