                const y = x * x;
                const ans1 = 57568490574.0 + y * (-13362590354.0 + y * (651619640.7 + y * (-11214424.18 + y * (77392.33017 + y * (-184.9052456)))));
                const ans2 = 57568490411.0 + y * (1029532985.0 + y * (9494680.718 + y * (59272.64853 + y * (267.8532712 + y))));
                return ans1 / ans2;
            } else {
                const z = 8.0 / ax;
                const y = z * z;
                const xx = ax - 0.785398164;
                const ans1 = 1.0 + y * (-0.1098628627e-2 + y * (0.2734510407e-4 + y * (-0.2073370639e-5 + y * 0.2093887211e-6)));
                const ans2 = -0.1562499995e-1 + y * (0.1430488765e-3 + y * (-0.6911147651e-5 + y * (0.7621095161e-6 - y * 0.934935152e-7)));
                return Math.sqrt(0.636619772 / ax) * (Math.cos(xx) * ans1 - z * Math.sin(xx) * ans2);
            }
        }

        function updateFormula() {
            const omega_k = zeros[k - 1] / 2;
            formulaDisplay.innerHTML = `\\(\\omega_{${k}} = ${omega_k.toFixed(2)} \\sqrt{g/l}\\)`;
            MathJax.typesetPromise([formulaDisplay]);
        }

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(600, 400);
            canvas.parent('canvas-container');
            updateFormula();
        }

        function draw() {
            background(240);

            // Coordinate system
            const xMin = -2, xMax = 2;
            const yMin = -1.2, yMax = 0;
            const margin = 50;

            // Map logical coordinates to canvas coordinates
            const mapX = (x) => map(x, xMin, xMax, margin, width - margin);
            const mapY = (y) => map(y, yMin, yMax, height - margin, margin);

            // Draw axes if enabled
            if (showAxes) {
                stroke(150);
                strokeWeight(1);
                drawingContext.setLineDash([5, 5]);

                // X-axis
                line(mapX(xMin), mapY(0), mapX(xMax), mapY(0));

                // Y-axis
                line(mapX(0), mapY(yMin), mapX(0), mapY(yMax));

                drawingContext.setLineDash([]);

                // X-axis ticks and labels
                for (let x = -2; x <= 2; x++) {
                    if (x !== 0) {
                        line(mapX(x), mapY(0) - 5, mapX(x), mapY(0) + 5);
                        fill(100);
                        noStroke();
                        textAlign(CENTER, TOP);
                        text(x, mapX(x), mapY(0) + 10);
                    }
                }

                // Y-axis ticks and labels
                for (let y = -1; y <= 0; y += 0.2) {
                    if (Math.abs(y) > 0.01) {
                        line(mapX(0) - 5, mapY(y), mapX(0) + 5, mapY(y));
                        fill(100);
                        noStroke();
                        textAlign(RIGHT, CENTER);
                        text(y.toFixed(1), mapX(0) - 10, mapY(y));
                    }
                }
            }

            // Calculate string parameters
            const z_k = zeros[k - 1];
            const omega_k = z_k / 2;
            const timeFactor = Math.cos(omega_k * t);

            // Draw the string
            stroke(30, 144, 255);
            strokeWeight(2);
            noFill();

            beginShape();
            for (let y_norm = 0; y_norm <= 1; y_norm += 0.01) {
                const bessel_arg = z_k * Math.sqrt(y_norm);
                const x_pos = amplitude * timeFactor * besselJ0(bessel_arg);
                const y_pos = -y_norm;

                vertex(mapX(x_pos), mapY(y_pos));
            }
            endShape();
        }
    </script>
</body>
</html>
