    <script>
        let layeringOrderIndex = 0;
        const layeringPermutations = [
            [1, 2, 3, 4],
            [4, 3, 2, 1],
            [1, 3, 2, 4],
            [2, 4, 1, 3],
            [3, 1, 4, 2]
        ];

        const sketch = (p) => {
            function updateLayeringText() {
                const order = layeringPermutations[layeringOrderIndex];
                document.getElementById('layering-display').textContent = order.join(', ');
            }

            function redrawCanvas() {
                p.redraw();
            }

            function drawCircles() {
                p.clear();
                p.background('#f0f0f0');
                const order = layeringPermutations[layeringOrderIndex];

                const canvasWidth = p.width;
                const canvasHeight = p.height;

                const circles = {
                    1: {
                        cx: canvasWidth * 0.35,
                        cy: canvasHeight * 0.4,
                        dia: canvasWidth * 0.4
                    },
                    2: {
                        cx: canvasWidth * 0.65,
                        cy: canvasHeight * 0.4,
                        dia: canvasWidth * 0.4
                    },
                    3: {
                        cx: canvasWidth * 0.5,
                        cy: canvasHeight * 0.65,
                        dia: canvasWidth * 0.4
                    },
                    4: {
                        cx: canvasWidth * 0.5,
                        cy: canvasHeight * 0.45,
                        dia: canvasWidth * 0.3
                    }
                };

                const strokeColors = {};
                const opacities = {};
                for (let i = 1; i <= 4; i++) {
                    const c = document.getElementById(`color-picker${i}`).value;
                    const r = parseInt(c.slice(1, 3), 16);
                    const g = parseInt(c.slice(3, 5), 16);
                    const b = parseInt(c.slice(5, 7), 16);
                    strokeColors[i] = [r, g, b];
                    opacities[i] = parseFloat(document.getElementById(`slider-opacity${i}`).value);
                }

                order.forEach(num => {
                    const { cx, cy, dia } = circles[num];
                    const [r, g, b] = strokeColors[num];
                    const alpha = opacities[num] * 255;
                    p.noStroke();
                    p.fill(r, g, b, alpha);
                    p.ellipse(cx, cy, dia);
                });

                p.fill(0);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(32);
                for (let i = 1; i <= 4; i++) {
                    const { cx, cy } = circles[i];
                    p.text(`${i}`, cx, cy);
                }
            }

            p.setup = function () {
                const container = document.getElementById('canvas-container');
                const canvas = p.createCanvas(container.clientWidth, container.clientHeight);
                canvas.parent('canvas-container');
                drawCircles();
                p.noLoop();
            };

            p.draw = function () {
                drawCircles();
            };

            p.windowResized = function () {
                const container = document.getElementById('canvas-container');
                p.resizeCanvas(container.clientWidth, container.clientHeight);
                redrawCanvas();
            };

            // Make redrawCanvas available globally
            window.redrawCanvas = redrawCanvas;
            window.updateLayeringText = updateLayeringText;
        };

        new p5(sketch);

        // Event listeners
        for (let i = 1; i <= 4; i++) {
            document.getElementById(`slider-opacity${i}`).addEventListener('input', redrawCanvas);
            document.getElementById(`color-picker${i}`).addEventListener('input', redrawCanvas);
        }

        document.getElementById('btn-layer-next').addEventListener('click', () => {
            layeringOrderIndex = (layeringOrderIndex + 1) % layeringPermutations.length;
            updateLayeringText();
            redrawCanvas();
        });

        document.getElementById('btn-layer-prev').addEventListener('click', () => {
            layeringOrderIndex = (layeringOrderIndex - 1 + layeringPermutations.length) % layeringPermutations.length;
            updateLayeringText();
            redrawCanvas();
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-opacity1').value = 0.7;
            document.getElementById('slider-opacity2').value = 0.7;
            document.getElementById('slider-opacity3').value = 0.7;
            document.getElementById('slider-opacity4').value = 0.7;
            document.getElementById('color-picker1').value = '#00ff00';
            document.getElementById('color-picker2').value = '#0000ff';
            document.getElementById('color-picker3').value = '#ff0000';
            document.getElementById('color-picker4').value = '#000000';
            layeringOrderIndex = 0;
            updateLayeringText();
            redrawCanvas();
        });
    </script>
</body>
