            <button id="btn-reaction-minus">-</button>
            <input type="range" id="slider-reaction" min="0" max="100" value="0">
            <button id="btn-reaction-plus">+</button>
        </div>
        <div id="erg-ewg-slider-container" style="display:none;">
            <label for="slider-erg-ewg">ERG/EWG effect intensity</label>
            <button id="btn-erg-ewg-minus">-</button>
            <input type="range" id="slider-erg-ewg" min="0" max="100" value="0">
            <button id="btn-erg-ewg-plus">+</button>
        </div>
    </div>

    <div id="canvas-container" style="flex:1; background:#fafafa;">
        <canvas id="viz-canvas"></canvas>
    </div>

</div>

<script>
const state = {
    mode: 'da',
    inverse: false,
    focus: false,
    reaction: 0,
    ewg: 0
};

function setup() {
    const canvas = select('#viz-canvas');
    const parent = select('#canvas-container');
    const w = int(parent.width);
    const h = int(parent.height);
    createCanvas(w, h).parent('canvas-container');
}

function draw() {
    background(250);
    drawForMode();
}

function drawForMode() {
    switch (state.mode) {
        case 'da': drawDA(); break;
        case 'regio': drawRegio(); break;
        case 'stereo-phile': drawStereo('phile'); break;
        case 'stereo-diene': drawStereo('diene'); break;
        case 'homo': drawHomo(); break;
    }
}

function drawDA() {
    push();
    translate(width / 2 - 180, height / 2 - 120);
    if (state.reaction < 50) {
        scale(1.3);
        fill(0);
        const ratio = state.reaction / 50;
        translate(lerp(0, 20, ratio), 0);
        drawButadiene(0, 0);
        drawEthylene(200 * (1 - ratio), 0);
    } else {
        scale(1.5);
        translate(60, 0);
        animatedDA().
    }
    pop();
}

function animatedDA() {
    const ratio = (state.reaction - 50) / 50;
    strokeWeight(2);
    stroke(50);
    fill(50);
    push();
    scale(0.8);
    beginShape();
    let positions = [
        [0,   0],
        [20, 20],
        [60, 40],
        [80, 40],
        [60, 0  ],
        [40, -20],
        [20, 0  ]
    ];
    const toFinal = [
        [0, -6], [6, 6], [20, 20], [26, 20], [20, -6 ], [13, -13], [6, -6]
    ];
    for (let [x,y] of positions) {
        vertex(lerp(x, x + toFinal[positions.indexOf([x,y])][0], ratio),
               lerp(y, y + toFinal[positions.indexOf([x,y])][1], ratio));
    }
    endShape();
    // pi bond fade
    stroke(50, 50 * (1 - ratio), 50, 255 * (1 - ratio));
    line( 8, 6, 12, 10);
    line(68,20, 72,20);
    line(12,-6, 8,-10);
    pop();
}

function drawButadiene(x, y) { drawSimpleButadiene(x, y); }
function drawEthylene(x, y) {
    stroke(0); fill(0);
    drawRect(x, y, [0,0], [0,0], "=");
    drawRect(x, y, [40,0], [40,0], "=");
    drawText("ethylene", x+20, y+30);
}

function drawSimpleButadiene(x, y) {
    drawRect(x, y, [0,0], [0,0], "=");
    drawRect(x, y, [40,0], [40,0], "=");
    drawRect(x, y, [80,0], [80,0], "=");
    drawRect(x, y, [120,0], [120,0], "=");
    drawText("cis-1,3-butadiene", x+60, y+30);
}

function drawRegio() {
    push();
    translate(40, 60);
    drawProductIsomers();
    pop();
}

function drawProductIsomers() {
    push();
    drawRect(0,0,[0,0],[0,0], "="); drawRect(0,0,[40,20],[40,20], "="); drawRect(0,0,[80,40],[80,40], "=");
    drawRect(120,0,[160,0],[160,0], "="); drawRect(0,0,[160,20],[160,20], "="); drawText("ortho", 80, 60);

    drawRect(0,90,[0,90],[0,90], "="); drawRect(0,90,[40,110],[40,110], "="); drawRect(0,90,[80,130],[80,130], "=");
    drawRect(160,90,[160,110],[160,110], "="); drawText("meta", 80, 160);

    const alpha = 255 * (state.ewg / 100);
    stroke(0);
    strokeWeight(3);
    stroke(255, alpha, alpha, 255 - alpha);
    line(120, 30, 180, 60);
    pop();
}

function drawStereo(type) {
    push();
    translate(width / 2 - 160, 60);
    const step = type === 'phile' ? 90 : 110;
    cisView(step * 0);
    transView(step * 1);
    pop();
}
function cisView(h) {
    drawRect(0,h,[0,h],[0,h], "="); drawRect(0,h,[40,h],[40,h], "="); drawRect(0,h,[80,h],[80,h], "="); drawRect(0,h,[120,h],[120,h], "=");
    drawText("cis-dienophile/ethylene", 20, h+80);
}
function transView(h) {
    drawRect(0,h,[0,h],[0,h], "="); drawRect(0,h,[40,h],[40,h], "="); drawRect(0,h,[80,h],[80,h], "="); drawRect(0,h,[120,h],[120,h], "=");
    drawText("trans-dienophile/ethylene", 20, h+80);
}

function drawHomo() {
    push();
    fill(0);
    noFill();
    stroke(0);
    strokeWeight(1);

    const x1 = 50;
    const y1 = 250;
    const x2 = 250;
    const y2 = 300;

    drawOrbitalBlock(x1, y1, 'diene HOMO');
    drawOrbitalBlock(x2, y2, 'dienophile LUMO');

    // arrow
    strokeWeight(2);
    const mx = x1 + 100 * (state.ewg/100);
    const my = y1 + 25;
    const angle = -PI/8;
    const arrowLen = 50 - (0.2 * state.ewg);
    push();
    translate(mx, my);
    rotate(angle);
    stroke(255,0,0, 180 + 75 * state.ewg/100);
    line(0, 0, arrowLen, 0);
    let ar = 5;
    line(arrowLen, 0, arrowLen-ar, -ar);
    line(arrowLen, 0, arrowLen-ar,  ar);
    pop();

    function drawOrbitalBlock(x, y, txt) {
        rect(x, y, 150, 30);
        fill(0);
        text(txt, x+5, y+20);
    }
    pop();
}

// minimal draw helpers
function drawText(t, x, y) {
    fill(0);
    textAlign(CENTER, CENTER);
    text(t, x, y);
}
function drawRect(x, y, p1, p2, bond) {
    line(p1[0] + x, p1[1] + y, p2[0] + x, p2[1] + y);
    if (bond === "=") {
        strokeWeight(2);
        const offset = 4;
        line(p1[0] + x, p1[1] + y - offset, p2[0] + x, p2[1] + y - offset);
        line(p1[0] + x, p1[1] + y + offset, p2[0] + x, p2[1] + y + offset);
        strokeWeight(1);
    }
}

function updateControls() {
    const regioSelector = x => select(`#${x}`).style('display', 'none');
    const enaSelector = x => select(`#${x}`).style('display', 'flex');

    regioSelector('inverse-container');
    regioSelector('focus-container');
    regioSelector('reaction-slider-container');
    regioSelector('erg-ewg-slider-container');

    if (['da', 'regio'].includes(state.mode)) enaSelector('reaction-slider-container');
    if (['regio','homo'].includes(state.mode)) enaSelector('inverse-container');
    if (['da', 'regio', 'stereo-phile', 'stereo-diene'].includes(state.mode)) enaSelector('focus-container');
    if (['regio', 'homo'].includes(state.mode)) enaSelector('erg-ewg-slider-container');
}

// mode selection listeners
select('#btn-mode-da').mousePressed(() => { setMode('da'); });
select('#btn-mode-regio').mousePressed(() => { setMode('regio'); });
select('#btn-mode-stereo-phile').mousePressed(() => { setMode('stereo-phile'); });
select('#btn-mode-stereo-diene').mousePressed(() => { setMode('stereo-diene'); });
select('#btn-mode-homo').mousePressed(() => { setMode('homo'); });

function setMode(m) {
    state.mode = m;
    document.querySelectorAll('.mode-button').forEach(b => b.classList.remove('active'));
    document.getElementById('btn-mode-' + (m === 'stereo-phile' ? 'stereo-phile' : m)).classList.add('active');
    updateControls();
    redraw();
}

// slider & checkbox listeners
select('#slider-reaction').input(() => { state.reaction = select('#slider-reaction').value(); redraw(); });
select('#slider-erg-ewg').input(() => { state.ewg = select('#slider-erg-ewg').value(); redraw(); });
select('#check-inverse').changed(() => { state.inverse = select('#check-inverse').checked(); redraw(); });
select('#check-focus').changed(() => { state.focus = select('#check-focus').checked(); redraw(); });

// +/- fine buttons
select('#btn-reaction-minus').mousePressed(() => { 
    const v = max(0, state.reaction - 1); 
    state.reaction = v; 
    select('#slider-reaction').elt.value = v; 
    redraw(); 
});
select('#btn-reaction-plus').mousePressed(() => { 
    const v = min(100, state.reaction + 1); 
    state.reaction = v; 
    select('#slider-reaction').elt.value = v; 
    redraw(); 
});
select('#btn-erg-ewg-minus').mousePressed(() => { 
    const v = max(0, state.ewg - 1); 
    state.ewg = v; 
    select('#slider-erg-ewg').elt.value = v; 
    redraw(); 
});
select('#btn-erg-ewg-plus').mousePressed(() => { 
    const v = min(100, state.ewg + 1); 
    state.ewg = v; 
    select('#slider-erg-ewg').elt.value = v; 
    redraw(); 
});

// window resize
function windowResized() {
    const parent = select('#canvas-container');
    resizeCanvas(int(parent.width), int(parent.height));
}

// initial setup
updateControls();
</script>
</body>
</html>
