<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="dwarf-planet-controls">
                <label for="dwarf-planet-selector">dwarf planet</label>
                <div id="dwarf-planet-selector">
                    <button data-planet="Ceres">Ceres</button>
                    <button data-planet="Pluto" class="active">Pluto</button>
                    <button data-planet="Makemake">Makemake</button>
                    <button data-planet="Haumea">Haumea</button>
                    <button data-planet="Eris">Eris</button>
                </div>
            </div>
            <div id="time-controls">
                <label for="time-slider">time</label>
                <input type="range" id="time-slider" min="0" max="110000" step="1" value="2922">
                <button id="play-pause-button">▶</button>
            </div>
        </div>
        <div id="visualization-container">
            <div id="date-display">Tue 1 Jan 2008 00:00:00</div>
            <canvas id="3d-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let sun, planets = {}, dwarfPlanet, dwarfPlanetOrbit;
        let selectedDwarfPlanet = 'Pluto';
        let isAnimating = false;
        let animationId = null;

        // Orbital elements data
        const orbitalElements = {
            Mercury: { a: 0.3871, e: 0.2056, i: 7.005, Ω: 48.331, ω: 29.124, M: 174.796, color: 0x808080 },
            Venus: { a: 0.7233, e: 0.0068, i: 3.395, Ω: 76.680, ω: 54.884, M: 50.416, color: 0xFFA500 },
            Earth: { a: 1.0000, e: 0.0167, i: 0.000, Ω: -11.260, ω: 114.208, M: 358.617, color: 0x0066CC },
            Mars: { a: 1.5237, e: 0.0934, i: 1.850, Ω: 49.562, ω: 286.502, M: 19.412, color: 0xCC0000 },
            Ceres: { a: 2.7691, e: 0.0760, i: 10.593, Ω: 80.393, ω: 73.598, M: 77.372, color: 0x8B4513 },
            Pluto: { a: 39.482, e: 0.2488, i: 17.160, Ω: 110.299, ω: 113.834, M: 14.882, color: 0x800080 },
            Haumea: { a: 43.132, e: 0.1912, i: 28.220, Ω: 122.155, ω: 239.544, M: 204.03, color: 0x8B4513 },
            Makemake: { a: 45.791, e: 0.1559, i: 29.006, Ω: 79.623, ω: 294.821, M: 95.895, color: 0x8B4513 },
            Eris: { a: 67.668, e: 0.4418, i: 44.187, Ω: 35.951, ω: 151.291, M: 206.51, color: 0x8B4513 }
        };

        // Initialize Three.js scene
        function initScene() {
            const canvas = document.getElementById('3d-canvas');
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf5f5f5);

            camera = new THREE.PerspectiveCamera(
                75,
                canvas.clientWidth / canvas.clientHeight,
                0.1,
                1000
            );
            camera.position.set(0, 15, 30);

            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const pointLight = new THREE.PointLight(0xffffff, 1);
            pointLight.position.set(0, 0, 0);
            scene.add(pointLight);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xFFFF00 });
            sun = new THREE.Mesh(sunGeometry, sunMaterial);
            scene.add(sun);

            // Create planets
            createPlanets();
            createDwarfPlanet();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function createPlanets() {
            ['Mercury', 'Venus', 'Earth', 'Mars'].forEach(name => {
                const elements = orbitalElements[name];

                // Planet body
                const geometry = new THREE.SphereGeometry(0.15, 16, 16);
                const material = new THREE.MeshLambertMaterial({ color: elements.color });
                const planet = new THREE.Mesh(geometry, material);
                scene.add(planet);
                planets[name] = { body: planet, elements: elements };

                // Orbit path
                createOrbitPath(elements, 0x0000ff);
            });
        }

        function createDwarfPlanet() {
            const elements = orbitalElements[selectedDwarfPlanet];

            // Dwarf planet body
            const geometry = new THREE.SphereGeometry(0.2, 16, 16);
            const material = new THREE.MeshLambertMaterial({ color: 0x800080 });
            dwarfPlanet = new THREE.Mesh(geometry, material);
            scene.add(dwarfPlanet);

            // Dwarf planet orbit
            dwarfPlanetOrbit = createOrbitPath(elements, 0xff0000);
        }

        function createOrbitPath(elements, color) {
            const { a, e, i, Ω, ω } = elements;

            // Create ellipse points
            const curve = new THREE.EllipseCurve(
                -a * e, 0,
                a, a * Math.sqrt(1 - e * e),
                0, 2 * Math.PI,
                false,
                0
            );

            const points = curve.getPoints(200);
            const geometry = new THREE.BufferGeometry().setFromPoints(
                points.map(p => new THREE.Vector3(p.x, p.y, 0))
            );

            // Apply orbital rotations
            const material = new THREE.LineBasicMaterial({ color: color });
            const orbit = new THREE.Line(geometry, material);

            // Rotate to match orbital plane
            orbit.rotation.z = THREE.MathUtils.degToRad(ω);
            orbit.rotation.y = THREE.MathUtils.degToRad(Ω);
            orbit.rotation.x = THREE.MathUtils.degToRad(i);

            scene.add(orbit);
            return orbit;
        }

        function calculatePosition(date, elements) {
            const { a, e, i, Ω, ω, M: M_epoch } = elements;

            // Days since J2000
            const j2000 = new Date('2000-01-01T12:00:00Z');
            const d = (date - j2000) / (1000 * 60 * 60 * 24);

            // Orbital period in days
            const P = 365.25 * Math.pow(a, 1.5);

            // Mean anomaly
            let M = (M_epoch + 360 / P * d) % 360;
            M = THREE.MathUtils.degToRad(M);

            // Solve Kepler's equation for eccentric anomaly
            let E = M;
            for (let j = 0; j < 5; j++) {
                E = E - (E - e * Math.sin(E) - M) / (1 - e * Math.cos(E));
            }

            // True anomaly
            const ν = 2 * Math.atan2(
                Math.sqrt(1 + e) * Math.sin(E / 2),
                Math.sqrt(1 - e) * Math.cos(E / 2)
            );

            // Distance from sun
            const r = a * (1 - e * Math.cos(E));

            // Position in orbital plane
            const x_orb = r * Math.cos(ν);
            const y_orb = r * Math.sin(ν);

            // Transform to ecliptic coordinates
            const cosΩ = Math.cos(THREE.MathUtils.degToRad(Ω));
            const sinΩ = Math.sin(THREE.MathUtils.degToRad(Ω));
            const cosω = Math.cos(THREE.MathUtils.degToRad(ω));
            const sinω = Math.sin(THREE.MathUtils.degToRad(ω));
            const cosi = Math.cos(THREE.MathUtils.degToRad(i));
            const sini = Math.sin(THREE.MathUtils.degToRad(i));

            const x = x_orb * (cosΩ * cosω - sinΩ * sinω * cosi) - y_orb * (cosΩ * sinω + sinΩ * cosω * cosi);
            const y = x_orb * (sinΩ * cosω + cosΩ * sinω * cosi) + y_orb * (cosΩ * cosω * cosi - sinΩ * sinω);
            const z = x_orb * sinω * sini + y_orb * cosω * sini;

            return new THREE.Vector3(x, z, y);
        }

        function updatePositions() {
            const slider = document.getElementById('time-slider');
            const days = parseInt(slider.value);
            const date = new Date('2000-01-01T00:00:00Z');
            date.setDate(date.getDate() + days);

            // Update date display
            document.getElementById('date-display').textContent = date.toUTCString().replace(' GMT', '');

            // Update planet positions
            Object.keys(planets).forEach(name => {
                const pos = calculatePosition(date, planets[name].elements);
                planets[name].body.position.copy(pos);
            });

            // Update dwarf planet position
            if (dwarfPlanet) {
                const pos = calculatePosition(date, orbitalElements[selectedDwarfPlanet]);
                dwarfPlanet.position.copy(pos);
            }
        }

        function onWindowResize() {
            const canvas = document.getElementById('3d-canvas');
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        function animate() {
            if (isAnimating) {
                const slider = document.getElementById('time-slider');
                let value = parseInt(slider.value) + 2;
                if (value > slider.max) value = slider.min;
                slider.value = value;
                updatePositions();
            }
            controls.update();
            renderer.render(scene, camera);
            animationId = requestAnimationFrame(animate);
        }

        // Event listeners
        document.getElementById('dwarf-planet-selector').addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                // Update active button
                document.querySelectorAll('#dwarf-planet-selector button').forEach(btn => btn.classList.remove('active'));
                e.target.classList.add('active');

                // Update dwarf planet
                selectedDwarfPlanet = e.target.dataset.planet;
                scene.remove(dwarfPlanet);
                scene.remove(dwarfPlanetOrbit);
                createDwarfPlanet();
                updatePositions();
            }
        });

        document.getElementById('time-slider').addEventListener('input', updatePositions);

        document.getElementById('play-pause-button').addEventListener('click', (e) => {
            isAnimating = !isAnimating;
            e.target.textContent = isAnimating ? '⏸' : '▶';
        });

        // Initialize
        initScene();
        updatePositions();
        animate();
    </script>
</body>
</html>
